INSERT INTO `zhgd_chart` VALUES ('2', 'CHART_TYPE.PIE', '系统内塔机安装黑匣子比重图', '', 'select \'已安装\' as name,ISNULL(COUNT(a.DeviceNO),0) as deviceNum  from data_monitor_device a where a.MonitorType = 0  ${SYNTAX_FILTER_STR} union all\r\nselect \'未安装\' as name,\r\nISNULL((sum(a.project_crone_num) -  (select COUNT(a.DeviceNO) as deviceNum  from data_monitor_device a where a.MonitorType = 0  ${SYNTAX_FILTER_STR})),0) as deviceNum\r\nfrom data_Zhgd_Group a where 1=1  ${SYNTAX_FILTER_STR}', 'name', '数量', '', '系统内塔机安装黑匣子比重图', 'CHART_CRONE01', '2017-06-19 11:14:22', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('10', 'CHART_TYPE.BAR', '力矩超120%次数排名', 'reportTime', 'select convert(varchar(10),b.ReportDate,120) as reportTime,a.name,a.group_code, SUM(PercentMax) as PercentMax\r\nfrom data_Zhgd_Group a, data_Device_TowerCraneWorkSumByDay b\r\nwhere a.group_code = b.group_code \r\n${SYNTAX_FILTER_STR}\r\ngroup by convert(varchar(10),b.ReportDate,120),a.name,a.group_code\r\norder by PercentMax', 'name', '数量', '', '力矩超120%次数排名', 'CHART_CRONE11', '2017-03-01 10:46:58', '汪梦园', 'convert(varchar(10),ReportDate,120)  = (case WHEN \'${reportDate}\' = \'\' then  convert(varchar(10),getdate() - 1,120) else \'${reportDate}\' END)  and\r\na.group_code in (@[VAR_CHILD_GROUP_CODES]) ', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('11', 'CHART_TYPE.COLUMN', '系统内项目工程数量及分布', '', 'select a.area_province, COUNT(*) as projectNum from zhgd_group a\r\nwhere\r\n1=1\r\n${SYNTAX_FILTER_STR} \r\nand a.group_type = \'ORG_TYPE.PROJECT\' \r\ngroup by a.area_province\r\norder by projectNum desc', 'area_province', '数量', '个', '系统内项目工程数量及分布', 'CHART_PROJECT01', '2017-02-28 15:22:42', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '1', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('13', 'CHART_TYPE.PIE', '系统内项目工程部位及分布', '', 'select isnull(a.project_status,\'未知\') as project_status, COUNT(a.id) as num\r\nfrom zhgd_group a\r\nwhere 1=1 ${SYNTAX_FILTER_STR}\r\nand a.group_type = \'ORG_TYPE.PROJECT\' \r\ngroup by a.project_status\r\norder by num desc', 'project_status', '数量', '个', '系统内项目工程部位及分布', 'CHART_PROJECT_STATUS01', '2017-05-10 15:36:14', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '1', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('15', 'CHART_TYPE.COLUMN', '系统内项目工程数量及分布', 'name', 'select a.area_city, b.name, COUNT(*) as projectNum \r\nfrom zhgd_group a, zhgd_standard_code b\r\nwhere\r\na.area_province = b.value ${SYNTAX_FILTER_STR} and\r\na.group_type = \'ORG_TYPE.PROJECT\' \r\ngroup by a.area_city, b.name\r\n${SYNTAX_SORT_STR}', 'area_city', '数量', '个', '系统内项目工程数量及分布-地市', 'CHART_PROJECT02', '2017-03-01 10:58:41', 'admin', 'a.area_province = \'${area_province}\'and a.group_code in (@[VAR_CHILD_GROUP_CODES])', '1', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('16', 'CHART_TYPE.TABLE', '工程项目一栏表', '', 'select a.* ,cast(a.project_budget as decimal(12,4))as project_budget,cast(a.project_cost as decimal(12,4))as project_cost  from zhgd_group a\r\nwhere\r\n1=1 ${SYNTAX_FILTER_STR} and\r\na.group_type = \'ORG_TYPE.PROJECT\' \r\n${SYNTAX_SORT_STR}', 'group_code', '', '', '工程项目一栏', 'CHART_PROJECT03', '2017-07-20 15:53:36', 'admin', '(a.area_city = \'${area_city}\' or (a.project_status = \'${project_status}\' or (\'${project_status}\'  = \'未知\' and a.project_status is null)) or (\r\n(a.project_status = \'PROJECT_STATUS.20\' or a.project_status = \'PROJECT_STATUS.22\') \r\nand CONVERT(CHAR(7), a.project_status_time, 23) = \'${status_time}\')) and a.group_code in (@[VAR_CHILD_GROUP_CODES])', null, null, 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, '0', null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('19', 'CHART_TYPE.3D_COLUMN', '年度累计竣工交付工程数量统计', 'statusYear', 'select \r\nCONVERT(CHAR(4), project_status_time, 23) as statusYear,\r\nCONVERT(CHAR(7), project_status_time, 23) as statusTime, \r\nCOUNT(*) as projectNum \r\nfrom zhgd_group a\r\nwhere\r\n1=1 ${SYNTAX_FILTER_STR}\r\nand a.group_type = \'ORG_TYPE.PROJECT\' \r\nand (a.project_status = \'PROJECT_STATUS.20\' or a.project_status = \'PROJECT_STATUS.22\')\r\ngroup by CONVERT(CHAR(4), project_status_time, 23) , CONVERT(CHAR(7), project_status_time, 23)\r\norder by CONVERT(CHAR(4), project_status_time, 23) , CONVERT(CHAR(7), project_status_time, 23)', 'statusTime', '数量', '个', '年度累计竣工交付工程数量统计', 'CHART_PROJECT_STATUS11', '2017-03-01 11:14:45', 'admin', 'CONVERT(CHAR(4), project_status_time, 23)  = (case WHEN \'${statusYear}\' = \'\' then  CONVERT(CHAR(4), GETDATE(), 23) else \'${statusYear}\' END) and a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('21', 'CHART_TYPE.PIE', '项目形象进度', '费用执行百分比', 'select \'完成\' as name,isnull(round(100 * project_cost/project_budget,2),0) as cost from zhgd_group  where 1=1 ${SYNTAX_FILTER_STR} ${SYNTAX_SORT_STR} union all \r\nselect \'剩余\' as name,isnull(round(100 * (project_budget - project_cost)/project_budget,2),0) as cost from zhgd_group  where 1=1 ${SYNTAX_FILTER_STR} ${SYNTAX_SORT_STR}', 'name', '数量', '%', '项目形象进度', 'CHART_PROJECT_STATUS21', '2017-06-19 10:34:34', 'admin', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('22', 'CHART_TYPE.COLUMN', '年度项目形象进度统计', '费用执行百分比', 'select CONVERT(CHAR(10), a.status_datetime, 23) as statusTime, \r\nisnull((select SUM(monthly_cost) from zhgd_group_status c where c.group_id = a.group_id and c.id <= a.id),0) as cost\r\nfrom zhgd_group_status a, zhgd_group b\r\nwhere a.group_id = b.id and 1=1 ${SYNTAX_FILTER_STR} ${SYNTAX_SORT_STR} \r\norder by statusTime', 'statusTime', '项目工程款合计', '万元', '年度项目形象进度统计', 'CHART_PROJECT_STATUS31', '2017-03-29 16:30:03', 'admin', 'b.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('23', 'CHART_TYPE.TABLE', '工程项目设备统计', '', 'select \'塔式起重机\' as name,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 0  ${SYNTAX_FILTER_STR}) as installedNum,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 0 and aa.DeviceState = 1  ${SYNTAX_FILTER_STR}) as onlineNum,\r\nconvert(varchar(10),(select aa.project_crone_num from data_Zhgd_Group aa where 1=1 ${SYNTAX_FILTER_STR})) as installedBlackBoxNum\r\nunion all\r\nselect \'施工升降机\' as name,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 1  ${SYNTAX_FILTER_STR}) as installedNum,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 1 and aa.DeviceState = 1  ${SYNTAX_FILTER_STR}) as onlineNum,\r\nconvert(varchar(10),(select aa.project_elevator_num from data_Zhgd_Group aa where 1=1 ${SYNTAX_FILTER_STR})) as installedBlackBoxNum\r\nunion all\r\nselect \'扬尘、噪音\' as name,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 2  ${SYNTAX_FILTER_STR}) as installedNum,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 2 and aa.DeviceState = 1  ${SYNTAX_FILTER_STR}) as onlineNum,\r\n\'--\' as INSTAonlineNum\r\nunion all\r\nselect \'视频监控\' as name,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 3  ${SYNTAX_FILTER_STR}) as installedNum,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 3 and aa.DeviceState = 1  ${SYNTAX_FILTER_STR}) as onlineNum,\r\n\'--\' as INSTAonlineNum\r\nunion all\r\nselect \'人员考勤\' as name,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 11  ${SYNTAX_FILTER_STR}) as installedNum,\r\n(select CAST(COUNT(*) as varchar(10)) from data_monitor_device aa where aa.MonitorType = 11 and aa.DeviceState = 1  ${SYNTAX_FILTER_STR}) as onlineNum,\r\n\'--\' as INSTAonlineNum', 'name', '', '', '各项设备状态数量统计', 'CHART_PROJECT_DEVICE', '2017-04-19 11:12:51', 'admin', 'aa.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('24', 'CHART_TYPE.TABLE', '', '', 'SELECT aa.*, bb.name,\n\'监控状态\' AS action1,\n\'模拟监控\' AS action2,\n\'运行数据\' AS action3,\n\'运行时间\' AS action4,\n\'吊重数据\' AS action5\nFROM d_device_tower_crane aa, d_data_zhgd_group bb \nWHERE aa.project_id = bb.group_code \nAND aa.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\n', 'area_province', null, null, '塔式起重机一栏', 'CHART_PROJECT_DEVICE02', '2018-10-19 14:18:47', 'admin', '\n', null, null, 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('25', 'CHART_TYPE.TABLE', '工程项目塔机安装黑匣子比重', '', 'select a.group_code,a.name,isnull(a.project_crone_num,0) as project_crone_num,isnull(COUNT(distinct b.DID),0) as installed_crone_num\r\nfrom data_Zhgd_Group a left join data_monitor_device b on b.MonitorType = 0 and a.group_code = b.group_code \r\nwhere a.group_type = \'ORG_TYPE.PROJECT\'\r\n${SYNTAX_FILTER_STR}\r\ngroup by a.group_code,a.name,a.project_crone_num\r\norder by installed_crone_num desc', 'name', '数量', '', '工程项目塔机安装黑匣子比重', 'CHART_CRONE02', '2017-04-05 11:15:49', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('26', 'CHART_TYPE.MULTI_AXES', '昨日繁忙工地排名', '', 'select top 10 a.name,a.group_code,\r\nCOUNT(*) as installNum,\r\n(select SUM(aa.LoadCount) from data_Device_TowerCraneWorkSumByDay aa where aa.group_code = a.group_code \r\nand aa.ReportDate >= convert(varchar(10),getdate()-1,120) + \' 00:00:00\') as LoadCount,\r\n(select SUM(aa.PercentMax) from data_Device_TowerCraneWorkSumByDay aa where aa.group_code = a.group_code \r\nand aa.ReportDate >= convert(varchar(10),getdate()-1,120) + \' 00:00:00\') as PercentMax\r\nfrom data_Zhgd_Group a,data_monitor_device b\r\nwhere \r\na.group_code = b.group_code and  b.MonitorType = 0  ${SYNTAX_FILTER_STR}\r\ngroup by a.name,a.group_code\r\norder by LoadCount desc', 'name', '吊次', '', '昨日繁忙工地排名', 'CHART_CRONE21', '2017-05-08 16:25:08', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES]) ', '0', '', 'ZHGD_DATA', '', null, null, null, null, 'right', '', '-70', '20', 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('27', 'CHART_TYPE.COLUMN', '昨日塔式起重机吊次排名', 'reportDate', 'SELECT \'设备号：\'+b.device_name AS DeviceNO,\n(SELECT SUM(aa.load_count) FROM f_data_towercrane_worksumbyday aa WHERE aa.device_sn = b.device_sn \nAND aa.report_date > DATE_SUB(CURDATE(), INTERVAL 1 DAY)) AS LoadCount,\n(SELECT SUM(aa.PercentMax) FROM f_data_towercrane_worksumbyday aa WHERE aa.device_sn = b.device_sn \nAND aa.report_date > DATE_SUB(CURDATE(), INTERVAL 1 DAY)) AS PercentMax\nFROM d_device_tower_crane b\nWHERE  b.group_code = \'${groupCode}\'\nORDER BY load_count', 'DeviceNO', '吊次', '', '日塔式起重机吊次排名', 'CHART_CRONE22', '2018-10-19 14:34:38', 'admin', 'b.group_code = \'${groupCode}\'\n', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '0', '0', '0', '0', '', '', '0', '0', null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('28', 'CHART_TYPE.LIST', '塔式起重机吊运工效统计', '', 'select \r\nCOUNT(distinct a.id_device) as totalDeviceNum,\r\nROUND(sum(a.load_),2) as totalLoad,\r\n(select SUM(LoadCount) from data_Device_TowerCraneWorkSumByDay  where  ReportDate >= convert(varchar(10),getdate()-1,120)  ${SYNTAX_FILTER_STR}) as totalCount,\r\nROUND(avg(a.load_),2)  as avgLoad,\r\nROUND(sum(a.load_)/COUNT(distinct a.id_device),2) as effect,\r\n(select ROUND(avg(b.load_),2) from data_Monitor_TowerCraneWorkData b where convert(varchar(7),b.insertTime,120) = convert(varchar(7),DATEADD(month,-1,getdate()),120)  ${SYNTAX_FILTER_STR})  as mAvgLoad,\r\n(select ROUND(sum(b.load_)/COUNT(distinct b.id_device)/COUNT(distinct convert(varchar(10),b.insertTime,120)),2) from data_Monitor_TowerCraneWorkData b where convert(varchar(7),b.insertTime,120) = convert(varchar(7),DATEADD(month,-1,getdate()),120)  ${SYNTAX_FILTER_STR})  as mEffect,\r\n(select ROUND(avg(b.load_),2) from data_Monitor_TowerCraneWorkData b where convert(varchar(4),b.insertTime,120) = convert(varchar(4),DATEADD(year,-1,getdate()),120)  ${SYNTAX_FILTER_STR})  as yAvgLoad,\r\n(select ROUND(sum(b.load_)/COUNT(distinct b.id_device)/COUNT(distinct convert(varchar(10),b.insertTime,120)),2) from data_Monitor_TowerCraneWorkData b where convert(varchar(4),b.insertTime,120) = convert(varchar(4),DATEADD(year,-1,getdate()),120)  ${SYNTAX_FILTER_STR})  as yEffect\r\nfrom data_Monitor_TowerCraneWorkData a\r\nwhere \r\nconvert(varchar(10),a.insertTime,120)  = convert(varchar(10),getdate() - 1,120)\r\n ${SYNTAX_FILTER_STR}', '1', '', '', '月度塔式起重机吊运工效统计', 'CHART_CRONE31', '2017-05-08 17:11:30', 'lsw', 'group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('29', 'CHART_TYPE.MULTI_AXES', '月度塔式起重机吊运工效统计', '', 'select \r\nconvert(varchar(7),a.insertTime,120) as month,\r\nROUND(avg(a.load_),2)  as avgLoad,\r\nROUND(sum(a.load_)/COUNT(distinct a.id_device)/COUNT(distinct convert(varchar(10),a.insertTime,120)),2) as effect\r\nfrom data_Monitor_TowerCraneWorkData a\r\nwhere \r\na.insertTime  > DATEADD(month,-12,getdate()) ${SYNTAX_FILTER_STR}\r\ngroup by convert(varchar(7),a.insertTime,120)', 'month', '', '', '塔式起重机吊运工效统计', 'CHART_CRONE32', '2017-05-11 15:16:26', 'dxf', 'group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, 'right', '', '-150', null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('30', 'CHART_TYPE.PIE', '系统内施工升降机安装黑匣子比重图', '', 'select \'已安装\' as name,ISNULL(COUNT( distinct a.DeviceNO),0) as deviceNum  from data_monitor_device a where a.MonitorType = 1  ${SYNTAX_FILTER_STR} union all\r\nselect \'未安装\' as name,\r\nISNULL((sum(a.project_elevator_num) -  (select COUNT(distinct a.DeviceNO) as deviceNum  from data_monitor_device a where a.MonitorType = 1  ${SYNTAX_FILTER_STR})),0) as deviceNum\r\nfrom data_Zhgd_Group a where 1=1  ${SYNTAX_FILTER_STR}', 'name', '数量', '', '系统内施工升降机安装黑匣子比重图', 'CHART_ELEVATOR01', '2017-06-19 11:16:49', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '1', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('31', 'CHART_TYPE.TABLE', '工程项目施工升降机安装黑匣子比重', '', 'select a.group_code,a.name,isnull(a.project_elevator_num,0) as project_elevator_num,isnull(COUNT(distinct b.DID),0) as installed_elevator_num\r\nfrom data_Zhgd_Group a left join data_monitor_device b on b.MonitorType = 1 and a.group_code = b.group_code \r\nwhere a.group_type = \'ORG_TYPE.PROJECT\' \r\n${SYNTAX_FILTER_STR}\r\ngroup by a.group_code,a.name,a.project_elevator_num\r\norder by installed_elevator_num desc', 'name', '数量', '', '工程项目施工升降机安装黑匣子比重', 'CHART_ELEVATOR02', '2017-09-13 07:47:07', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, '0', '1', null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('32', 'CHART_TYPE.3D_COLUMN', '昨日设备报警汇总', 'reportTime', ' select top 10 b.group_code,b.name,\r\n (select SUM(isnull(LoadAlarm,0) + isnull(NumberAlarm,0) +  isnull(TopAlarm,0)) \r\nfrom data_Device_ElevatorAlarmSumByDay a where a.group_code = b.group_code \r\n and AlarmDate  >= convert(varchar(10),getdate() -1,120)) as TotalAlarm,  \r\n(select SUM(LoadAlarm) \r\nfrom data_Device_ElevatorAlarmSumByDay a where a.group_code = b.group_code \r\n and AlarmDate  >= convert(varchar(10),getdate() -1,120)) as LoadAlarm,  \r\n (select SUM(NumberAlarm) \r\nfrom data_Device_ElevatorAlarmSumByDay a where a.group_code = b.group_code \r\n and AlarmDate  >= convert(varchar(10),getdate() -1,120)) as NumberAlarm,\r\n  (select SUM(TopAlarm) \r\nfrom data_Device_ElevatorAlarmSumByDay a where a.group_code = b.group_code \r\n and AlarmDate  >= convert(varchar(10),getdate() -1,120)) as TopAlarm\r\nfrom data_Zhgd_Group b \r\nwhere \r\nb.group_type = \'ORG_TYPE.PROJECT\' \r\n${SYNTAX_FILTER_STR}\r\norder by TotalAlarm desc', 'name', '', '', '昨日设备报警汇总', 'CHART_ELEVATOR11', '2017-03-31 13:50:52', 'admin', 'b.group_code in (@[VAR_CHILD_GROUP_CODES]) ', '0', '', 'ZHGD_DATA', '', '0', null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('33', 'CHART_TYPE.BAR', '昨日施工升降机总报警次数排名', '', 'select top 10 b.group_code,b.name,\r\n(select SUM(AlarmCount) \r\nfrom data_Device_ElevatorAlarmSumByDay a where a.group_code = b.group_code \r\n and AlarmDate  >= convert(varchar(10),getdate() -1,120)) as AlarmCount  \r\nfrom data_Zhgd_Group b \r\nwhere \r\nb.group_type = \'ORG_TYPE.PROJECT\' \r\n${SYNTAX_FILTER_STR}\r\norder by AlarmCount desc', 'name', '', '', '昨日施工升降机总报警次数排名', 'CHART_ELEVATOR21', '2017-03-31 13:44:14', 'admin', 'b.group_code in (@[VAR_CHILD_GROUP_CODES]) ', '0', '', 'ZHGD_DATA', '', '0', null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('34', 'CHART_TYPE.MULTI_AXES', '月度塔式起重机吊运工效统计明细', 'reportMonth', 'SELECT  \nCOUNT(DISTINCT a.subsystem_code) AS totalDeviceNum,\nROUND(SUM(a.load),2) AS totalLoad,\n(SELECT SUM(load_count) FROM f_data_towercrane_worksumbyday  WHERE  report_date > DATE_SUB(CURDATE(), INTERVAL 1 DAY) AND group_code IN (@[VAR_CHILD_GROUP_CODES])) AS totalCount,\nROUND(AVG(a.load),2)  AS avgLoad,\nROUND(SUM(a.load)/COUNT(DISTINCT a.subsystem_code),2) AS effect,\n(SELECT ROUND(AVG(b.load),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y-%m\') = DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 MONTH),\'%Y-%m\')  AND group_code IN (@[VAR_CHILD_GROUP_CODES]))  AS mAvgLoad,\n(SELECT ROUND(SUM(b.load)/COUNT(DISTINCT b.subsystem_code)/COUNT(DISTINCT DATE_FORMAT(b.insert_time,\'%Y-%m-%d\')),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y-%m\') = DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 MONTH),\'%Y-%m\') AND group_code IN (@[VAR_CHILD_GROUP_CODES]))  AS mEffect,\n(SELECT ROUND(AVG(b.load),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y\') = DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 YEAR),\'%Y\') AND group_code IN (@[VAR_CHILD_GROUP_CODES]))  AS yAvgLoad,\n(SELECT ROUND(SUM(b.load)/COUNT(DISTINCT b.subsystem_code)/COUNT(DISTINCT DATE_FORMAT(b.insert_time,\'%Y-%m-%d\')),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y\') = DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 YEAR),\'%Y\')  AND group_code IN (@[VAR_CHILD_GROUP_CODES]))  AS yEffect\nFROM f_data_towercrane_workdata a\nWHERE\nDATE_FORMAT(b.insert_time,\'%Y-%m-%d\') = DATE_FORMAT(NOW(),\'%Y-%m-%d\')\nAND group_code IN (@[VAR_CHILD_GROUP_CODES])', 'name', '', '', '月度塔式起重机吊运工效统计明细', 'CHART_CRONE33', '2018-10-19 15:13:30', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES]) and\nconvert(varchar(7),a.insertTime,120) = \'${month}\'', '0', '', 'ZHGD_DATA', '', null, null, null, null, '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('35', 'CHART_TYPE.BAR', '昨日施工升降机总报警次数明细', '', 'select distinct \'设备号：\'+b.DeviceNO as DeviceNO,\r\n(select SUM(LoadAlarm) as LoadAlarm\r\nfrom data_Device_ElevatorAlarmSumByDay a\r\nwhere a.DeviceSN = b.DeviceSN  and a.AlarmDate  >= convert(varchar(10),getdate() -1,120)) as LoadAlarm,\r\n(select SUM(NumberAlarm) as NumberAlarm\r\nfrom data_Device_ElevatorAlarmSumByDay a\r\nwhere a.DeviceSN = b.DeviceSN  and a.AlarmDate  >= convert(varchar(10),getdate() -1,120)) as NumberAlarm\r\nfrom data_Monitor_Device b\r\nwhere b.MonitorType = 1\r\n${SYNTAX_FILTER_STR}\r\norder by DeviceNO', 'DeviceNO', '', '', '日施工升降机总报警次数明细', 'CHART_ELEVATOR22', '2017-03-24 15:34:24', 'admin', 'b.group_code = \'${groupCode}\' ', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('36', 'CHART_TYPE.PIE', '环境监测安装项目占比图', '', 'select \'已安装\' as name,\r\nISNULL(COUNT(distinct a.group_code),0) as deviceNum  from data_monitor_device a,data_Zhgd_Group b where a.MonitorType = 2 and  b.group_type = \'ORG_TYPE.PROJECT\' and  b.group_code = a.group_code  ${SYNTAX_FILTER_STR}\r\nunion all \r\nselect \'未安装\' as name,\r\nISNULL((count(a.id) -  (select COUNT(distinct a.group_code) as deviceNum  from data_monitor_device a where a.MonitorType = 2  ${SYNTAX_FILTER_STR})),0) as deviceNum\r\nfrom data_Zhgd_Group a,data_Zhgd_Group b where 1=1  and  b.group_type = \'ORG_TYPE.PROJECT\' and  b.group_code = a.group_code  ${SYNTAX_FILTER_STR}\r\n', 'name', '数量', 'HEL', '环境监测安装项目占比图', 'CHART_DUST01', '2017-05-08 09:57:19', 'dxf', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('37', 'CHART_TYPE.TABLE', '环境监测安装项目详细', '', 'select a.group_code,a.name,(select COUNT(distinct b.id) from data_monitor_device b where b.MonitorType = 2 and b.group_code = a.group_code) as deviceNum\r\nfrom data_Zhgd_Group a \r\nwhere\r\n a.group_type = \'ORG_TYPE.PROJECT\' ${SYNTAX_FILTER_STR}\r\norder by deviceNum desc', 'name', '', '', '环境监测安装项目详细', 'CHART_DUST02', '2017-04-05 11:19:55', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('38', 'CHART_TYPE.STACKED_COLUMN', '环境监测分布图', '', 'select c.value,c.name,count(distinct a.group_code) -  COUNT(distinct b.group_code) as uninstalledNum, COUNT(distinct b.group_code) as installedNum\r\nfrom data_Zhgd_Group a left join data_monitor_device b on b.MonitorType = 2 and b.group_code = a.group_code, data_Zhgd_Standard_Code c\r\nwhere\r\na.area_province = c.value and \r\n a.group_type = \'ORG_TYPE.PROJECT\' ${SYNTAX_FILTER_STR}\r\ngroup by c.value,c.name', 'name', '', '%', '环境监测分布图', 'CHART_DUST11', '2017-03-31 15:20:47', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '1', null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('39', 'CHART_TYPE.STACKED_COLUMN', '环境监测分布-地市', null, 'select c.value,c.name,count(distinct a.group_code) -  COUNT(distinct b.group_code) as uninstalledNum, COUNT(distinct b.group_code) as installedNum\r\nfrom data_Zhgd_Group a left join data_monitor_device b on b.MonitorType = 2 and b.group_code = a.group_code, data_Zhgd_Standard_Code c\r\nwhere\r\na.area_city = c.value and \r\n a.group_type = \'ORG_TYPE.PROJECT\' ${SYNTAX_FILTER_STR}\r\ngroup by c.value,c.name', 'name', '', '', '环境监测分布-地市', 'CHART_DUST12', '2017-02-28 09:41:25', '汪梦园', ' a.area_province =\'${area_province}\'', '0', '', 'ZHGD_DATA', null, null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('40', 'CHART_TYPE.COLUMN', '本年度PM10/PM2.5项目排名', '', 'select b.group_code, b.name, ROUND(AVG(PMTen),2) as PMTen, ROUND(AVG(PMTwoPointFive),2) as PMTwoPointFive\r\nfrom data_Monitor_DustRunData a,data_Zhgd_Group b\r\nwhere a.group_code = b.group_code\r\nand DeviceTime >= dateadd(yy,-1,GETDATE())  \r\n${SYNTAX_FILTER_STR}\r\ngroup by b.group_code, b.name\r\norder by PMTen desc,PMTwoPointFive desc', 'name', '', '(μg/m3)', '本年度PM10/PM2.5项目排名', 'CHART_DUST21', '2017-04-05 14:30:49', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '0', null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('41', 'CHART_TYPE.COLUMN', '本月夜间施工噪音超标次数排名(Top 10)', '噪音', 'select b.group_code, b.name as name,SUM(case when a.Noise>=b.param_noise then 1 else 0 end) as num from\r\n(select * from data_Monitor_DustRunData \r\nwhere (DeviceTime > convert(char(7), getdate(), 120) + \'-1\' and  DeviceTime >= convert(varchar(10),DeviceTime,120)+space(1)+\'22:00:00\'\r\n  and DeviceTime <= convert(varchar(10),DeviceTime,120)+space(1)+\'23:00:00\')\r\n  or (DeviceTime > convert(char(7), getdate(), 120) + \'-1\' and  DeviceTime >= convert(varchar(10),DeviceTime,120)+space(1)+\'00:00:00\'\r\n  and DeviceTime <= convert(varchar(10),DeviceTime,120)+space(1)+\'06:00:00\')) a,\r\n  data_Zhgd_Group b\r\nwhere a.group_code=b.group_code\r\n ${SYNTAX_FILTER_STR}\r\ngroup by b.name,b.group_code\r\norder by num desc ', 'name', '', '次', '本月夜间施工噪音超标次数排名(Top 10)', 'CHART_DUST31', '2017-05-03 09:59:57', 'lsw', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('42', 'CHART_TYPE.TABLE', '未装视频监控项目', '', 'select distinct \r\na.group_code,\r\na.name,\r\nconvert(varchar(10),a.project_plan_start_time ,120) as project_plan_start_time,\r\nconvert(varchar(10),a.project_plan_end_time ,120) as project_plan_end_time,\r\nb.value,\r\n\'<div style=\"float:left;background-color:blue;height:10px;width:\'+cast(b.priority * 30 as varchar)+\'px\"></div><span>\' +b.name +\'</span>\'  as project_status\r\nfrom data_Zhgd_Group a, data_Zhgd_Standard_Code b\r\nwhere a.project_status = b.value\r\nand a.group_code not in (select group_code from data_Monitor_Device where MonitorType = 3) ${SYNTAX_FILTER_STR} \r\nunion all\r\nselect distinct \r\na.group_code,\r\na.name,\r\n convert(varchar(10),a.project_plan_start_time ,120)  as project_plan_start_time,\r\n convert(varchar(10),a.project_plan_end_time ,120) as project_plan_end_time,\r\n\'未知\' as value,\r\n\'未知\' as project_status\r\nfrom data_Zhgd_Group a\r\nwhere  a.group_type = \'ORG_TYPE.PROJECT\' and a.project_status is null and\r\na.group_code not in (select group_code from data_Monitor_Device where MonitorType = 3)  ${SYNTAX_FILTER_STR} \r\n order by project_plan_start_time desc\r\n', null, null, null, '未装视频监控项目', 'CHART_VIDEO01', '2017-05-10 14:13:07', 'dxf', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', null, null, 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('43', 'CHART_TYPE.TABLE', '已装视频监控项目', '', 'select \r\na.group_code,\r\na.name,\r\nconvert(varchar(10),a.project_plan_start_time ,120) as project_plan_start_time,\r\nconvert(varchar(10),a.project_plan_end_time ,120) as project_plan_end_time,\r\nCOUNT (b.id) as device_num\r\nfrom data_Zhgd_Group a, data_Monitor_Device b\r\nwhere a.group_code = b.group_code\r\nand b.MonitorType = 3 \r\n${SYNTAX_FILTER_STR}\r\ngroup by \r\na.group_code,\r\na.name,\r\nconvert(varchar(10),a.project_plan_start_time ,120),\r\nconvert(varchar(10),a.project_plan_end_time ,120)', '1', '', '', '已装视频监控项目', 'CHART_VIDEO11', '2017-05-10 14:09:41', 'dxf', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('44', 'CHART_TYPE.IMAGE', '塔式起重机实时状态', '', 'SELECT DISTINCT bb.device_no AS DeviceNo,cc.source_id AS IID, cc.device_sn AS DeviceSN, \nCASE WHEN IFNULL(aaa.alarm0, 0)<> 0 THEN 3 \n	 WHEN IFNULL(aaa.alarm1, 0)<> 0 THEN 4\n	 WHEN IFNULL(aaa.alarm2, 0)<> 0 THEN 5\n	 WHEN IFNULL(cc.online, 0)<> 0 THEN 1\n	ELSE 0 END AS STATUS\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn  \nLEFT JOIN  d_towercrane_live aaa ON aaa.source_id = cc.source_id AND DATE_FORMAT( aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \n${SYNTAX_FILTER_STR}\nORDER BY bb.device_no', 'DeviceNO', 'images/eqi/Crone_', '.png', '塔机实时状态', 'CHART_CRONE101', '2018-10-19 13:16:03', 'admin', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' ', '1', 'DEVICE_STATUS', '', 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('45', 'CHART_TYPE.PIE', '人员管理系统安装项目占比', '', 'select \'已安装\' as name,\r\nISNULL(COUNT(distinct a.group_code),0) as deviceNum  from data_monitor_device a where a.MonitorType in (11,12,13)  ${SYNTAX_FILTER_STR}\r\nunion all \r\nselect \'未安装\' as name,\r\nISNULL((count(a.id) -  (select COUNT(distinct a.group_code) as deviceNum  from data_monitor_device a where a.MonitorType in (11,12,13) ${SYNTAX_FILTER_STR})),0) as deviceNum\r\nfrom data_Zhgd_Group a where 1=1 ${SYNTAX_FILTER_STR}', 'name', '数量', '', '人员管理系统安装项目占比', 'CHART_HUMAN01', '2017-06-19 11:18:22', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('46', 'CHART_TYPE.TABLE', '人员管理系统安装项目详细', '', 'select a.group_code,a.name,\r\n(select COUNT(distinct b.id) from data_monitor_device b where b.MonitorType =11 and b.group_code = a.group_code) as deviceNum_KQ,\r\n(select COUNT(distinct b.id) from data_monitor_device b where b.MonitorType =12 and b.group_code = a.group_code) as deviceNum_MJ,\r\n(select COUNT(distinct b.id) from data_monitor_device b where b.MonitorType =13 and b.group_code = a.group_code) as deviceNum_JY\r\nfrom data_Zhgd_Group a \r\nwhere\r\n a.group_type = \'ORG_TYPE.PROJECT\' ${SYNTAX_FILTER_STR}\r\norder by deviceNum_KQ desc', 'name', '', '', '人员管理系统安装项目详细', 'CHART_HUMAN02', '2017-04-05 14:35:55', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('47', 'CHART_TYPE.COLUMN', '人员年龄分布', '', 'select \'16-19\' as name, COUNT(*) as mun from data_F_Worker where age >=16 and age <= 19 ${SYNTAX_FILTER_STR}\r\nunion all\r\nselect \'20-29\' as name, COUNT(*) as mun from data_F_Worker where age >=20 and age <= 29 ${SYNTAX_FILTER_STR}\r\nunion all\r\nselect \'30-39\' as name, COUNT(*) as mun from data_F_Worker where age >=30 and age <= 39 ${SYNTAX_FILTER_STR}\r\nunion all \r\nselect \'40-49\' as name, COUNT(*) as mun from data_F_Worker where age >=40 and age <= 49 ${SYNTAX_FILTER_STR}\r\nunion all\r\nselect \'50-59\' as name, COUNT(*) as mun from data_F_Worker where age >=50 and age <= 59 ${SYNTAX_FILTER_STR}\r\nunion all\r\nselect \'60-69\' as name, COUNT(*) as mun from data_F_Worker where age >=60 and age <= 69 ${SYNTAX_FILTER_STR}', 'name', '人员数量', '人', '人员年龄分布', 'CHART_HUMAN11', '2017-04-01 10:02:49', 'admin', 'group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '0', null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('48', 'CHART_TYPE.COLUMN', '女性从业人员岗位排比统计', '', 'select group_name, COUNT(*) as num\r\nfrom data_F_Worker\r\nwhere sex =2 ${SYNTAX_FILTER_STR}\r\ngroup by group_name\r\norder by num', 'group_name', '人员数量', '人', '女性从业人员岗位排比统计', 'CHART_HUMAN21', '2017-04-01 10:02:39', 'admin', 'group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '0', null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('49', 'CHART_TYPE.TABLE', '塔式起重机运行数据', '', 'SELECT DISTINCT DATE_FORMAT(ccc.device_time,\'%Y-%m-%d %H:%i\') AS dtime,\n CAST(MAX(ccc.angle) AS DECIMAL(18,2)) AS angle,\n CAST(MAX(ccc.radius) AS DECIMAL(18,2)) AS radius,\n CAST(MAX(ccc.height) AS DECIMAL(18,2)) AS hight,\n CAST(MAX(ccc.load) AS DECIMAL(18,2)) AS load_,\n CAST(MAX(ccc.percent) AS DECIMAL(18,2)) AS `[percent]`,\n CAST(MAX(ccc.safeload) AS DECIMAL(18,2)) AS safeload,\n CAST(MAX(ccc.windspeed) AS DECIMAL(18,2)) AS windspeed,\n CAST(MAX(ccc.fall) AS DECIMAL(18,2)) AS fall\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_live ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\n${SYNTAX_FILTER_STR}\nGROUP BY DATE_FORMAT(ccc.device_time,\'%Y-%m-%d %H:%i\')\nORDER BY dtime DESC', 'dtime', '', '', '塔式起重机运行数据', 'CHART_CRONE1101', '2018-10-19 13:19:48', 'admin', 'aa.device_sn = \'${DeviceSN}\'', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '0', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('50', 'CHART_TYPE.MULTI_AXES', '塔机今日数据', 'DeviceNO', 'SELECT DISTINCT \'设备号：\'+bb.device_sn AS DeviceNO,CASE WHEN\n\'@[GROUP_PARAMS.WINDSPEED_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.WINDSPEED_MAX]\' END AS windspeedMAX, aa.device_sn AS DeviceNO,\nDATE_FORMAT(ccc.device_time,\'%H:%i\') AS dataTime,\nROUND(MAX(ccc.height),2) AS hight,\nROUND(MAX(ccc.windspeed),2) AS windspeed,\nROUND(MAX(ccc.load),4) AS `load`\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_live ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\n${SYNTAX_FILTER_STR} \n GROUP BY \'设备号：\'+bb.device_sn, aa.device_sn, DATE_FORMAT(ccc.device_time,\'%H:%i\')\n ORDER BY dataTime ', 'dataTime', '', '（米/秒）', '塔机今日数据', 'CHART_CRONE111', '2018-10-19 13:20:19', 'admin', 'aa.device_sn=\'${DeviceSN}\' ', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', '1', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('51', 'CHART_TYPE.3D_COLUMN', '塔机今日告警', 'dataTime', 'SELECT DISTINCT \'设备号：\'+bb.device_sn AS DeviceNO,cc.source_id AS IID\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb\nWHERE\naa.source_id = bb.source_id\n${SYNTAX_FILTER_STR}', 'DeviceNO', '报警数量', '', '塔机今日告警', 'CHART_CRONE121', '2018-10-19 13:16:47', 'admin', 'aa.device_sn = \'${DeviceSN}\' and bb.project_id=\'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '0', '0', '0', '1', 'yyyy-MM-dd', 'dataTime', '1', '0', '', '', null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('52', 'CHART_TYPE.TABLE', '塔式起重机提醒数据', '', 'SELECT DISTINCT DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') AS StartTime,\nDATE_FORMAT(ccc.end_time,\'%Y-%m-%d %H:%i:%s\') AS EndTime,\nalarm1 AS Alarm1\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_alarm ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\nAND ccc.alarm1 <> 0\n${SYNTAX_FILTER_STR}\nORDER BY StartTime DESC', 'dtime', '', '', '塔式起重机提醒数据', 'CHART_CRONE1211', '2018-10-19 13:55:10', 'admin', 'cc.source_id = ${device_id} AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') = \'${dataTime}\'', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '1', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('53', 'CHART_TYPE.TABLE', '塔式起重机报警数据', '', 'SELECT DISTINCT DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') AS StartTime,\nDATE_FORMAT(ccc.end_time,\'%Y-%m-%d %H:%i:%s\') AS EndTime,\nalarm2 AS Alarm2\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_alarm ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\nAND ccc.alarm2 <> 0\n${SYNTAX_FILTER_STR}\nORDER BY StartTime DESC', 'dtime', '', '', '塔式起重机报警数据', 'CHART_CRONE1212', '2018-10-19 13:23:06', 'admin', 'cc.source_id = ${device_id} AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') = \'${dataTime}\'  ', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '1', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('54', 'CHART_TYPE.TABLE', '塔式起重机违章数据', '', 'SELECT DISTINCT DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') AS StartTime,\nDATE_FORMAT(ccc.end_time,\'%Y-%m-%d %H:%i:%s\') AS EndTime,\nalarm3 AS Alarm3\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_alarm ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\nAND ccc.alarm3 in (3,12,48,64)\n${SYNTAX_FILTER_STR}\nORDER BY StartTime DESC', 'dtime', '', '', '塔式起重机违章数据', 'CHART_CRONE1213', '2018-10-19 13:23:32', 'admin', 'cc.source_id = ${device_id} AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') = \'${dataTime}\' ', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '1', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('56', 'CHART_TYPE.BAR', '日塔式起重机力矩超120%次数排名', 'reportDate', 'SELECT c.name + DATE_FORMAT(report_date,\'%Y-%m-%d\') AS reportDate,b.device_id,\'设备号：\'+b.device_name AS DeviceNO,SUM(a.PercentMax) AS PercentMax\nFROM f_data_towercrane_worksumbyday a,d_device_tower_crane b,d_data_zhgd_group c\nWHERE a.did = b.id AND a.group_code = c.group_code\nAND \nDATE_FORMAT(reportDate,\'%Y-%m-%d %T\') = DATE_FORMAT(\'${reportDate}\',\'%Y-%m-%d %T\')\nAND a.group_code = \'${groupCode}\' \nGROUP BY c.name + DATE_FORMAT(report_date.\'%Y-%m-%d\'),b.device_id,\'设备号：\'+b.device_name\nORDER BY a.PercentMax', 'DeviceNO', '数量', '', '日塔式起重机力矩超120%次数排名明细', 'CHART_CRONE12', '2018-10-19 14:36:25', 'admin', 'ReportDate  >= convert(datetime, \'${reportDate}\')\nand  ReportDate  <= convert(datetime, \'${reportDate} 23:59:59\')\n and a.group_code = \'${groupCode}\' ', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '0', '0', '0', '0', '', '', '0', '0', null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('57', 'CHART_TYPE.IMAGE', '施工升降机实时状态', '', 'select distinct bb.DeviceNO + \' - \' + convert(varchar(10),aa.HoistsNo) as DeviceNO, aa.HoistsNo, cc.IID,aa.DID, aa.DeviceSN,\r\ncase when ISNULL(aaa.EarlyAlarm, 0)<> 0 then 2\r\n	 when ISNULL(aaa.Alarm, 0)<> 0 then 4\r\n	 when ISNULL(cc.IsOnline, 0)<> 0 then 1\r\n	else 0 end as status\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN  left join  DataCenter.Device_ElevatorLiveData aaa on aaa.IID = cc.IID and convert(varchar(10),aaa.DeviceTime,120) = convert(varchar(10),GETDATE(),120) , \r\nCraneMonitor.Monitor_Device bb\r\nwhere \r\nbb.MonitorType = 1 and bb.ProjectID = a.ProID\r\nand aa.DID = bb.DID and bb.DeviceState = 0 and bb.isMonitor = 1 \r\n${SYNTAX_FILTER_STR} \r\norder by DeviceNO', 'DeviceNO', 'images/eqi/Elevator_', '.png', '升降机状态', 'CHART_ELEVATOR101', '2018-05-29 19:16:54', 'wl', 'a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', '1', 'DEVICE_STATUS', '', 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('58', 'CHART_TYPE.MULTI_AXES', '施工升降机今日监测数据', 'DeviceNO', 'SELECT DISTINCT cc.device_sn AS DeviceSN, bb.source_id AS DID,\r\nCONCAT(LEFT(DATE_FORMAT(ccc.device_time,\'%H:%i\'),4),\'0\') AS dataTime,\r\nROUND(MAX(ccc.percent_rate),4) AS PercentRate,\r\nROUND(MAX(ccc.number_of_people),2) AS NumberOfPeople\r\nFROM \r\n`f_device_elevator` aa  LEFT JOIN `f_device` cc ON cc.device_sn = aa.device_sn , \r\n`d_project_device` bb,\r\n`d_elevator_live` ccc\r\nWHERE \r\nbb.monitor_type = 1 AND aa.source_id = bb.source_id\r\nAND ccc.device_sn = cc.source_id\r\n${SYNTAX_FILTER_STR}\r\n GROUP BY bb.source_id, cc.device_sn, CONCAT(LEFT(DATE_FORMAT(ccc.device_time,\'%H:%i\'),4),\'0\')\r\n ORDER BY DeviceSN,dataTime', 'dataTime', '载重百分比', '%', '升降机数据', 'CHART_ELEVATOR111', '2018-05-30 15:27:18', 'wl', 'cc.device_sn = \'${DeviceSN}\' ', '0', '', '', 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('59', 'CHART_TYPE.TABLE', '施工升降机运行数据', '', 'select distinct CONVERT(varchar(15), ccc.DeviceTime, 25)+\'0\' as dtime,\r\nround(max(ccc.LoadValue),2) as LoadValue,\r\nround(max(ccc.PercentRate),2) as PercentRate,\r\nround(max(ccc.NumberOfPeople),2) as NumberOfPeople,\r\nround(max(ccc.Wind),2) as Wind,\r\nround(max(ccc.Height),2) as Height,\r\nround(max(ccc.Speed),2) as Speed,\r\nround(max(ccc.Angle),2) as Angle,\r\nround(max(ccc.RelFloor),2) as RelFloor,\r\nround(max(ccc.CallFloor),2) as CallFloor\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_ElevatorRunData_@[VAR_MONTH_SUFFIX].Monitor_ElevatorRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere\r\nbb.MonitorType = 1  \r\nand bb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n${SYNTAX_FILTER_STR} \r\ngroup by CONVERT(varchar(15), ccc.DeviceTime, 25)+\'0\'\r\norder by dtime desc', 'dtime', '', '', '施工升降机运行数据 ', 'CHART_ELEVATOR1101', '2018-05-29 19:18:13', 'wl', 'aa.DID = \'${DID}\'', '0', '', '', 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('60', 'CHART_TYPE.COLUMN', '施工升降机今日告警', '', ' SELECT DISTINCT  cc.device_sn AS DeviceSN,cc.source_id AS IID,  \r\n(SELECT SUM(CASE WHEN ccc.early_alarm <> 0 THEN 1 ELSE 0 END) FROM d_elevator_alarm ccc WHERE  ccc.source_id = cc.source_id  \r\nAND ccc.start_time  >=  DATE_FORMAT(NOW(),\"%Y-%m-%d\") ) AS Alarm0,  \r\n(SELECT SUM(CASE WHEN ccc.notice_alarm <> 0 THEN 1 ELSE 0 END) FROM d_elevator_alarm ccc WHERE  ccc.source_id = cc.source_id  \r\nAND ccc.start_time  >=  DATE_FORMAT(NOW(),\"%Y-%m-%d\") ) AS Alarm1  \r\nFROM f_device_elevator aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn ,   \r\nd_project_device bb  \r\nWHERE bb.monitor_type = 1 AND aa.source_id = bb.source_id\r\n ${SYNTAX_FILTER_STR} ', 'DeviceNO', '报警数量', '', '升降机告警', 'CHART_ELEVATOR121', '2018-05-29 19:20:09', 'wl', 'aa.device_sn = \'${DeviceSN}\'', '0', '', '', 'FEATURE_TYPE.ELEVATORMONITOR', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('61', 'CHART_TYPE.TABLE', '施工升降机提醒数据', '', 'select distinct \r\nCONVERT(CHAR(19), ccc.StartTime, 120) as StartTime,\r\nCONVERT(CHAR(19), ccc.EndTime, 120) as EndTime,\r\nccc.Alarm as Alarm1\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nDataCenter.Device_ElevatorAlarm ccc\r\nwhere bb.MonitorType = 1 \r\nand bb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.IID = cc.IID\r\nand ccc.StartTime  > convert(varchar(10),getdate(),120)\r\nand ccc.Alarm <> 0\r\n${SYNTAX_FILTER_STR} \r\norder by StartTime desc', 'dtime', '', '', '施工升降机提醒数据', 'CHART_ELEVATOR1211', '2018-05-29 19:21:25', 'wl', 'cc.IID = ${device_id}', '0', '', '', 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('62', 'CHART_TYPE.TABLE', '施工升降机报警数据', '', 'select distinct \r\nCONVERT(CHAR(19), ccc.StartTime, 120) as StartTime,\r\nCONVERT(CHAR(19), ccc.EndTime, 120) as EndTime,\r\nAlarm2 \r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nDataCenter.Device_ElevatorAlarm ccc\r\nwhere\r\nbb.MonitorType = 1  \r\nand bb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.IID = cc.IID\r\nand ccc.StartTime  > convert(varchar(10),getdate(),120)\r\nand ccc.Alarm2 <> 0\r\n${SYNTAX_FILTER_STR} \r\norder by StartTime desc', 'dtime', '', '', '施工升降机报警数据', 'CHART_ELEVATOR1212', '2018-05-29 19:22:15', 'wl', 'cc.IID = ${device_id}', '0', '', '', 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('63', 'CHART_TYPE.LINE', '今日环境监测数据', '', 'select distinct cc.IID,\r\ncase when \'@[GROUP_PARAMS.PM2.5_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PM2.5_MAX]\' end as paramPmTwoPointFive,\r\ncase when \'@[GROUP_PARAMS.PM10_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PM10_MAX]\' end as paramPmTen,\r\nCONVERT(CHAR(4), ccc.DeviceTime, 24)+\'0\' as dataTime,\r\nround(AVG(ccc.PMTen),4) as PMTen,\r\nround(AVG(ccc.PMTwoPointFive),4) as PMTwoPointFive,\r\nround(AVG(ccc.Noise),4) as Noise\r\nfrom \r\nMonitor_Device aa, Device_Info cc , \r\nMonitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere \r\nccc.DeviceID = cc.IID and  cc.DeviceSN = aa.DeviceSN  \r\n${SYNTAX_FILTER_STR} \r\n group by cc.IID,CONVERT(CHAR(4), ccc.DeviceTime, 24)+\'0\'\r\n order by dataTime', 'dataTime', '', '(ug/m3)', '今日环境数据', 'CHART_DUST111', '2017-09-27 12:38:21', 'lsw', 'aa.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\' and (cc.DeviceSN =\'${DeviceSN}\' or \'${DeviceSN}\' =\'\')', '0', '', '', 'FEATURE_TYPE.EVENMONITOR', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_1_XM', null);
INSERT INTO `zhgd_chart` VALUES ('64', 'CHART_TYPE.LINE', '今日噪声检测数据', '', ' select distinct cc.IID,\r\ncase when \'@[GROUP_PARAMS.NOISE_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.NOISE_MAX]\' end as paramNoise,\r\nCONVERT(CHAR(4), ccc.DeviceTime, 24)+\'0\' as dataTime,\r\nround(AVG(ccc.Noise),4) as Noise\r\nfrom \r\nMonitor_Device aa ,Device_Info cc, \r\nMonitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere \r\nccc.DeviceID = cc.IID and  cc.DeviceSN = aa.DeviceSN \r\n${SYNTAX_FILTER_STR} \r\n group by cc.IID,CONVERT(CHAR(4), ccc.DeviceTime, 24)+\'0\'\r\n order by dataTime', 'dataTime', '', 'db', '今日噪声数据', 'CHART_DUST101', '2017-09-27 12:38:52', 'lsw', 'aa.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\' and (cc.DeviceSN =\'${DeviceSN}\' or \'${DeviceSN}\' =\'\')', '0', '', '', 'FEATURE_TYPE.EVENMONITOR', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_1_XM', null);
INSERT INTO `zhgd_chart` VALUES ('65', 'CHART_TYPE.TABLE', '环境监测运行数据', '', 'select distinct\r\nCONVERT(CHAR(16), DeviceTime, 120) as dataTime,\r\nCAST(AVG(PMTen) as decimal(18,2)) as PMTen,\r\nCAST(AVG(PMTwoPointFive) as decimal(18,2)) as PMTwoPointFive,\r\n CAST(AVG(Noise) as decimal(18,2)) as Noise,\r\n CAST(AVG(Temperature) as decimal(18,2))  as Temperature,\r\n CAST(AVG(Humidity) as decimal(18,2)) as Humidity,\r\nCAST(AVG(WindSpeed) as decimal(18,2)) as WindSpeed,\r\nWindDirection\r\nfrom \r\ndata_Monitor_DustRunData\r\nwhere\r\n1=1  \r\n${SYNTAX_FILTER_STR} \r\n group by CONVERT(CHAR(16), DeviceTime, 120),WindDirection\r\n order by dataTime desc', 'dtime', '', '', '环境监测运行数据', 'CHART_DUST1101', '2018-01-16 08:55:44', 'wl', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and CONVERT(CHAR(10), DeviceTime, 120) = \'${Deviceday}\'\r\n and ((\'${DeviceSN}\' = \'\' and 1=1) or (\'${DeviceSN}\' <> \'\' and DeviceSN = \'${DeviceSN}\'))', '0', '', '', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, '1', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('66', 'CHART_TYPE.LINE', '噪声实时监控', '', 'select distinct cc.IID,\r\ncase when \'@[GROUP_PARAMS.NOISE_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.NOISE_MAX]\' end as paramNoise,\r\nCONVERT(CHAR(4), ccc.DeviceTime, 24)+\'0\' as dataTime,\r\nround(AVG(ccc.Noise),4) as Noise\r\nfrom \r\nMonitor_Device aa , Device_Info cc , \r\nMonitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere \r\nccc.DeviceID = cc.IID and  cc.DeviceSN = aa.DeviceSN and ccc.DeviceTime > DATEADD(mi,-120,GETDATE()) \r\n${SYNTAX_FILTER_STR} \r\n group by  cc.IID,CONVERT(CHAR(4), ccc.DeviceTime, 24)+\'0\'\r\n order by  dataTime', 'dataTime', '', 'dB', '噪声实时监测', 'CHART_DUST121', '2017-10-24 16:35:53', 'admin', 'aa.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\' and (cc.DeviceSN =\'${DeviceSN}\' or \'${DeviceSN}\' =\'\')', '0', '', '', 'FEATURE_TYPE.EVENMONITOR', '0', '1', '600', 'and CONVERT(CHAR(19), ccc.DeviceTime, 24) > \'@[VAR_REALTIME_XVALUE]\'', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('67', 'CHART_TYPE.TABLE', '今日务工人员', '', 'SELECT  DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\') AS today,DATE_FORMAT(DATE_SUB(CURRENT_DATE(),INTERVAL 1 DAY),\'%Y%m%d\') AS yesterday,\r\n DATE_FORMAT(DATE_SUB(CURRENT_DATE(),INTERVAL 2 DAY),\'%Y%m%d\') AS beforeYesterday,g.name,c.unit_name,\r\n(SELECT COUNT(*) FROM `d_worker_attendance` aa WHERE aa.group_id = g.group_id AND aa.record_date = CURRENT_DATE())  AS t_num,\r\nCOUNT(*) AS total_num,\r\n(SELECT COUNT(*) FROM `d_worker_attendance` aa WHERE aa.group_id = g.group_id AND aa.record_date = DATE_SUB(CURRENT_DATE(),INTERVAL 1 DAY)) AS y_num, \r\n(SELECT COUNT(*) FROM `d_worker_attendance` aa WHERE aa.group_id = g.group_id AND aa.record_date = DATE_SUB(CURRENT_DATE(),INTERVAL 2 DAY)) AS by_num\r\nFROM f_group g, f_worker w,`f_cooperator` c\r\nWHERE\r\ng.group_id = w.group_id \r\nAND c.cooperator_id = g.cooperator_id\r\n${SYNTAX_FILTER_STR}\r\nGROUP BY g.name,c.unit_name\r\nORDER BY g.name', 'name', '', '', '今日务工人员', 'CHART_HUMAN101', '2017-12-19 14:16:22', 'lsw', 'w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n AND g.status = 1 ', '0', '', '', 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('68', 'CHART_TYPE.TABLE', '通讯录', '', 'SELECT \r\n  g.`name` groupName,\r\n  c.`unit_name`,\r\n  w.`name`,\r\n  w.`mobile`,\r\n  c.`mobile` nuit_mobile\r\n\r\nFROM \r\n  `f_worker` w,  `f_cooperator` c, `f_group` g\r\nwhere \r\n	w.`group_id` = g.`group_id`\r\n    and w.`cooperator_id` = c.`cooperator_id`\r\n 	${SYNTAX_FILTER_STR}\r\n    order by groupName,c.`unit_name`', 'name', '', '', '通讯录', 'CHART_HUMAN111', '2017-04-25 14:07:36', 'admin', 'w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nand w.`name` like \'%${workerName}%\'\r\nand role_id <> 1', '0', '', '', 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('69', 'CHART_TYPE.LINE', '月度人员趋势', '', 'select themonth, max(num) as num FROM (\r\nselect DATE_FORMAT(wa.record_date,\'%Y%m\') as themonth,\r\n		count(*) as num\r\nfrom `d_worker_attendance` wa\r\nwhere 1=1\r\n${SYNTAX_FILTER_STR}\r\ngroup by wa.record_date) a\r\ngroup by themonth\r\norder by themonth', 'themonth', '人员数量', '人', '月度人员趋势', 'CHART_HUMAN121', '2018-02-27 14:52:55', 'lsw', 'wa.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n AND  DATE_FORMAT(wa.record_date,\'%Y%m\') <= DATE_FORMAT(CURDATE(),\'%Y%m\') AND DATE_FORMAT(wa.record_date,\'%Y%m\') >= DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 YEAR),\'%Y%m\')', '0', '', '', 'FEATURE_TYPE.HUMAN', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('72', 'CHART_TYPE.LINE', '每日进场务工人员趋势', 'themonth', 'select DATE_FORMAT(wa.record_date,\'%Y%m\') as themonth, DATE_FORMAT(wa.record_date,\'%Y%m%d\') as theday,\r\n		count(*) as num\r\nfrom `d_worker_attendance` wa\r\nwhere 1=1\r\n${SYNTAX_FILTER_STR}\r\ngroup by wa.record_date\r\norder by theday', 'theday', '人员数量', '人', '每日进场务工人员趋势', 'CHART_HUMAN1211', '2018-05-28 16:00:10', 'cx', 'wa.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n and DATE_FORMAT(wa.record_date,\'%Y%m\') = \'${theMonth}\'', '0', '', '', 'FEATURE_TYPE.HUMAN', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('74', 'CHART_TYPE.LINE', '塔机15日情况', '', 'SELECT \ndid,device_sn,DATE_FORMAT(report_date,\'%Y-%m-%d\') AS ReportDate,\nload_count,over_load_count\nFROM f_data_towercrane_worksumbyday\nWHERE \nDATE_SUB(CURDATE(), INTERVAL 7 DAY) < report_date\nAND device_sn = \'${DeviceSN}\'AND group_code IN (@[VAR_CHILD_GROUP_CODES])\nORDER BY report_date', 'ReportDate', '', '', '塔机15日情况', 'CHART_CRONE131', '2018-10-19 14:35:35', 'admin', 'DeviceSN = \'${DeviceSN}\'and group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('75', 'CHART_TYPE.LINE', '月度噪音检测', '', ' select CONVERT(CHAR(7), DeviceTime, \r\n120)+\'\' as DeviceTime, case when \'@[GROUP_PARAMS.NOISE_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.NOISE_MAX]\' end as paramNoise,round(max(Noise),4) as Noise\r\n  from data_Monitor_DustRunData\r\nwhere\r\nDeviceTime >=dateadd(year,-1,getdate())\r\n${SYNTAX_FILTER_STR}\r\ngroup by  CONVERT(CHAR(7), DeviceTime, \r\n120)+\'\'\r\norder by DeviceTime', 'DeviceTime', '噪音', 'dB', '月度噪音检测', 'CHART_DUST122', '2017-06-19 13:31:57', 'admin', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and (DeviceSN =\'${DeviceSN}\' or \'${DeviceSN}\' =\'\')', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('76', 'CHART_TYPE.LINE', '月度每日噪音最大值趋势', '', 'select\r\ncase when \'@[GROUP_PARAMS.NOISE_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.NOISE_MAX]\' end as paramNoise,\r\ncase when \'70\'=\'\' then \'0\' else \'70\' end as dayNoise,\r\ncase when \'55\'=\'\' then \'0\' else \'55\' end as nightNoise,\r\nCONVERT(CHAR(10), DeviceTime, 120) as Deviceday, round(max(Noise),4) as Noise,DeviceSN\r\nfrom data_Monitor_DustRunData \r\nwhere \r\n1=1  \r\n${SYNTAX_FILTER_STR}\r\ngroup by CONVERT(CHAR(10), DeviceTime, 120),DeviceSN\r\norder by Deviceday', 'Deviceday', '', 'dB', '月度每日噪音最大值趋势', 'CHART_DUST123', '2018-06-04 15:55:14', 'wl', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and ((\'${Deviceday}\' = \'\' and CONVERT(CHAR(7), DeviceTime, 120) = CONVERT(CHAR(7), getDate(), 120)) or (\'${Deviceday}\' <> \'\' and CONVERT(CHAR(7), DeviceTime, 120) = \'${Deviceday}\'))\r\n and ((\'${DeviceSN}\' = \'\' and 1=1) or (\'${DeviceSN}\' <> \'\' and DeviceSN = \'${DeviceSN}\'))', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, '0', '0', '0', '1', 'yyyy-MM', 'Deviceday', '1', '0', '', '', null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('77', 'CHART_TYPE.AREA', '塔机实时吊重', '', 'SELECT DISTINCT \'设备号：\'+bb.device_sn AS DeviceNO, aa.source_id,aa.device_sn,\nDATE_FORMAT(ccc.device_time,\'%H:%i:%s\') AS dataTime,\nROUND(MAX(ccc.height),2) AS hight,\nROUND(MAX(ccc.load),4) AS `LOAD`,\nROUND(MAX(ccc.angle),4) AS angle\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_live ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\nAND ccc.device_time > DATE_SUB(NOW(), INTERVAL 1 HOUR) \n${SYNTAX_FILTER_STR}\n GROUP BY \'设备号：\'+bb.device_sn,aa.device_sn, aa.source_id, DATE_FORMAT(ccc.device_time,\'%H:%i:%s\')\n ORDER BY dataTime', 'dataTime', '', '吨', '塔机实时吊重', 'CHART_CRONE141', '2018-10-19 13:18:04', 'admin', 'aa.device_sn = \'${DeviceSN}\'', '0', '', '', 'FEATURE_TYPE.CRONEMONITOR', '1', '1', '10', 'and CONVERT& #40;CHAR& #40;19& #41;, ccc.dtime, 24& #41; & gt; & #39;@[VAR_REALTIME_XVALUE]& #39;', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '0', '0', '0', '0', '', '', '0', '0', null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('78', 'CHART_TYPE.AREA', '施工升降机实时载重', 'DeviceNO', 'select distinct bb.DeviceNO + \' - \' + convert(varchar(10),aa.HoistsNo) as DeviceNO,bb.DID,\r\nCONVERT(CHAR(19), ccc.DeviceTime, 24) as dataTime,\r\nround(MAX(ccc.PercentRate),4) as PercentRate,\r\nMAX(ccc.LoadValue) as LoadValue\r\nfrom \r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_ElevatorRunData_@[VAR_MONTH_SUFFIX].Monitor_ElevatorRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere  \r\nbb.MonitorType = 1 and aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n and ccc.DeviceTime > DATEADD(mi,-60,GETDATE()) \r\n${SYNTAX_FILTER_STR} \r\n group by bb.DID,bb.DeviceNO+ \' - \' + convert(varchar(10),aa.HoistsNo), CONVERT(CHAR(19), ccc.DeviceTime, 24)\r\n order by DeviceNO,dataTime', 'dataTime', '载重百分比', 'kg', '升降机载重', 'CHART_ELEVATOR141', '2018-05-29 19:22:55', 'wl', 'cc.DeviceSN = \'${DeviceSN}\'', '0', '', '', 'FEATURE_TYPE.ELEVATORMONITOR', '1', '1', '10', 'and CONVERT(CHAR(19), ccc.DeviceTime, 24) > \'@[VAR_REALTIME_XVALUE]\'', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('79', 'CHART_TYPE.COLUMN', '本月度PM10超标累计天数项目排名(Top 10)', '', 'select top 10 aa.group_code,aa.name,SUM(case when aa.PMTen>aa.param_pmten then 1 else 0 end) as num \r\n from\r\n(select a.group_code,b.name,b.param_pmten,\r\nCONVERT(CHAR(10), a.DeviceTime, 120)+\'\' as DeviceTime,\r\nAVG(a.PMTen) as PMTen\r\nfrom data_Monitor_DustRunData a,data_Zhgd_Group b\r\nwhere  a.group_code=b.group_code \r\nand a.DeviceTime > CONVERT(char(7), getdate() , 120) + \'-1\'\r\n${SYNTAX_FILTER_STR}\r\ngroup by CONVERT(CHAR(10), a.DeviceTime, 120)+\'\',b.param_pmten,\r\na.group_code,b.name) aa\r\ngroup by aa.name,aa.group_code\r\norder by num desc', 'name', '', '天', '本月度PM10超标累计天数项目排名(Top 10)', 'CHART_DUST213', '2017-05-03 10:01:05', 'lsw', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('80', 'CHART_TYPE.MULTI_AXES', '年度累计竣工交付工程统计', '', 'select[month]=convert(varchar(7),project_status_time,120),\r\n[count]=(select COUNT(1) from zhgd_group where convert(varchar(7),project_status_time,120)!>convert(varchar(7),t.project_status_time,120) and group_type = \'ORG_TYPE.PROJECT\' and (project_status = \'PROJECT_STATUS.20\' or project_status = \'PROJECT_STATUS.22\')),\r\n[area]=(select SUM(project_area) from zhgd_group where convert(varchar(7),project_status_time,120)!>convert(varchar(7),t.project_status_time,120) and group_type = \'ORG_TYPE.PROJECT\' and (project_status = \'PROJECT_STATUS.20\' or project_status = \'PROJECT_STATUS.22\'))\r\nfrom zhgd_group t\r\nwhere 1=1 ${SYNTAX_FILTER_STR}\r\n and t.group_type = \'ORG_TYPE.PROJECT\' and (t.project_status = \'PROJECT_STATUS.20\' or t.project_status = \'PROJECT_STATUS.22\') and t.project_status_time >=dateadd(YY,-1,getdate())\r\ngroup by convert(varchar(7),project_status_time,120)\r\norder by count', 'month', '', '', '年度累计竣工交付工程统计', 'CHART_PROJECT_STATUS41', '2017-04-24 15:48:06', 'admin', 't.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD', '', '0', '0', null, '', 'left', 'top', '40', '30', 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('81', 'CHART_TYPE.BAR', '年度累计竣工交付金额统计', '', 'select[month]=convert(varchar(7),project_status_time,120),\r\n[cost]=(select round(SUM(project_cost)/10000,4) from zhgd_group where convert(varchar(7),project_status_time,120)!>convert(varchar(7),t.project_status_time,120) and group_type = \'ORG_TYPE.PROJECT\' and (project_status = \'PROJECT_STATUS.20\' or project_status = \'PROJECT_STATUS.22\'))\r\nfrom zhgd_group t\r\nwhere 1=1 ${SYNTAX_FILTER_STR}\r\nand t.group_type = \'ORG_TYPE.PROJECT\' and (t.project_status = \'PROJECT_STATUS.20\' or t.project_status = \'PROJECT_STATUS.22\') and t.project_status_time >=dateadd(YY,-1,getdate())\r\ngroup by convert(varchar(7),project_status_time,120)', 'month', '', '亿元', '年累计竣工交付金额统计', 'CHART_PROJECT_STATUS51', '2017-04-18 11:24:10', 'admin', 't.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD', '', '0', '0', null, '', null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('84', 'CHART_TYPE.TABLE', '塔式起重机运行数据', '', 'SELECT DISTINCT DATE_FORMAT(device_time,\'%Y-%m-%d %T\') AS dtime,\nROUND(angle,2) AS angle,\nROUND(radius,2) AS radius,\nROUND(hight,2) AS hight,\nROUND(a.load,2) AS load_,\nROUND(percent,2) AS percent,\nROUND(safeload,2) AS safeload,\nROUND(windspeed,2) AS windspeed,\nROUND(fall,2) AS fall\nFROM \nf_data_towercrane_workdata a\nWHERE 1=1 \nAND CONVERT(CHAR(10), device_time,  120) = \'${ReportDate}\' AND device_id = \'${DID}\'\nGROUP BY DATE_FORMAT(device_time,\'%Y-%m-%d %T\'),angle,radius,hight,percent,safeload,windspeed,fall,load_\nORDER BY device_time DESC', 'dtime', '', '', '塔式起重机近日运行数据', 'CHART_CRONE1102', '2018-10-20 19:09:09', 'admin', 'CONVERT(CHAR(10), dtime,  120) = \'${ReportDate}\' and DID = \'${DID}\'', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '1', '1', '1', '0', '', '', '0', null, null, null, null, '', '', '');
INSERT INTO `zhgd_chart` VALUES ('85', 'CHART_TYPE.TABLE', '塔式起重机超载运行数据', '', 'SELECT DISTINCT DATE_FORMAT(device_time,\'%Y-%m-%d %T\') AS dtime,\nROUND(angle,2) AS angle,\nROUND(radius,2) AS radius,\nROUND(hight,2) AS hight,\nROUND(a.load,2) AS load_,\nROUND(percent,2) AS percent,\nROUND(safeload,2) AS safeload,\nROUND(windspeed,2) AS windspeed,\nROUND(fall,2) AS fall\nFROM \nf_data_towercrane_workdata a\nWHERE 1=1 AND is_overload = 1\nAND CONVERT(CHAR(10), device_time,  120) = \'${ReportDate}\' AND device_id = \'${DID}\'\nGROUP BY DATE_FORMAT(device_time,\'%Y-%m-%d %T\'),angle,radius,hight,percent,safeload,windspeed,fall,load_\nORDER BY device_time DESC', 'dtime', '', '', '塔式起重机超载运行数据', 'CHART_CRONE1103', '2018-10-19 14:36:58', 'admin', 'CONVERT(CHAR(10), dtime,  120) = \'${ReportDate}\' and DID = \'${DID}\'', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '1', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('89', 'CHART_TYPE.TABLE', '今日进场务工人员详情', '', 'SELECT g.name AS groupName,f.name AS personName,f.census_register AS censusRegister,CASE WHEN f.sex = \'1\' THEN \'男\' \r\nWHEN f.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS sex,DATE_FORMAT(d.on_time, \'%Y-%m-%d %H:%i:%s\') AS entryDate,\r\nDATE_FORMAT(d.off_time, \'%Y-%m-%d %H:%i:%s\') AS offDate\r\nFROM f_worker f,d_worker_attendance d,f_group g,`f_cooperator` c\r\nWHERE g.group_id=f.group_id AND f.worker_id = d.worker_id AND f.group_id = d.group_id ${SYNTAX_FILTER_STR}\r\nORDER BY entryDate', null, null, null, '进场人员详情', 'CHART_HUMAN13', '2017-12-19 14:16:33', 'lsw', 'd.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\' and DATE_FORMAT(d.record_date,\'%Y%m%d\') = \'${date}\' and g.name = \'${name}\'\r\n AND c.cooperator_id = g.cooperator_id AND c.unit_name= \'${unit_name}\'  AND g.status = 1\r\n', null, null, '', 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('91', 'CHART_TYPE.CHINAMAP', '测试中国地图', '', 'use ZHGD\r\nselect b.name as provinceName_03,COUNT(a.name) as Number_04,\'0,20\' as Interval_01,\'21,40\' as Interval_02 from zhgd_group a,zhgd_standard_code b where a.area_province = b.value group by b.name', '1', '', '', '测试中国地图', 'CHART_CRONE311', '2017-05-10 16:10:44', 'wl', '1 = {1}', '0', '', 'ZHGD', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('92', 'CHART_TYPE.TABLE', '每日进场务工人员详情', '', 'SELECT DATE_FORMAT(d.on_time, \'%Y%m%d %H:%i:%s\') AS theday,f.name AS personName,f.census_register AS censusRegister,\r\nCASE WHEN f.sex = \'1\' THEN \'男\' \r\n         WHEN f.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS sex,g.name AS groupName FROM d_worker_attendance d,f_worker f,f_group g\r\nWHERE d.worker_id=f.worker_id AND f.group_id= g.group_id ${SYNTAX_FILTER_STR}', 'theday', '人员数量', '人', '每日进场务工人员详情', 'CHART_HUMAN14', '2018-01-22 11:02:11', 'lsw', 'd.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\' and DATE_FORMAT(d.record_date,\'%Y%m%d\') = \'${theday}\'', '0', '', '', 'FEATURE_TYPE.HUMAN', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('95', 'CHART_TYPE.PIE', '巡更点今日工作统计', '', 'select \r\na.status,\r\na.pointStatus,\r\na.countPoint,\r\nconcat(a.pointStatus, \' \',b.patrolStatus) as patrolStatus,\r\nb.countPatrolPoint\r\n FROM\r\n(\r\nselect a.`status`, a.project_id,\r\n 		case \r\n        when a.`status` = 1 then \'启用\'\r\n        when a.`status` = 2 then \'停用\'\r\n        END as pointStatus,\r\n        count(distinct a.point_id) as countPoint\r\n from `d_patrol_point` a \r\n group by a.`status`,project_id,pointStatus\r\n ) a,\r\n (\r\nselect a.`status`, a.project_id,\r\n 		case \r\n        when a.`status` = 1 then \'启用\'\r\n        when a.`status` = 2 then \'停用\'\r\n        END as pointStatus,\r\n 		case \r\n        when b.point_id is not null then \'已巡检\'\r\n        else \'未巡检\'\r\n        END as patrolStatus,\r\n        count(distinct a.point_id) as countPatrolPoint\r\n from `d_patrol_point` a left join `d_patrol_route_point_log` b on a.point_id = b.point_id\r\n and b.`is_delete` = 1\r\n  and DATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\')\r\n group by a.`status`,project_id,pointStatus,patrolStatus\r\n ) b\r\nwhere a.status = b.status\r\nand a.project_id = b.project_id\r\n${SYNTAX_FILTER_STR}', 'pointStatus,patrolStatus', '', '个', '巡更点今日工作统计', 'CHART_PATROL101', '2017-05-12 15:27:13', 'admin', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', '', 'FEATURE_TYPE.PROJECT_PATROL', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('96', 'CHART_TYPE.TABLE', '巡更点一览', '', 'SELECT \r\n  a.`name`,\r\n  a.`position`,\r\n  a.`content`,\r\n  CASE \r\n        WHEN a.`times` IS NULL THEN 1\r\n        WHEN a.`times` IS NOT NULL THEN a.`times`\r\n        END AS times,\r\nCASE \r\n        WHEN a.`status` = 1 THEN \'启用\'\r\n        WHEN a.`status` = 2 THEN \'停用\'\r\n        END AS pointStatus,\r\n  b.`member_name`,\r\n  b.`link_tele`\r\n FROM `d_patrol_point` a LEFT JOIN `f_company_employee` b ON a.`member_id` = b.`member_id`\r\nWHERE  1=1\r\n${SYNTAX_FILTER_STR}\r\n', null, null, null, '巡更点一览', 'CHART_PATROL1011', '2017-09-13 07:48:33', 'admin', 'a.`status` = ${status} and a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '1', null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('97', 'CHART_TYPE.TABLE', '今日巡更点记录', '', 'select \r\nDISTINCT\r\n  a.`name`,\r\n  a.`position`,\r\n  a.`content`,\r\n  a.`times`,\r\nDATE_FORMAT(b.commit_time, \'%Y-%m-%d %h:%i:%s\') as commit_time,\r\n  c.`member_name`,\r\n  d.`note`\r\n from `d_patrol_point` a \r\n left join `d_patrol_route_point_log` b on a.point_id = b.point_id and DATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\')\r\n left join `f_company_employee` c on b.`member_id` = c.`member_id` \r\n left join `d_patrol_note` d on b.`note_id`= d.`note_id` where 1=1\r\n${SYNTAX_FILTER_STR}\r\norder by a.name,d.commit_time', null, null, null, '今日巡更点记录', 'CHART_PATROL1012', '2017-05-17 15:05:39', 'lsw', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\' and\r\n ((\'${patrolStatus}\' = \'启用 已巡检\' and b.point_id is not null) or  \r\n (\'${patrolStatus}\' = \'启用 未巡检\' and b.point_id is null and a.status = 1) OR\r\n (\'${patrolStatus}\' = \'停用 未巡检\' and a.status = 2)\r\n )', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('99', 'CHART_TYPE.CIRCLE', '圆', '', 'select a.group_code, \r\na.name,\r\nCOUNT (b.id) as device_num,\r\n\'0,10\' as range1,\r\n\'10,20\' as range2,\r\n\'21,200\' as range3\r\nfrom data_Zhgd_Group a, data_Monitor_Device b\r\nwhere a.group_code = b.group_code\r\ngroup by \r\na.name,a.group_code\r\norder by device_num desc', 'name', '', '', '圆', 'CHART_VIDEO11_copy', '2017-05-12 14:52:36', 'admin', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', 'ZHGD_DATA', '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('100', 'CHART_TYPE.PIE', '巡更线路今日工作统计', '', 'select \r\na.`member_id`,\r\na.`member_name`,\r\na.routeCount,\r\nb.patrolStatus,\r\nb.routePatrolCount\r\nfrom \r\n(select \r\n  a.`project_id`,\r\n  a.`member_id`,\r\n  b.`member_name`,\r\n  count(distinct a.`route_id`) as routeCount\r\n from \r\n`d_patrol_route` a left join `f_company_employee` b on a.`member_id` = b.`member_id`\r\nwhere \r\na.is_delete = 1\r\ngroup by a.`project_id`,a.`member_id`,b.`member_name`) a,\r\n(\r\nselect \r\n  a.`project_id`,\r\n  a.`member_id`,\r\n  c.`member_name`,\r\n  case when rl_id is not null then CONCAT(c.`member_name`,\' 已巡检\') ELSE\r\n  	CONCAT(c.`member_name`,\' 未巡检\') END as patrolStatus,\r\n  count(distinct a.`route_id`) as routePatrolCount\r\n from \r\n`d_patrol_route` a left join `d_patrol_route_log` b on a.`route_id` = b.`route_id` and b.`is_delete` = 1 and b.`success` = 1\r\nand DATE_FORMAT(b.start_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\')\r\nleft join `f_company_employee` c on a.`member_id` = c.`member_id`\r\nwhere \r\na.is_delete = 1\r\ngroup by a.`project_id`,a.`member_id`,patrolStatus) b\r\nwhere a.project_id = b.project_id\r\nand a.member_id = b.member_id\r\n${SYNTAX_FILTER_STR}', 'member_name,patrolStatus', '', '条', '巡更线路今日工作统计', 'CHART_PATROL201', '2017-05-12 15:29:18', 'admin', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', '', 'FEATURE_TYPE.PROJECT_PATROL', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('101', 'CHART_TYPE.TABLE', '巡更线路一览', '', 'SELECT distinct\r\n\r\n  a.`route_id`,\r\n  a.`name`,\r\n  b.`member_name`\r\nFROM \r\n  `d_patrol_route` a left join `f_company_employee` b on a.`member_id` = b.`member_id`\r\nWHERE\r\n	a.`is_delete` = 1\r\n${SYNTAX_FILTER_STR}', null, null, null, '巡更线路一览', 'CHART_PATROL2011', '2017-05-11 14:05:44', 'admin', 'a.`member_id` = \'${member_id}\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('102', 'CHART_TYPE.TABLE', '巡更线路详细', '', 'SELECT \r\n  a.`route_id`,\r\n  b.`name`,\r\n  b.`position`,\r\n  b.`content`\r\nFROM \r\n  `d_patrol_route_point` a left join `d_patrol_point` b on a.`point_id` = b.`point_id`\r\nWHERE\r\na.`is_delete` = 1 ${SYNTAX_FILTER_STR} \r\norder by a.`route_id`', null, null, null, '巡更线路详细', 'CHART_PATROL2021', '2017-05-11 14:25:46', 'admin', 'a.`route_id` = \'${route_id}\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('104', 'CHART_TYPE.TABLE', '今日巡更线路记录', '', 'select distinct\r\n  a.`route_id`,\r\n  a.`project_id`,\r\n  a.`member_id`,\r\n  a.`name`,\r\n  b.rl_id,\r\n  c.`member_name`,\r\n  DATE_FORMAT(b.start_time, \'%Y-%m-%d %h:%i:%s\') as start_time,\r\n  DATE_FORMAT(b.end_time, \'%Y-%m-%d %h:%i:%s\') as end_time\r\n from \r\n`d_patrol_route` a left join `d_patrol_route_log` b on a.`route_id` = b.`route_id` and b.`is_delete` = 1 and b.`success` = 1\r\nand DATE_FORMAT(b.start_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\') \r\nleft join `f_company_employee` c on a.`member_id` = c.`member_id`\r\nwhere \r\na.is_delete = 1 ${SYNTAX_FILTER_STR} ', null, null, null, '今日巡更线路记录', 'CHART_PATROL2031', '2017-05-12 15:30:07', 'admin', 'a.`member_id` = \'${member_id}\'and \r\n((\'${patrolStatus}\' like \'%未巡检\' and b.rl_id is null) or\r\n(\'${patrolStatus}\' like \'%已巡检\' and b.rl_id is not null ))\r\n', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('105', 'CHART_TYPE.TABLE', '今日巡更线路详细记录', '', 'SELECT distinct\r\nb.`name`,\r\nb.`position`,\r\nb.`content`,\r\nd.`member_name`,\r\nDATE_FORMAT(a.`commit_time`, \'%Y-%m-%d %h:%i:%s\') as commit_time,\r\nc.`note`\r\nFROM \r\n  `d_patrol_route_point_log` a left join `d_patrol_point` b on a.point_id = b.`point_id`\r\n  left join `d_patrol_note` c on a.`note_id` = c.`note_id`\r\n  left join `f_company_employee` d on a.`member_id` = d.`member_id`\r\nwhere \r\na.`is_delete` = 1 \r\n ${SYNTAX_FILTER_STR} \r\norder by a.`rpl_id`', null, null, null, '今日巡更线路详细记录', 'CHART_PATROL2041', '2017-05-11 16:48:50', 'admin', 'a.`rl_id` =  \'${rl_id}\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('108', 'CHART_TYPE.KPILIST', '移动巡更提示语', '', ' SELECT \r\n DISTINCT(\r\n ROUND(\r\n (SELECT \r\n  COUNT(distinct a.point_id)\r\n FROM `d_patrol_route_point_log` a \r\n WHERE DATE_FORMAT(a.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\') \r\n${SYNTAX_FILTER_STR} )\r\n /\r\n (SELECT \r\n  COUNT(a.point_id)\r\n FROM `d_patrol_point` a \r\n WHERE 1=1\r\n  ${SYNTAX_FILTER_STR}  AND a.status = 1 )\r\n ,\r\n 2\r\n )*100\r\n ) AS coverageRate,\r\n CASE WHEN \'@[GROUP_PARAMS.MOVEONE_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.MOVEONE_MAX]\' END AS moveOneMax,\r\n CASE WHEN \'@[GROUP_PARAMS.MOVEONE_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.MOVEONE_MIN]\' END AS moveOneMin,\r\n CASE WHEN \'@[GROUP_PARAMS.MOVETWO_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.MOVETWO_MAX]\' END AS moveTwoMax,\r\n CASE WHEN \'@[GROUP_PARAMS.MOVETWO_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.MOVETWO_MIN]\' END AS moveTwoMin,\r\n CASE WHEN \'@[GROUP_PARAMS.MOVETHREE_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.MOVETHREE_MAX]\' END AS moveThreeMax,\r\n CASE WHEN \'@[GROUP_PARAMS.MOVETHREE_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.MOVETHREE_MIN]\' END AS moveThreeMin\r\n FROM d_patrol_point', null, null, null, '移动巡更提示语', 'CHART_PATROL2051', '2018-08-23 17:34:58', 'sbb', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', 'CHART_TEMPLATE_3.0', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0,GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('112', 'CHART_TYPE.KPILIST', '路线巡更完成率提示语', '', ' SELECT \r\n DISTINCT(\r\n ROUND(\r\n (SELECT \r\n  COUNT(distinct a.route_id)\r\n FROM `d_patrol_route_log` a \r\n WHERE DATE_FORMAT(a.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\') and a.is_delete=1\r\n ${SYNTAX_FILTER_STR})\r\n /\r\n (SELECT \r\n  COUNT(a.route_id)\r\n FROM `d_patrol_route` a \r\n WHERE 1=1 and a.is_delete=1\r\n  ${SYNTAX_FILTER_STR})\r\n ,\r\n 2\r\n )*100\r\n ) AS coverageRate,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDONE_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDONE_MAX]\' END AS findOneMax,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDONE_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDONE_MIN]\' END AS findOneMin,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDTWO_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDTWO_MAX]\' END AS findTwoMax,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDTWO_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDTWO_MIN]\' END AS findTwoMin,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDTHREE_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDTHREE_MAX]\' END AS findThreeMax,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDTHREE_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDTHREE_MIN]\' END AS findThreeMin,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDFOUR_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDFOUR_MAX]\' END AS findFourMax,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDFOUR_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDFOUR_MIN]\' END AS findFourMin,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDFIVE_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDFIVE_MAX]\' END AS findFiveMax,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDFIVE_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDFIVE_MIN]\' END AS findFiveMin,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDSIX_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDSIX_MAX]\' END AS findSixMax,\r\n CASE WHEN \'@[GROUP_PARAMS.FINDSIX_MIN]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.FINDSIX_MIN]\' END AS findSixMin\r\n\r\n FROM d_patrol_route', null, null, null, '温馨提示', 'CHART_PATROL514', '2017-05-16 15:33:19', 'dxf', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('113', 'CHART_TYPE.TABLE', '今日巡更次数排名', '', 'SELECT (@rowNO := @rowNo+1) AS rowno,c.name, c.num FROM\r\n(SELECT b.member_id,b.member_name AS NAME,a.num AS num FROM `f_company_employee` b ,\r\n(SELECT b.rl_id,b.company_id, b.member_id,b.project_id,COUNT(*) AS num FROM d_patrol_route_log b WHERE b.`is_delete` = 1 AND b.`success` = 1 \r\nAND DATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\')  GROUP BY b.member_id \r\nORDER BY num DESC) a\r\nWHERE b.member_id = a.member_id \r\nAND b.company_id= a.company_id\r\n ${SYNTAX_FILTER_STR}\r\nORDER BY num DESC) c,(SELECT @rowNO :=0) f', null, null, null, '今日巡更次数排名', 'CHART_PATROL1013', '2017-05-17 08:26:10', 'lsw', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('114', 'CHART_TYPE.CIRCLE', '今日巡更次数前三名', '', ' SELECT \'0,10\' AS range1,\r\n\'10,50\' AS range2,\r\n\'50,200\' AS range3,b.member_id AS member_id ,b.member_name AS name,a.num AS num FROM `f_company_employee` b ,\r\n(SELECT b.rl_id,b.company_id, b.member_id,b.project_id,COUNT(*) AS num FROM d_patrol_route_log b WHERE b.`is_delete` = 1 AND b.`success` = 1 \r\nAND DATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\')  GROUP BY b.member_id \r\nORDER BY num DESC) a\r\nWHERE b.member_id = a.member_id \r\nAND b.company_id= a.company_id\r\n ${SYNTAX_FILTER_STR}\r\nORDER BY num DESC \r\nLIMIT 3', 'name', null, null, '今日巡更前三名', 'CHART_PATROL1014', '2017-05-17 08:27:37', 'lsw', 'a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('115', 'CHART_TYPE.TABLE', '今日巡更详情', '', 'SELECT c.route_id,c.rl_id,d.name AS rounteName, c.member_id,b.member_name AS personName, DATE_FORMAT(c.start_time, \'%Y-%m-%d %h:%i:%s\') AS starttime,DATE_FORMAT(c.end_time, \'%Y-%m-%d %h:%i:%s\') AS endtime  FROM `f_company_employee` b,\r\n(SELECT b.* FROM d_patrol_route_log b WHERE b.`is_delete` = 1 AND b.`success` = 1 AND DATE_FORMAT(b.start_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\' )  \r\n${SYNTAX_FILTER_STR}\r\nAND DATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURRENT_DATE(),\'%Y%m%d\') ORDER BY b.route_id) c,\r\nd_patrol_route d\r\nWHERE c.route_id = d.route_id\r\nAND b.company_id =c.company_id  \r\n${SYNTAX_FILTER_STR}', null, null, null, '今日巡更详情', 'CHART_PATROL1015', '2017-09-06 09:27:21', 'lsw', 'b.`member_id` = \'${member_id}\'', null, null, '', 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
