INSERT INTO `zhgd_chart` VALUES ('125', 'CHART_TYPE.SIMPLE_VIEW', '巡更点巡更线时实数据', '', 'select\r\ncase when \'@[GROUP_PARAMS.PATROL.ABNORMAL]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PATROL.ABNORMAL]\' end as abnormal,\r\ncase when \'@[GROUP_PARAMS.PATROLNUM]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PATROLNUM]\' end as patrolNum,\r\ncase when \'@[GROUP_PARAMS.PATROL.FINISH]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PATROL.FINISH]\' end as patrolFinish,\r\ncase when \'@[GROUP_PARAMS.PATROL.ROUTE]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PATROL.ROUTE]\' end as rouitNum,\r\ncase when \'@[GROUP_PARAMS.PATROL.ROUTEFINISH]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PATROL.ROUTEFINISH]\' end as rouitFinish', null, null, null, '巡更实时数据', 'CHART_PATROL_MOVE_FIND', '2017-09-12 14:51:29', 'wl', '', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_PATROL_MOVE_FIND', '0', null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
