INSERT INTO `zhgd_chart` VALUES ('127', 'CHART_TYPE.SIMPLE_VIEW', '项目进度', '', 'select case when \'@[GROUP_PARAMS.DEVIATIONDAY]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.DEVIATIONDAY]\' end as deviationday,\r\n case \r\n when convert(decimal(18,1),a.beginday*100.0/a.totalday) IS null then \'未知\'\r\n when convert(decimal(18,1),a.beginday*100.0/a.totalday) >= 100 then \'100%\'\r\n  else\r\n  convert(varchar(9),rtrim(convert(decimal(18,1),a.beginday*100.0/a.totalday))+\'%\') \r\nend as percentage,\r\ncase when a.endday <= 0 then \'已完工\' \r\n when a.endday IS null then \'未知\'\r\n else convert(varchar(9),a.endday)\r\n end as endday,\r\ncase when a.project_plan_end_time is null then \'未知\' else a.project_plan_end_time end as project_plan_end_time,\r\ncase when a.project_plan_start_time is null then \'未知\' else a.project_plan_start_time end as project_plan_start_time\r\nfrom\r\n(select DATEDIFF(DAY,project_plan_start_time,GETDATE()) as beginday,\r\n DATEDIFF(DAY,GETDATE(),project_plan_end_time) as endday,\r\n DATEDIFF(DAY,project_plan_start_time,GETDATE()) + DATEDIFF(DAY,GETDATE(),project_plan_end_time) as totalday,\r\n convert(varchar(10),project_plan_end_time ,120) as project_plan_end_time,\r\n  convert(varchar(10),project_plan_start_time ,120) as project_plan_start_time\r\nfrom zhgd_group where 1=1  ${SYNTAX_FILTER_STR}) a', null, null, null, '项目进度', 'CHART_GENERAL_PROJECT01', '2017-06-15 10:07:59', 'lsw', 'group_code in (@[VAR_CHILD_GROUP_CODES])', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_GENERAL_PROJECT01', null, null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('131', 'CHART_TYPE.SIMPLE_VIEW', '工程概况', '', ' \r\nSELECT case when \'@[GROUP_PARAMS.UPAREA]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.UPAREA]\' end AS project_up_area,\r\ncase when \'@[GROUP_PARAMS.DOWNAREA]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.DOWNAREA]\' end AS project_down_area,\r\ncase when \'@[GROUP_PARAMS.UPHEIGHT]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.UPHEIGHT]\' end AS project_up_height,\r\ncase when \'@[GROUP_PARAMS.DOWNHEIGHT]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.DOWNHEIGHT]\' end AS project_down_height,\r\ncase when \'@[GROUP_PARAMS.DOWNFLOOR]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.DOWNFLOOR]\' end AS project_down_floor,\r\ncase when \'@[GROUP_PARAMS.UPFLOOR]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.UPFLOOR]\' end as project_up_floor,\r\ncase when \'@[GROUP_PARAMS.BUILDINGNUM]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.BUILDINGNUM]\' end as building_num,\r\ncase when \'@[GROUP_PARAMS_PROJECT_VIEW_URL]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS_PROJECT_VIEW_URL]\' end as projectInfo_url,\r\ncase when \'@[ATTACHMENT.GAIKUANG]\'=\'\' then \'未知\' else \'@[ATTACHMENT.GAIKUANG]\' end as image_url\r\n FROM zhgd_group\r\n', null, null, null, '工程概况', 'CHART_GENERAL_PROJECT02', '2017-12-05 12:10:06', 'qjh', '1=1', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_GENERAL_PROJECT02', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('132', 'CHART_TYPE.SIMPLE_VIEW', '安全检查', '', 'select \r\ncase when \'@[GROUP_PARAMS.NOMODIFY]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.NOMODIFY]\' end as nomodify,\r\ncase when \'@[GROUP_PARAMS.MODIFY]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.MODIFY]\' end as modify,\r\ncase when \'@[GROUP_PARAMS.NOVERIFY]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.NOVERIFY]\' end as noverify,\r\ncase when \'@[GROUP_PARAMS.SAFETY.WZG_URL]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.SAFETY.WZG_URL]\' end as nomodifyURL', null, null, null, '安全检查', 'CHART_SAFETY_CHECK', '2017-09-13 15:36:38', 'wl', '1=1', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_SAFETY_CHECK', '0', '0', null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('133', 'CHART_TYPE.JSON', '塔式起重机违章数据', '', 'select distinct cc.IID, cc.DeviceSN, aaa.dtime, aaa.angle,\r\ncase when dd.L1 = 0 then 0 \r\n	else (aaa.radius / dd.L1)*100 end as fudu,\r\ncase when ISNULL(aaa.Alarm0, 0)<> 0 then 2 \r\n	 when ISNULL(aaa.Alarm1, 0)<> 0 then 3 \r\n	 when ISNULL(aaa.Alarm2, 0)<> 0 then 4 \r\n	 when aaa.Alarm3 in(3,12,48,64) then 5 \r\n	 when ISNULL(cc.IsOnline, 0)<> 0 then 1 \r\n	else 0 end as status,\r\n	(select count(*) from Monitor_TowerCraneWorkData.Monitor_TowerCraneWorkData_@[VAR_MONTH_SUFFIX] da1 where da1.id_device = cc.IID and da1.insertTime >= convert(varchar, getdate(), 101))\r\n	+ (select count(*) from Monitor_TowerCraneWorkDataEx.Monitor_TowerCraneWorkDataEx_@[VAR_MONTH_SUFFIX] da2 where da2.id_device = cc.IID and da2.insertTime >= convert(varchar, getdate(), 101)) as CraneCount\r\nfrom \r\nCraneMonitor.Monitor_TowerCraneDevice aa  \r\nleft join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN  \r\nleft join DataCenter.Device_TowerCraneStructure dd on dd.IID = cc.IID \r\nleft join Monitor_TowerCraneRunData_@[VAR_MONTH_SUFFIX].Monitor_TowerCraneRunData_@[VAR_DAY_SUFFIX] aaa \r\non aaa.id_device = cc.IID and aaa.dtime = (select MAX(aaaa.dtime) from Monitor_TowerCraneRunData_@[VAR_MONTH_SUFFIX].Monitor_TowerCraneRunData_@[VAR_DAY_SUFFIX] aaaa where aaaa.id_device = aaa.id_device and aaaa.dtime > DATEADD(hh,-1,GETDATE())) , \r\nCraneMonitor.Monitor_Device bb\r\nwhere \r\n aa.DID = bb.DID and bb.DeviceState = 0 and bb.isMonitor = 1 \r\n${SYNTAX_FILTER_STR} \r\norder by aaa.dtime desc, cc.DeviceSN', 'dtime', '', '', '获取塔基信息用于项目场景图', 'CHART_CRANE_SENCE', '2018-05-11 08:15:11', 'admin', 'bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', null, '0', '0', '0', null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
