INSERT INTO `zhgd_chart` VALUES ('137', 'CHART_TYPE.SIMPLE_VIEW', '塔机时实状态数据', '', 'SELECT A.IsOnline,B.IsntOnline,C.craneTotal,(C.craneTotal-CASE WHEN \'@[GROUP_PARAMS.CRONENUM]\'=\'\' \nTHEN \'0\' ELSE \'@[GROUP_PARAMS.CRONENUM]\' END) AS surplus\nFROM\n(SELECT COUNT(*)AS IsOnline\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc \nON cc.device_sn = aa.device_sn  LEFT JOIN  d_monitor_towercrane_workdata aaa \nON aaa.source_id = cc.source_id AND DATE_FORMAT(aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \n${SYNTAX_FILTER_STR}\n AND cc.online=1) A,\n (SELECT COUNT(*) AS IsntOnline\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc \nON cc.device_sn = aa.device_sn  LEFT JOIN  d_monitor_towercrane_workdata aaa \nON aaa.source_id = cc.source_id AND DATE_FORMAT(aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \n${SYNTAX_FILTER_STR}\n AND cc.online=0) B,\n (SELECT COUNT(*)AS craneTotal\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc \nON cc.device_sn = aa.device_sn  LEFT JOIN  d_monitor_towercrane_workdata aaa \nON aaa.source_id = cc.source_id AND DATE_FORMAT(aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0  \n${SYNTAX_FILTER_STR}\n) C ', null, null, null, '塔机时实数据', 'CHART_SAFETY_CRONE_INF', '2018-10-19 13:25:18', 'admin', 'bb.project_id= \'@[VAR_SELECTED_PROJECT_CODE]\' ', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_SAFETY_CRONE_INF', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_2_0_XM', '');
