INSERT INTO `zhgd_chart` VALUES ('156', 'CHART_TYPE.LIST', '项目级塔式起重机吊运工效统计', '', ' SELECT \n COUNT(DISTINCT a.subsystem_code) AS totalDeviceNum,\n ROUND(SUM(a.load),2) AS totalLoad,\n ROUND(AVG(a.load),2)  AS avgLoad,\n ROUND(SUM(a.load)/COUNT(DISTINCT a.subsystem_code),2) AS effect,\n (SELECT SUM(load_count) FROM f_data_towercrane_worksumbyday  WHERE  report_date >= DATE_SUB(CURDATE(),INTERVAL 1 DAY)  ${SYNTAX_FILTER_STR}) AS totalCount,\n (SELECT ROUND(AVG(b.load),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y-%m\') =  DATE_FORMAT(DATE_SUB(CURDATE(),INTERVAL 1 MONTH),\'%Y-%m\')  ${SYNTAX_FILTER_STR})  AS mAvgLoad,\n (SELECT ROUND(SUM(b.load)/COUNT(DISTINCT b.subsystem_code)/COUNT(DISTINCT DATE_FORMAT(b.insert_time,\'%Y-%m-%d\')),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y-%m\') = DATE_FORMAT(DATE_SUB(CURDATE(),INTERVAL 1 MONTH),\'%Y-%m\')  ${SYNTAX_FILTER_STR})  AS mEffect,\n (SELECT ROUND(AVG(b.load),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y\') = DATE_FORMAT(DATE_SUB(CURDATE(),INTERVAL 1 YEAR),\'%Y\')  ${SYNTAX_FILTER_STR})  AS yAvgLoad,\n (SELECT ROUND(SUM(b.load)/COUNT(DISTINCT b.subsystem_code)/COUNT(DISTINCT DATE_FORMAT(b.insert_time,\'%Y-%m-%d\')),2) FROM f_data_towercrane_workdata b WHERE DATE_FORMAT(b.insert_time,\'%Y\') = DATE_FORMAT(DATE_SUB(CURDATE(),INTERVAL 1 YEAR),\'%Y\')  ${SYNTAX_FILTER_STR})  AS yEffect\n FROM f_data_towercrane_workdata a \n WHERE \n DATE_FORMAT(b.insert_time,\'%Y-%m-%d\')  = DATE_SUB(CURDATE(),INTERVAL 1 DAY)\n ${SYNTAX_FILTER_STR}', '1', '', '', '塔机工效统计', 'CHART_CRONE34', '2018-10-19 14:30:54', 'admin', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('157', 'CHART_TYPE.MULTI_AXES', '月度塔式起重机吊运工效统计', '', 'select \r\nconvert(varchar(7),a.insertTime,120) as month,\r\nROUND(avg(a.load_),2)  as avgLoad,\r\nROUND(sum(a.load_)/COUNT(distinct a.id_device)/COUNT(distinct convert(varchar(10),a.insertTime,120)),2) as effect\r\nfrom data_Monitor_TowerCraneWorkData a\r\nwhere \r\na.insertTime  > DATEADD(month,-12,getdate()) ${SYNTAX_FILTER_STR}\r\ngroup by convert(varchar(7),a.insertTime,120)\r\norder by month ', 'month', '', '', '塔机月度工效', 'CHART_CRONE35', '2017-09-29 16:30:43', 'lsw', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', null, null, null, null, 'right', '', '-150', null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', '0', null, '0', '0', '0', null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
