INSERT INTO `zhgd_chart` VALUES ('171', 'CHART_TYPE.TABLE', '巡更点记录（近七日）', '', 'SELECT \r\nDISTINCT\r\n  a.`name`,\r\n  a.`position`,\r\n  a.`content`,\r\n  a.`times`,\r\nd.commit_time,\r\nc.`member_name`,\r\n  d.`note`,d.`note_id`,\r\n  (SELECT COUNT(*) FROM d_file f WHERE f.source_type = 51 AND f.source_id = d.note_id) AS photoNum\r\n FROM `d_patrol_point` a, d_patrol_note d, `f_company_employee` c\r\n WHERE a.point_id = d.point_id AND c.member_id = d.member_id AND c.company_id = d.company_id\r\n AND d.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND d.is_delete = 1\r\n${SYNTAX_FILTER_STR}\r\nORDER BY a.name,d.commit_time DESC', null, null, null, '巡更点记录', 'CHART_PATROL1016', '2018-07-05 15:57:03', 'wl', 'a.point_id = \'${point_id}\'', null, null, null, 'FEATURE_TYPE.PROJECT_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', '0', '', '1', '1', '0', '0', '', '', '0', null, null, null, null, '', '', null);
