INSERT INTO `zhgd_chart` VALUES ('179', 'CHART_TYPE.TABLE', '未做安全教育人员', '', 'select a.UNIT_NAME,a.NAME,a.ENTRY_TIME,a.GNAME,a.SEX ,\r\n(@rowNO := @rowNo+1) AS rowno from\r\n(SELECT \r\n  c.`unit_name` as UNIT_NAME,\r\n  w.`name` as NAME,\r\n  w.entry_time as ENTRY_TIME,\r\n \r\n  g.name GNAME,\r\n  CASE WHEN w.sex = \'1\' THEN \'男\' \r\n         WHEN w.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS SEX\r\n  FROM \r\n  `f_worker` w,  `f_cooperator` c, `f_group` g \r\nwhere \r\n	w.`group_id` = g.`group_id`\r\n    and w.`cooperator_id` = c.`cooperator_id`\r\nand  w.status=\'1\'\r\nand w.train_count=\'0\'\r\n ${SYNTAX_FILTER_STR}\r\norder by w.entry_time desc) a,(SELECT @rowNO :=0) f', null, null, null, '未做安全教育人员', 'CHART_SAFETY_EDUCATION2', '2017-09-13 07:49:33', 'admin', 'w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '1', '1', null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('180', 'CHART_TYPE.TABLE', '花名册', '', 'SELECT \r\n  w.`name` AS NAME,\r\n  c.`unit_name` AS UNIT_NAME,\r\n  g.name GNAME,\r\n  CASE WHEN w.sex = \'1\' THEN \'男\' \r\n         WHEN w.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS SEX,\r\n  w.age AS AGE,\r\n  w.census_register AS CENSUS_REGISTER,\r\n  w.id_card AS ID_CARD,\r\n  w.timecard AS TIMECARD,\r\n  CASE \r\n  WHEN w.status = \'1\' THEN \'正常\' \r\nWHEN w.status = \'2\' THEN \'离场\'  \r\nWHEN w.status = \'3\' THEN \'删除\'  \r\nWHEN w.status = \'4\' THEN \'黑名单\'    \r\nELSE \'其他\' END AS STATUS,\r\n  DATE_FORMAT(w.entry_time,\'%Y-%m-%d\') AS ENTRY_TIME\r\nFROM \r\n  `f_worker` w \r\n  LEFT JOIN `f_group` g  ON  w.`group_id` = g.`group_id`\r\nLEFT JOIN `f_cooperator` c ON w.`cooperator_id` = c.`cooperator_id` \r\nWHERE 1=1 \r\n${SYNTAX_FILTER_STR}\r\nORDER BY w.entry_time DESC\r\n', null, null, null, '花名册', 'CHART_SAFETY_EDUCATION_ROSTER', '2017-09-13 07:49:29', 'admin', 'w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '1', '1', null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('181', 'CHART_TYPE.TABLE', '今日进场工人详情', '', 'SELECT (@rowNO := @rowNo+1) AS rowno, a.entrytime,a.outime,a.personName,a.censusRegister,a.unit_name,\r\na.sex,a.groupName,a.dict_value from\r\n(SELECT DATE_FORMAT(d.on_time, \'%Y-%m-%d %h:%i:%s\') AS entrytime,h.unit_name,DATE_FORMAT(d.off_time, \'%Y-%m-%d %h:%i:%s\') AS outime,f.name AS personName,f.census_register AS censusRegister,\r\nCASE WHEN f.sex = \'1\' THEN \'男\' \r\n         WHEN f.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS sex,g.name AS groupName,s.dict_value\r\n FROM f_worker f\r\n LEFT JOIN \r\n s_sys_dict s ON s.dict_id =f.profession_id AND s.dict_key=\'construct_profession\' , \r\n d_worker_attendance d,f_group g,f_cooperator h\r\nWHERE d.worker_id=f.worker_id AND f.group_id= g.group_id  AND d.cooperator_id =h.cooperator_id and  d.off_time IS NULL\r\n and DATE_FORMAT(d.record_date,\'%Y%m%d\')=DATE_FORMAT(CURDATE(),\'%Y%m%d\')\r\n${SYNTAX_FILTER_STR})a,(SELECT @rowNO:=0) b', null, null, null, '进场工人详情', 'CHART_SAFETY_TODAY_HUMAN2', '2018-01-08 08:33:24', 'lsw', 'd.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '1', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('182', 'CHART_TYPE.TABLE', '塔式起重机报警数据', '', 'SELECT DISTINCT \'设备号：\'+bb.device_sn AS DeviceNO,cc.source_id AS IID, cc.device_sn AS DeviceSN, \nDATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') AS StartTime,\nDATE_FORMAT(ccc.end_time,\'%Y-%m-%d %H:%i:%s\') AS EndTime,\nccc.alarm2 AS Alarm2\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn  \nLEFT JOIN  d_monitor_towercrane_workdata aaa ON aaa.source_id = cc.source_id \nAND DATE_FORMAT(aaa.device_time,\'%Y-%m-%d\') = DATE_FORMAT(NOW(),\'%Y-%m-%d\') , \nd_project_device bb,\nd_towercrane_alarm ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \nAND ccc.source_id = cc.source_id\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%Y-%m-%d\')\nAND ccc.alarm2 <> 0\n${SYNTAX_FILTER_STR}\nORDER BY DeviceNo,StartTime DESC', null, null, null, '塔式起重机报警数据', 'CHART_SAFETY_CRONE_INF2', '2018-10-19 13:54:04', 'admin', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' ', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('184', 'CHART_TYPE.SIMPLE_VIEW', '项目看板', '', 'select \r\ncase when \'@[GROUP_PARAMS.DEVIATIONDAY]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.DEVIATIONDAY]\' end as deviationday,\r\ncase when \'@[GROUP_PARAMS.NOMODIFY]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.NOMODIFY]\' end as nomodify,\r\ncase when \'@[GROUP_PARAMS.MODIFY]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.MODIFY]\' end as modify,\r\ncase when \'@[GROUP_PARAMS.NOVERIFY]\'=\'\' then \'未知\' else \'@[GROUP_PARAMS.NOVERIFY]\' end as noverify,\r\ncase when \'@[GROUP_PARAMS.HUMAN.JCGR]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.HUMAN.JCGR]\' end as JCGR,\r\ncase when \'@[GROUP_PARAMS.HUMAN.ZCGR]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.HUMAN.ZCGR]\' end as ZCGR,\r\ncase when \'@[GROUP_PARAMS.PATROL.ABNORMAL]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PATROL.ABNORMAL]\' end as abnormal', null, null, null, '项目看板', 'CHART_GENERAL_PROJECT04', '2017-09-12 14:35:11', 'wl', '1=1', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_GENERAL_PROJECT04', '0', null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
