INSERT INTO `zhgd_chart` VALUES ('187', 'CHART_TYPE.TABLE', '塔式起重机报警数据', '', 'SELECT DISTINCT \'设备号：\'+bb.device_sn AS DeviceNO,cc.source_id AS IID, cc.device_sn AS DeviceSN, \nDATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') AS StartTime,\nDATE_FORMAT(ccc.end_time,\'%Y-%m-%d %H:%i:%s\') AS EndTime,\nccc.alarm1 AS Alarm1\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn  \nLEFT JOIN  d_monitor_towercrane_workdata aaa ON aaa.source_id = cc.source_id \nAND DATE_FORMAT(aaa.device_time,\'%Y-%m-%d\') = DATE_FORMAT(NOW(),\'%Y-%m-%d\') , \nd_project_device bb,\nd_towercrane_alarm ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \nAND ccc.source_id = cc.source_id\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%Y-%m-%d\')\nAND ccc.alarm1 <> 0\n${SYNTAX_FILTER_STR}\nORDER BY DeviceNo,StartTime DESC', null, null, null, '塔式起重机提醒数据', 'CHART_SAFETY_CRONE_INF3', '2018-10-19 13:53:23', 'admin', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' ', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, '', '1', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', '');
