INSERT INTO `zhgd_chart` VALUES ('198', 'CHART_TYPE.MULTI_AXES', '项目水电用量', '', 'SELECT DATE_FORMAT(b.status_datetime,\'%Y-%m\')AS MONTH, SUM(b.water) AS water,SUM(b.electricity) AS electricity FROM zhgd_group_status b \nWHERE 1=1 ${SYNTAX_FILTER_STR}\nGROUP BY DATE_FORMAT(b.status_datetime,\'%Y-%m\') ORDER BY b.status_datetime ASC ', 'MONTH', null, null, '水电表读数', 'CHART_WATER_ELECTRICITY', '2018-10-25 23:31:51', 'admin', 'b.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', null, null, null, null, '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, '', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('199', 'CHART_TYPE.MULTI_AXES', '月度水电用量', '', 'select * into #tmp_WaterElectricity from(\r\nselect *,row_number() OVER(ORDER BY  month) AS id from (select convert(varchar(10),b.status_datetime,120)as month, b.electricity ,b.water from zhgd_group a,zhgd_group_status b \r\nwhere a.id=b.group_id  ${SYNTAX_FILTER_STR}) as t\r\n) as table_source\r\nselect * into #tmp_nWaterElectricity from(\r\nselect left(month,7) as month,electricity, water ,(select electricity from #tmp_WaterElectricity where ID=a.ID+1)-electricity as  month_electricity,(select water from #tmp_WaterElectricity where ID=a.ID+1)-water as  month_water from #tmp_WaterElectricity as a \r\n) as table_source\r\nselect month,month_water,month_electricity from #tmp_nWaterElectricity where month_electricity is not null and month_water is not null\r\ndrop table #tmp_WaterElectricity\r\ndrop table #tmp_nWaterElectricity', 'month', null, null, '月度水电用量', 'CHART_MONTH_WATER_ELECTRICITY', '2017-08-08 10:54:22', 'ltc', 'a.group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', null, '', null, null, null, null, '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, null, '0', null, null, null, null, null, null, null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('200', 'CHART_TYPE.TABLE', '塔式起重机预警数据', '', 'SELECT DISTINCT DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') AS StartTime,\nDATE_FORMAT(ccc.end_time,\'%Y-%m-%d %H:%i:%s\') AS EndTime,\nalarm0 AS Alarm0\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_alarm ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\nAND ccc.alarm0 <> 0\n${SYNTAX_FILTER_STR}\nORDER BY StartTime DESC', null, null, null, '塔式起重机预警数据', 'CHART_CRONE1210', '2018-10-19 14:28:19', 'admin', 'cc.source_id = ${device_id} AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d %H:%i:%s\') = \'${dataTime}\' ', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, '', '1', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('201', 'CHART_TYPE.TABLE', '施工升降机预警数据', '', 'select distinct \r\nCONVERT(CHAR(19), ccc.StartTime, 120) as StartTime,\r\nCONVERT(CHAR(19), ccc.EndTime, 120) as EndTime,\r\nccc.EarlyAlarm as Alarm0\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nDataCenter.Device_ElevatorAlarm ccc\r\nwhere\r\nbb.MonitorType = 1 \r\nand bb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.IID = cc.IID\r\nand ccc.StartTime  > convert(varchar(10),getdate(),120)\r\nand ccc.EarlyAlarm <> 0\r\n${SYNTAX_FILTER_STR} \r\norder by StartTime desc', null, null, null, '施工升降机预警数据', 'CHART_ELEVATOR1210', '2018-05-29 19:20:57', 'wl', 'cc.IID = ${device_id}', null, null, null, 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('202', 'CHART_TYPE.TABLE', '今日进场女工人详情', '', 'SELECT (@rowNO := @rowNo+1) AS rowno, a.entrytime,a.outime,a.personName,a.censusRegister,a.unit_name,\r\na.groupName,a.dict_value FROM\r\n(SELECT DATE_FORMAT(d.on_time, \'%Y-%m-%d %h:%i:%s\') AS entrytime,h.unit_name,DATE_FORMAT(d.off_time, \'%Y-%m-%d %h:%i:%s\') AS outime,f.name AS personName,f.census_register AS censusRegister,\r\ng.name AS groupName,s.dict_value\r\n FROM f_worker f\r\n LEFT JOIN \r\n s_sys_dict s ON s.dict_id =f.profession_id AND s.dict_key=\'construct_profession\' , \r\n d_worker_attendance d,f_group g,f_cooperator h\r\nWHERE d.worker_id=f.worker_id AND f.group_id= g.group_id  AND d.cooperator_id =h.cooperator_id \r\n AND DATE_FORMAT(d.record_date,\'%Y%m%d\')=DATE_FORMAT(CURDATE(),\'%Y%m%d\') AND f.sex = \'2\'\r\n${SYNTAX_FILTER_STR})a,(SELECT @rowNO:=0) b', null, null, null, '今日进场女工人详情', 'CHART_SAFETY_TODAY_HUMAN3', '2018-01-08 08:33:18', 'lsw', 'd.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '1', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('203', 'CHART_TYPE.TABLE', '升降机预警数据', '', 'select CONVERT(CHAR(19), ccc.StartTime, 120) as StartTime,CONVERT(CHAR(19), ccc.EndTime, 120) as EndTime,ccc.EarlyAlarm as Alarm0,bb.DeviceNO \r\nfrom DataCenter.Device_ElevatorAlarm ccc,CraneMonitor.Monitor_Device bb,\r\nCraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc on aa.DeviceSN = cc.DeviceSN\r\nwhere bb.MonitorType = 1 and ccc.IID = cc.IID and aa.DID = bb.DID and ccc.EarlyAlarm <> 0 and ccc.StartTime >= CONVERT(VARCHAR(10),getdate(),120)  \r\n${SYNTAX_FILTER_STR} order by ccc.StartTime desc', null, null, null, '升降机预警数据', 'CHART_SAFETY_ELEVATOR_INF2', '2018-07-03 08:46:27', 'wl', 'bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, '', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_1_0', null);
