INSERT INTO `zhgd_chart` VALUES ('205', 'CHART_TYPE.TABLE', '今日进场特种作业人员详情', '', 'SELECT (@rowNO := @rowNo+1) AS rowno, a.entrytime,a.outime,a.personName,a.censusRegister,a.unit_name,\r\na.sex,a.groupName,a.dict_value FROM\r\n(SELECT DATE_FORMAT(d.on_time, \'%Y-%m-%d %h:%i:%s\') AS entrytime,h.unit_name,DATE_FORMAT(d.off_time, \'%Y-%m-%d %h:%i:%s\') AS outime,f.name AS personName,f.census_register AS censusRegister,\r\nCASE WHEN f.sex = \'1\' THEN \'男\' \r\n         WHEN f.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS sex,g.name AS groupName,s.dict_value\r\n FROM f_worker f\r\n LEFT JOIN \r\n s_sys_dict s ON s.dict_id =f.profession_id AND s.dict_key=\'construct_profession\' , \r\n d_worker_attendance d,f_group g,f_cooperator h\r\nWHERE d.worker_id=f.worker_id AND f.group_id= g.group_id  AND d.cooperator_id =h.cooperator_id \r\n AND DATE_FORMAT(d.record_date,\'%Y%m%d\')=DATE_FORMAT(CURDATE(),\'%Y%m%d\') AND s.dict_id=9\r\n${SYNTAX_FILTER_STR})a,(SELECT @rowNO:=0) b', null, null, null, '今日进场特种作业人员详情', 'CHART_SAFETY_TODAY_HUMAN4', '2018-01-08 08:33:11', 'lsw', 'd.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '1', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
