INSERT INTO `zhgd_chart` VALUES ('224', 'CHART_TYPE.LINE2', '月度气温趋势', '', ' select a.data_create_time,a.tempMin,a.tempMax,cast((convert(float,a.tempMin)+convert(float,a.tempMax))/2 as decimal(12,1)) as tempavg\r\n from \r\n(select \r\n SUBSTRING(PARSENAME(REPLACE([temp],\'~\',\'.\'),2),0,len(PARSENAME(REPLACE([temp],\'~\',\'.\'),2))) as tempMin,\r\n SUBSTRING(PARSENAME(REPLACE([temp],\'~\',\'.\'),1),0,len(PARSENAME(REPLACE([temp],\'~\',\'.\'),1))) as tempMax,temp,CONVERT(varchar(10), data_create_time,120) as data_create_time \r\nfrom data_Weather\r\n where  1=1 ${SYNTAX_FILTER_STR} \r\n) a\r\norder by data_create_time', 'data_create_time', '气温', '（℃）', '月度气温趋势', 'CHART_WEATHER01', '2018-01-05 08:28:23', 'lsw', '((\'${month}\' = \'\' and CONVERT(varchar(7),data_create_time,120) = CONVERT(varchar(7),GETDATE(),120)) \r\n or \r\n (\'${month}\' <> \'\' and CONVERT(varchar(7),data_create_time,120) =\'${month}\')\r\n )\r\n  and group_code in (@[VAR_CHILD_GROUP_CODES])', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', '0', null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
