INSERT INTO `zhgd_chart` VALUES ('226', 'CHART_TYPE.3D_COLUMN', '塔机今日告警总数', '', 'SELECT DISTINCT \'项目告警数\' AS ProName,SUM(CASE WHEN aaa.alarm0 <> 0 THEN 1 ELSE 0 END) Alarm0,\nSUM(CASE WHEN aaa.alarm1 <> 0 THEN 1 ELSE 0 END) Alarm1,SUM(CASE WHEN aaa.alarm2 <> 0 THEN 1 ELSE 0 END) Alarm2\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn  = aa.device_sn\nLEFT JOIN d_towercrane_alarm aaa ON aaa.source_id = cc.source_id AND\nDATE_FORMAT(aaa.end_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id ${SYNTAX_FILTER_STR}', 'ProName', '报警数量', '', '塔机今日告警总数', 'CHART_GOVT_CRONE_ALARM', '2018-10-19 13:17:27', 'admin', 'bb.project_id =\'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'FEATURE_TYPE.CRONEMONITOR', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, '', '0', '0', '0', '0', '', '', '0', '0', null, null, null, '', 'GROUP_VERSION_2_1_XM', '');
