INSERT INTO `zhgd_chart` VALUES ('239', 'CHART_TYPE.JSON', '', '', 'SELECT CONCAT(REPEAT(\'0\', 6 - length(Value)), CAST(Value AS char(6))) AS Value\nFROM (\n	SELECT ROUND(SUM(b.meter_diff_value * a.electricity_ct * a.electricity_pt), 0) AS Value\n	FROM d_device_electricity a, f_electricity b\n	WHERE a.group_code = b.group_code\n		AND a.device_sn = b.device_id\n		AND b.is_master_meter = 1\n		AND DATE_FORMAT(b.meter_datetime, \'%Y-%m\') = DATE_FORMAT(CURRENT_DATE, \'%Y-%m\')\n		AND a.device_type = 20\n		${SYNTAX_FILTER_STR} \n) a', null, null, null, '总电表本月用电量，用于项目场景图', 'CHART_ELECTRICTY_SENCE', '2018-05-11 08:17:18', 'admin', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
