INSERT INTO `zhgd_chart` VALUES ('241', 'CHART_TYPE.COLUMN', '月度用电趋势', 'DeviceNO', 'select (sum(a.meter_diff_value)*b.electricity_ct*b.electricity_pt) as diffvalue, DATE_FORMAT(a.meter_datetime,\'%y-%m\') as DeviceTime,\nb.device_sn,b.device_name from f_electricity a,d_device_electricity b where a.group_code = b.group_code and a.device_id = b.device_sn and b.device_type = 20  \n${SYNTAX_FILTER_STR} group by DATE_FORMAT(a.meter_datetime,\'%Y-%m\'),b.electricity_ct,b.electricity_pt,b.device_sn,b.device_name order by DATE_FORMAT(a.meter_datetime,\'%Y-%m\') asc', 'DeviceTime', '用电量（度）', 'kW·h', '月度用电趋势', 'CHART_ELECTRICITY', '2018-07-26 14:15:40', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and ((\'${DeviceSN}\'=\'\' and a.is_master_meter = 1) or (\'${DeviceSN}\'!=\'\' and b.device_sn =\'${DeviceSN}\'))', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, '', '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('242', 'CHART_TYPE.COLUMN', '每天用电趋势', '', 'select (round(sum(a.meter_diff_value)*b.electricity_ct*b.electricity_pt,2)) as diffvalue,DATE_FORMAT(a.meter_datetime,\'%Y-%m-%d\') as DeviceTime,b.device_sn \nfrom f_electricity a,d_device_electricity b \nwhere a.group_code = b.group_code and\na.device_id = b.device_sn and b.device_type = 20  ${SYNTAX_FILTER_STR} \ngroup by DATE_FORMAT(a.meter_datetime,\'%Y-%m-%d\'),b.electricity_ct,b.electricity_pt,b.device_sn order by DATE_FORMAT(a.meter_datetime,\'%Y-%m-%d\') asc', 'DeviceTime', '用电量（度）', 'kW·h', '每天用电趋势', 'CHART_ELECTRICITY02', '2018-08-07 10:47:05', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and b.device_sn =\'${pivotParam1}\' and DATE_FORMAT(a.meter_datetime,\'%Y-%m\') = \'${DeviceTime}\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, '', '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('243', 'CHART_TYPE.TABLE', '设备每天用电趋势', '', 'SELECT ROUND(a.meter_value, 2) AS meter_value\n	, DATE_FORMAT(a.meter_datetime, \'%Y-%m-%d %H:%i:%s\') AS device_time\nFROM f_electricity a, d_device_electricity b\nWHERE a.device_id = b.device_sn\n	AND b.device_type = 20\n	 ${SYNTAX_FILTER_STR}\nGROUP BY DATE_FORMAT(a.meter_datetime, \'%Y-%m-%d %H:%i:%s\'), a.meter_value\nORDER BY DATE_FORMAT(a.meter_datetime, \'%Y-%m-%d %H:%i:%s\') ASC', null, null, null, '项目每天电表读数详情', 'CHART_ELECTRICITY03', '2018-03-14 20:10:06', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' \nAND \nb.device_sn =\'${DeviceSN}\' \nAND \nDATE_FORMAT(a.meter_datetime,\'%Y-%m-%d %H:%i:%s\')', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
