INSERT INTO `zhgd_chart` VALUES ('246', 'CHART_TYPE.COLUMN', '项目每天用水趋势', '', 'select sum(a.meter_diff_value) as diffvalue,DATE_FORMAT(a.meter_datetime,\'%Y-%m-%d\') as DeviceTime,b.device_sn from \nf_water a,d_device_water b where a.group_code = b.group_code and\na.device_id = b.device_sn and b.device_type = 21 ${SYNTAX_FILTER_STR} group by DATE_FORMAT(a.meter_datetime,\'%Y-%m-%d\'),b.device_sn order by DATE_FORMAT(a.meter_datetime,\'%Y-%m-%d\') asc', 'DeviceTime', '用水量（吨）', 't', '项目每天用水趋势', 'CHART_WATER02', '2018-01-18 16:09:25', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and b.device_sn =\'${pivotParam1}\' and DATE_FORMAT(a.meter_datetime,\'%Y-%m\') = \'${DeviceTime}\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('247', 'CHART_TYPE.TABLE', '项目每天水表读数详情', '', 'select round(a.meter_value,2) as meter_value,CONVERT(CHAR(100), a.data_time, 120) as DeviceTime from \r\ndata_Utility_Water a,data_Monitor_Device b where \r\na.device_id = b.DeviceSN and b.MonitorType = 21  ${SYNTAX_FILTER_STR} group by CONVERT(CHAR(100), a.data_time, 120),a.meter_value order by CONVERT(CHAR(100), a.data_time, 120) asc', null, null, null, '项目每天水表读数详情', 'CHART_WATER03', '2017-12-25 17:13:36', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and b.DeviceSN =\'${DeviceSN}\' and CONVERT(CHAR(10), a.data_time, 120) = \'${DeviceTime}\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('248', 'CHART_TYPE.SIMPLE_VIEW', '水表数据详情', '', 'SELECT b.device_sn, b.device_name, DATE_FORMAT(a.meter_datetime, \'%Y-%m\') AS dataMonth\n	, DATE_FORMAT(a.meter_datetime, \'%Y-%m-%d\') AS dataDay\n	, SUM(a.meter_diff_value) AS diffvalue, a.is_master_meter AS ismaster\nFROM f_water a, d_device_water b\nWHERE a.group_code = b.group_code\n	AND a.device_id = b.device_sn\n	AND b.device_type = 21\n	AND a.is_master_meter = 1\n	${SYNTAX_FILTER_STR}\nGROUP BY b.device_sn, b.device_id, a.is_master_meter, DATE_FORMAT(a.meter_datetime, \'%Y-%m\'), DATE_FORMAT(a.meter_datetime, \'%Y-%m-%d\')\nORDER BY device_sn, dataMonth, dataDay', null, null, null, '水表数据详情', 'CHART_WATER07', '2018-07-26 14:18:12', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_WATER07', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_2_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('249', 'CHART_TYPE.LINE', '支撑轴力曲线图', 'newData', 'select CONVERT(varchar(10),a.data_time,120) as Time,a.device_sn as SN,\r\nb.threshold_value,c.threshold_value as louValue,a.axial_force_value,CONVERT(varchar(7),a.data_time,120) as newData\r\nfrom data_MaterialStructureAxialForce a,data_Device_Threshold b,data_Device_Threshold c\r\nwhere a.device_sn = b.DeviceSN and b.threshold_code = \'DEVICE_TYPE.34.SENSOR_NAME\' and a.group_code = b.group_code\r\nand a.device_sn = c.DeviceSN and c.threshold_code = \'DEVICE_TYPE.34.FLOOR\' and\r\na.data_time = (select MAX(cc.data_time) from data_MaterialStructureAxialForce cc where cc.device_sn = a.device_sn and CONVERT(varchar(10),cc.data_time,120)=CONVERT(varchar(10),a.data_time,120)) ${SYNTAX_FILTER_STR} order by CONVERT(varchar(10),a.data_time,120) asc', 'Time', '支撑轴力', 'kN', '支撑轴力曲线图', 'CHART_STRUCTURE_AXIAIFORCE', '2017-12-18 15:44:33', 'qjh', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and a.device_sn in (${DeviceSN})\r\nand ((\'${Time}\'=\'\' and CONVERT(varchar(7),a.data_time,120)=CONVERT(varchar(7),getdate(),120)) or (\'${Time}\'<>\'\' and CONVERT(varchar(7),a.data_time,120)=\'${Time}\'))\r\n', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '1', 'yyyy-MM', 'Time', '1', '0', null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('250', 'CHART_TYPE.SIMPLE_VIEW', '深基坑支撑内力每日详情', '', ' select \r\na.ddevice_name as well_num,a.group_code,a.device_sn,a.data_time,\r\n(SUBSTRING(d.threshold_value,LEN(d.threshold_value)-5,LEN(d.threshold_value))) as num,\r\nround(c.threshold_value,2) as initial_value, --初始值\r\n round(a.axial_force_value,2) as local_value, --本次\r\n  round(a.axial_force_value-a.day_diff_value,2) as last_value, --上次\r\n  round(a.day_diff_value,2) as local_various_value, --本次变化\r\n round(a.axial_force_value-c.threshold_value,2) as local_total_value, --累计变化 \r\n round(e.threshold_value,2) as design_value, --构件承载能力设计值\r\n  round(f.threshold_value,2) as alarm_value--预警值\r\n from data_MaterialStructureAxialForce a ,data_Device_Threshold c,data_Device_Threshold d\r\n,data_Device_Threshold e,data_Device_Threshold f\r\n where \r\n  a.device_sn = c.DeviceSN and c.threshold_code = \'DEVICE_TYPE.34.INITIAL_VALUE\' and a.group_code = c.group_code and\r\n  a.device_sn = d.DeviceSN and d.threshold_code = \'DEVICE_TYPE.34.SENSOR_NAME\' and a.group_code = d.group_code and\r\n  a.device_sn = e.DeviceSN and e.threshold_code = \'DEVICE_TYPE.34.DESIGN_VALUE\' and a.group_code = e.group_code and\r\n  a.device_sn = f.DeviceSN and f.threshold_code = \'DEVICE_TYPE.34.THRESHOLD1\' and a.group_code = f.group_code\r\n    ${SYNTAX_FILTER_STR} \r\n order by well_num desc', 'dataTime', '支撑内力', 'kN', '深基坑支撑轴力每日详情', 'CHART_STRUCTURE_AXIAIFORCE02', '2017-12-01 14:52:12', 'lsw', '((\'${data_time}\' =\'\' and convert(varchar(13),a.data_time,120) =(select  convert(varchar(13),MAX(cc.data_time),120) from \r\n dbo.data_MaterialStructureAxialForce cc where a.device_sn = cc.device_sn))\r\n or (\'${data_time}\' <> \'\' and  convert(varchar(13),a.data_time,120) = \r\n (select top 1 convert(varchar(13),data_time,120) from\r\n  data_MaterialStructureAxialForce where convert(varchar(13),data_time,120) < \'${data_time}\' order by data_time desc)))\r\n and a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nand ((\'${rate001}\'=\'1\' and a.device_sn in (\'${DeviceSN001}\'))\r\n or (\'${rate001}\'=\'2\' and 1=1)\r\nor(\'${rate001}\'= \'\' and 1=1))\r\n', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_STRUCTURE_AXIAIFORCE02', '0', '1', '0', '1', 'yyyy-MM-dd HH', 'data_time', '2', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('251', 'CHART_TYPE.SIMPLE_VIEW', '支撑轴力', '', 'select  round(a.day_diff_value,2) as day_diff_value,b.threshold_value,c.threshold_value as louValue,CONVERT(varchar(20),a.data_time,120) as newDates\r\nfrom data_MaterialStructureAxialForce a,data_Device_Threshold b,data_Device_Threshold c\r\nwhere a.device_sn = b.DeviceSN and b.threshold_code = \'DEVICE_TYPE.34.SENSOR_NAME\' and a.group_code = b.group_code\r\nand a.device_sn = c.DeviceSN and c.threshold_code = \'DEVICE_TYPE.34.FLOOR\' and a.group_code = c.group_code\r\nand a.data_time = (select MAX(cc.data_time) from data_MaterialStructureAxialForce cc where cc.device_sn = a.device_sn)\r\nand a.device_sn = \r\n(select  top 1 aa.device_sn from data_MaterialStructureAxialForce aa,data_Device_Threshold dd \r\nwhere dd.DeviceSN = aa.device_sn and dd.threshold_code = \'DEVICE_TYPE.34.FLOOR\' and aa.group_code = dd.group_code and dd.threshold_value = c.threshold_value \r\nand aa.data_time = a.data_time order by abs(aa.day_diff_value) desc) ${SYNTAX_FILTER_STR} order by c.threshold_value', 'dataTime', '支撑内力', 'kN', '支撑轴力', 'CHART_STRUCTURE_AXIAIFORCE03', '2017-12-01 15:27:45', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_STRUCTURE_AXIAIFORCE03', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('253', 'CHART_TYPE.SPLINE', '深层水平位移曲线图', 'newData', '\r\nselect a.x_value,(a.x_value-a.x_day_diff_value) as x_day_value,a.x_3day_value,a.x_7day_value,a.x_14day_value,a.x_30day_value,b.threshold_value as thresholdValue,c.threshold_value+\'m\' as yValue,CONVERT(varchar(10),a.data_time,120) as newData\r\nfrom data_MaterialStructureDisplace a,data_Device_Threshold b,data_Device_Threshold c\r\nwhere\r\na.device_sn = b.DeviceSN and b.threshold_code = \'DEVICE_TYPE.32.THRESHOLD3\' and a.group_code = b.group_code\r\nand a.device_sn = c.DeviceSN and c.threshold_code = \'DEVICE_TYPE.32.DEEP\' and a.group_code = c.group_code\r\nand a.data_time = (select MAX(cc.data_time) from data_MaterialStructureDisplace cc where a.device_sn = cc.device_sn)\r\n${SYNTAX_FILTER_STR} order by ABS(c.threshold_value) asc', 'yValue', '位移量', 'mm', '深层水平位移曲线图', 'CHART_STRUCTURE_DISPLACEX', '2017-12-01 15:34:00', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and a.device_sn in (${DeviceSN})', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', '1', null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('254', 'CHART_TYPE.SIMPLE_VIEW', '深层土体水平位移', '', 'select top 1 round(a.x_day_diff_value,2) as x_day_diff_value,b.threshold_value as DeviceNO,CONVERT(varchar(20),a.data_time,120) as newDates\r\nfrom data_MaterialStructureDisplace a,data_Device_Threshold b\r\nwhere a.device_sn = b.DeviceSN and b.threshold_code =\'DEVICE_TYPE.32.SENSOR_NAME\' and a.group_code = b.group_code and\r\na.data_time = (select MAX(cc.data_time) from data_MaterialStructureDisplace cc where a.device_sn = cc.device_sn)\r\n${SYNTAX_FILTER_STR} order by ABS(a.x_day_diff_value) desc', 'dataTime', '位移', 'mm', '深层土体水平位移', 'CHART_STRUCTURE_DISPLACEX02', '2017-12-01 15:35:48', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_STRUCTURE_DISPLACEX02', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('257', 'CHART_TYPE.SIMPLE_VIEW', 'Y轴位移今日实时监测', '', ' select a.device_name as well_num,a.group_code,a.device_sn,a.data_time,\r\na.device_sn as num, \r\n round(a.x_value,2) as local_total_value,\r\n round(a.x_value-a.x_day_diff_value,2) as last_total_value,\r\n round(a.x_day_diff_value,2) as local_value,\r\n round(a.x_day_diff_value,2) as local_value,\r\nconvert(varchar(19),dateadd(DD,-1,a.data_time),120) AS yesterday,\r\nconvert(varchar(19),a.data_time,120) AS today,\r\nb.threshold_value as threshold_value\r\n  from data_MaterialStructureDisplace a, data_Device_Threshold b,data_Device_Threshold d\r\n   where a.device_sn = b.DeviceSN and b.threshold_code = \'DEVICE_TYPE.32.DEEP\' and a.group_code = b.group_code and\r\n  a.device_sn = d.DeviceSN and d.threshold_code = \'DEVICE_TYPE.32.SENSOR_NAME\' and  a.group_code = d.group_code\r\n  ${SYNTAX_FILTER_STR}\r\n order by a.device_name', 'dataTime', '位移', 'mm', '深基坑位移每日详情', 'CHART_STRUCTURE_DISPLACEY', '2018-03-13 15:37:05', 'lsw', ' ((\'${data_time}\' =\'\' and convert(varchar(13),a.data_time,120) =(select  convert(varchar(13),MAX(cc.data_time),120) from \r\n dbo.data_MaterialStructureDisplace cc where a.device_sn = cc.device_sn))\r\n or (\'${data_time}\' <> \'\' and  convert(varchar(13),a.data_time,120) = (select top 1 convert(varchar(13),data_time,120) from data_MaterialStructureDisplace where convert(varchar(13),data_time,120) < \'${data_time}\' order by data_time desc)))\r\n and a.group_code=\'@[VAR_SELECTED_PROJECT_CODE]\'\r\n and ((\'${rate001}\'=\'1\' and a.device_sn in (\'${DeviceSN001}\'))\r\n or (\'${rate001}\'=\'2\' and a.device_sn in (\'${DeviceSN002}\'))\r\n or (\'${rate001}\'= \'3\' and 1=1)\r\nor(\'${rate001}\'= \'\' and 1=1))', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_STRUCTURE_DISPLACEY', '0', '1', '0', '1', 'yyyy-MM-dd HH', 'data_time', '2', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
