INSERT INTO `zhgd_chart` VALUES ('265', 'CHART_TYPE.LINE', '水位变化曲线图', 'monthTime', 'select (round(a.water_value*1000,0)-ISNULL(zz.threshold_value,0))  as diffvalue,CONVERT(char(10),a.data_time,120) as dataTime,CONVERT(char(7),a.data_time,120) as monthTime,a.device_sn,\r\ntt.threshold_value as ddevice_name,dd.threshold_value as upValue,ff.threshold_value as lowerValue\r\nfrom data_MaterialStructureWater a,data_Device_Threshold tt,data_Device_Threshold dd,data_Device_Threshold ff,data_Device_Threshold zz\r\nwhere  \r\ntt.threshold_code = \'DEVICE_TYPE.31.SENSOR_NAME\' and a.device_sn = tt.DeviceSN and a.group_code = tt.group_code and\r\ndd.threshold_code = \'DEVICE_TYPE.31.THRESHOLD1\' and a.device_sn = dd.DeviceSN and a.group_code = dd.group_code and\r\nff.threshold_code = \'DEVICE_TYPE.31.THRESHOLD2\' and a.device_sn = ff.DeviceSN and a.group_code = ff.group_code and\r\nzz.threshold_code = \'DEVICE_TYPE.31.POSITION\' and a.device_sn = zz.DeviceSN and a.group_code = zz.group_code and\r\na.data_time = (select MAX(cc.data_time) from data_MaterialStructureWater cc where a.device_sn = cc.device_sn and CONVERT(char(10),cc.data_time,120) = CONVERT(char(10),a.data_time,120))\r\n ${SYNTAX_FILTER_STR} order by CONVERT(char(10),a.data_time,120) asc', 'dataTime', '水位累计变化', 'mm', '水位变化曲线图', 'CHART_STRUCTURE_WATER', '2018-01-15 15:03:00', 'lsw', ' a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and a.device_sn in (${DeviceSN}) \r\nand ((\'${dataTime}\'=\'\' and CONVERT(char(7),a.data_time,120) = CONVERT(char(7),GETDATE(),120)) or ((\'${dataTime}\'<>\'\' and CONVERT(char(7),a.data_time,120)=\'${dataTime}\')))', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '1', 'yyyy-MM', 'dataTime', '1', '0', 'datetime', '%Y-%m-%d', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('266', 'CHART_TYPE.LINE', '水位变化速率曲线图', 'monthTime', 'select round(isNULL(a.day_diff_value,0)*1000,0) as day_diff_value,CONVERT(char(10),a.data_time,120) as dataTime,CONVERT(char(7),a.data_time,120) as monthTime,a.device_sn,\r\ntt.threshold_value as ddevice_name,dd.threshold_value as upValue,ff.threshold_value as lowerValue\r\nfrom data_MaterialStructureWater a,data_Device_Threshold tt,data_Device_Threshold dd,data_Device_Threshold ff \r\nwhere\r\ntt.threshold_code = \'DEVICE_TYPE.31.SENSOR_NAME\' and a.device_sn = tt.DeviceSN and a.group_code = tt.group_code and\r\ndd.threshold_code = \'DEVICE_TYPE.31.SPEEDTHRESHOLD1\' and a.device_sn = dd.DeviceSN and a.group_code = dd.group_code and\r\nff.threshold_code = \'DEVICE_TYPE.31.SPEEDTHRESHOLD2\' and a.device_sn = ff.DeviceSN and  a.group_code = ff.group_code and\r\na.data_time = (select MAX(cc.data_time) from data_MaterialStructureWater cc where a.device_sn = cc.device_sn and CONVERT(char(10),cc.data_time,120) = CONVERT(char(10),a.data_time,120))\r\n${SYNTAX_FILTER_STR} order by CONVERT(char(10),a.data_time,120) asc', 'dataTime', '变化速率', 'mm/d', '水位变化速率曲线图', 'CHART_STRUCTURE_WATER02', '2018-01-15 15:13:27', 'lsw', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and a.device_sn in (${DeviceSN})\r\nand ((\'${dataTime}\'=\'\' and CONVERT(char(7),a.data_time,120) = CONVERT(char(7),GETDATE(),120)) or ((\'${dataTime}\'<>\'\' and CONVERT(char(7),a.data_time,120)=\'${dataTime}\')))', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '1', 'yyyy-MM', 'dataTime', '1', '0', 'datetime', '%Y-%m-%d', null, '', 'GROUP_VERSION_2_0_XM', null);
