INSERT INTO `zhgd_chart` VALUES ('269', 'CHART_TYPE.TABLE', '安全隐患部门详情', '', 'SELECT DISTINCT d.company_name AS departmentName,\'总公司\' AS projectType,\'3\' AS finish,\'4\' AS notfinish,a.department_id,a.company_id,\r\n(SELECT COUNT(cc.inspect_id) FROM d_inspect cc WHERE cc.department_id IS NULL AND cc.status=2 AND cc.project_id = a.project_id AND cc.company_id = a.company_id) AS modifyNum,\r\n(SELECT COUNT(dd.inspect_id) FROM d_inspect dd WHERE dd.department_id IS NULL AND dd.status=1 AND dd.project_id = a.project_id AND dd.company_id = a.company_id) AS nomodifyNum\r\nFROM d_inspect a,f_company d WHERE a.department_id IS NULL AND a.company_id = d.company_id ${SYNTAX_FILTER_STR}\r\n\r\nUNION ALL\r\n\r\nSELECT DISTINCT b.department_name AS departmentName,\r\n \'分公司\'  AS projectType,\r\n\'3\' AS finish,\'4\' AS notfinish,a.department_id,a.company_id,\r\n(SELECT COUNT(cc.inspect_id) FROM d_inspect cc WHERE cc.department_id = a.department_id AND cc.status=2 AND cc.project_id = a.project_id) AS modifyNum,\r\n(SELECT COUNT(dd.inspect_id) FROM d_inspect dd WHERE dd.department_id = a.department_id AND dd.status=1 AND dd.project_id = a.project_id) AS nomodifyNum\r\nFROM d_inspect a LEFT JOIN f_department b ON a.department_id = b.department_id\r\nWHERE a.department_id IS NOT NULL AND a.department_id > 0 ${SYNTAX_FILTER_STR}\r\nGROUP BY departmentName \r\n\r\nUNION ALL\r\n\r\nSELECT DISTINCT c.project_title AS departmentName,\r\n\'项目\' AS projectType,\r\n\'3\' AS finish,\'4\' AS notfinish,a.department_id,a.company_id,\r\n(SELECT COUNT(cc.inspect_id) FROM d_inspect cc WHERE cc.department_id = a.department_id AND cc.status=2 AND cc.project_id = a.project_id) AS modifyNum,\r\n(SELECT COUNT(dd.inspect_id) FROM d_inspect dd WHERE dd.department_id = a.department_id AND dd.status=1 AND dd.project_id = a.project_id) AS nomodifyNum\r\nFROM d_inspect a ,d_construction_project c\r\nWHERE c.project_id = a.project_id  AND a.department_id < 0 ${SYNTAX_FILTER_STR}\r\nGROUP BY departmentName', null, null, null, '安全隐患部门详情', 'CHART_GENERAL_SAFE05', '2018-02-26 10:14:05', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.SAFE_PROBLEM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('270', 'CHART_TYPE.SIMPLE_VIEW', '安全隐患问题列表详情', '', 'SELECT a.inspect_id,DATE_FORMAT(a.gmt_create,\'%m-%d %h:%i\') AS createTime,a.type,a.items,a.position,a.comment,a.day,\r\na.is_read,a.status,DATE_FORMAT(a.finish_time,\'%m-%d %h:%i\') AS finish_time,\r\nTIMESTAMPDIFF(SECOND,DATE_ADD(a.gmt_create, INTERVAL a.day DAY),a.finish_time) AS timeliness,\r\n(SELECT b.member_name FROM f_company_employee b WHERE a.create_id = b.member_id AND a.company_id = b.company_id) AS cmember_name,\r\n(SELECT COUNT(c.file_name) FROM d_file c WHERE a.inspect_id = c.source_id AND c.source_type = 70) AS file_name,\r\n(SELECT d.member_name FROM f_member d WHERE a.member_id = d.member_id) AS member_name,\r\nCASE WHEN a.department_id > 0 THEN b.department_name ELSE (CASE WHEN a.department_id IS NULL THEN d.company_name ELSE c.project_title END) END AS department_name\r\nFROM d_inspect a LEFT JOIN f_department b ON a.department_id = b.department_id,d_construction_project c,f_company d  \r\nWHERE c.project_id = a.project_id AND a.company_id = d.company_id AND a.status <> 3    ${SYNTAX_FILTER_STR}', null, null, null, '安全隐患问题列表详情', 'CHART_GENERAL_SAFE06', '2018-08-28 15:59:20', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' ', null, null, null, 'FEATURE_TYPE.SAFE_PROBLEM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GENERAL_SAFE06', '1', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_2_0_XM', '');
