INSERT INTO `zhgd_chart` VALUES ('272', 'CHART_TYPE.LINE', '安全隐患月度趋势', '', 'SELECT  CASE WHEN a.department_id > 0 THEN b.department_name ELSE (CASE WHEN a.department_id IS NULL THEN d.company_name ELSE c.project_title END) END AS departmentNames,\r\nCASE WHEN a.department_id > 0 THEN \'分公司\' ELSE (CASE WHEN a.department_id IS NULL THEN \'总公司\' ELSE \'项目\' END) END AS projectType,\r\nCOUNT(a.inspect_id) AS numbers,a.department_id,a.company_id,\r\nDATE_FORMAT(a.gmt_create,\'%Y-%m\') AS dataTime,DATE_FORMAT(a.gmt_create,\'%Y-%m\') AS dataTime2\r\nFROM  d_inspect a LEFT JOIN f_department b ON a.department_id = b.department_id,d_construction_project c,f_company d   \r\nWHERE c.project_id = a.project_id AND a.company_id = d.company_id ${SYNTAX_FILTER_STR} GROUP BY DATE_FORMAT(a.gmt_create,\'%Y-%m\'),departmentNames', 'dataTime', '安全问题次数', '', '安全隐患月度趋势', 'CHART_GENERAL_SAFE08', '2018-01-12 09:00:51', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'FEATURE_TYPE.SAFE_PROBLEM', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', null);
