INSERT INTO `zhgd_chart` VALUES ('274', 'CHART_TYPE.SIMPLE_VIEW', '深基坑水位每日详情', '', 'select d.device_sn,d.data_time,isnull(bb.threshold_value,0),\r\nround((b.threshold_value),0) as threshold_value,  --初始高程\r\nround((d.water_value*1000+b.threshold_value)-isnull(bb.threshold_value,0),0) as water_value, -- 本次高程\r\nround((d.water_value*1000)-isnull(bb.threshold_value,0),0) as local_total_value, -- 本次累计高程\r\nround((d.water_value*1000-isnull(bb.threshold_value,0)-d.day_diff_value*1000+b.threshold_value),0) as  last_value, --上次高程\r\nround((d.water_value*1000-isnull(bb.threshold_value,0)-d.day_diff_value*1000),0) as  last_total_value, --上次累计高程\r\n\r\nround((d.day_diff_value*1000),0) as day_diff_value, --本次变化量\r\nconvert(varchar(19),data_time,120) as yesterday, \r\nconvert(varchar(19),dateadd(DD,-1,data_time),120) as today,\r\n (select c.threshold_value from data_Device_Threshold c where \r\n c.threshold_code = \'DEVICE_TYPE.31.SENSOR_NAME\' and d.device_sn= c.DeviceSN  and d.group_code = c.group_code) as ddevice_name\r\n from data_Device_Threshold b ,data_Device_Threshold bb\r\n,data_MaterialStructureWater d  where 1=1 ${SYNTAX_FILTER_STR}', null, null, null, '深基坑水位每日详情', 'CHART_STRUCTURE_WATER05', '2017-12-01 15:01:13', 'lsw', '((\'${data_time}\' =\'\' and convert(varchar(13),data_time,120) =(select  convert(varchar(13),MAX(c.data_time),120) from \r\n dbo.data_MaterialStructureWater c where d.device_sn = c.device_sn))\r\n or (\'${data_time}\' <> \'\' and  convert(varchar(13),data_time,120) = (select top 1 convert(varchar(13),data_time,120) from data_MaterialStructureWater where convert(varchar(13),data_time,120) < \'${data_time}\' order by data_time desc)))\r\nand \r\nbb.threshold_code = \'DEVICE_TYPE.31.POSITION\' and d.device_sn= bb.DeviceSN  and d.group_code = bb.group_code and\r\nb.threshold_code = \'DEVICE_TYPE.31.INITIALVALUE\' and d.device_sn= b.DeviceSN and d.group_code = b.group_code\r\nand  d.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nand ((\'${rate001}\'=\'1\' and d.device_sn in (\'${DeviceSN001}\'))\r\n or (\'${rate001}\'=\'2\' and d.device_sn in (\'${DeviceSN002}\'))\r\n or (\'${rate001}\'= \'3\' and 1=1)\r\nor(\'${rate001}\'= \'\' and 1=1))', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_STRUCTURE_WATER05', '0', '1', '0', '1', 'yyyy-MM-dd HH', 'data_time', '2', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
