INSERT INTO `zhgd_chart` VALUES ('297', 'CHART_TYPE.SIMPLE_VIEW', '奉节县塔机状态', '', 'SELECT A.IsOnline,B.IsntOnline,C.craneTotal,(C.craneTotal-CASE WHEN \'@[GROUP_PARAMS.CRONENUM]\'=\'\' \nTHEN \'0\' ELSE \'@[GROUP_PARAMS.CRONENUM]\' END) AS surplus\nFROM\n(SELECT COUNT(*)AS IsOnline\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc \nON cc.device_sn = aa.device_sn  LEFT JOIN  d_monitor_towercrane_workdata aaa \nON aaa.source_id = cc.source_id AND DATE_FORMAT(aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \n${SYNTAX_FILTER_STR}\n AND cc.online=1) A,\n (SELECT COUNT(*) AS IsntOnline\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc \nON cc.device_sn = aa.device_sn  LEFT JOIN  d_monitor_towercrane_workdata aaa \nON aaa.source_id = cc.source_id AND DATE_FORMAT(aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0 \n${SYNTAX_FILTER_STR}\n AND cc.online=0) B,\n (SELECT COUNT(*)AS craneTotal\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc \nON cc.device_sn = aa.device_sn  LEFT JOIN  d_monitor_towercrane_workdata aaa \nON aaa.source_id = cc.source_id AND DATE_FORMAT(aaa.device_time, \"%Y-%m-%d\" ) = DATE_FORMAT( NOW(), \"%Y-%m-%d\" ), \nd_project_device bb\nWHERE \naa.source_id = bb.source_id AND bb.device_status = 0 AND bb.monitor_type = 0  \n${SYNTAX_FILTER_STR}\n) C ', null, null, null, '奉节县塔机状态', 'CHART_FENGJIE_CRONE', '2018-10-19 13:24:04', 'admin', 'bb.project_id= \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_FENGJIE_CRONE', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('298', 'CHART_TYPE.TABLE', '奉节扬尘表格数据', '', ' select distinct top 30 \r\nCONVERT(CHAR(16), ccc.DeviceTime, 120) as dataTime,\r\ncase when CAST(AVG(ccc.TSP) as decimal(18,2)) is null then 0 else CAST(AVG(ccc.TSP) as decimal(18,2)) end as TSP,\r\nCAST(AVG(ccc.PMTen) as decimal(18,2)) as PMTen,\r\nCAST(AVG(ccc.PMTwoPointFive) as decimal(18,2)) as PMTwoPointFive,\r\n CAST(AVG(ccc.Noise) as decimal(18,2)) as Noise,\r\n CAST(AVG(ccc.Temperature) as decimal(18,2))  as Temperature,\r\n  CAST(AVG(ccc.Humidity) as decimal(18,2)) as Humidity,\r\n  CAST(AVG(ccc.Atmospheric) as decimal(18,2)) as Atmospheric,\r\nCAST(AVG(ccc.WindSpeed) as decimal(18,2)) as WindSpeed,\r\nccc.WindDirection\r\nfrom JC_Project a,Monitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc,\r\nMonitor_Device aa left join Device_Info cc on cc.DeviceSN = aa.DeviceSN \r\nwhere \r\nccc.DeviceID = cc.IID and aa.ProID = a.ProID  \r\n${SYNTAX_FILTER_STR} \r\n group by CONVERT(CHAR(16), ccc.DeviceTime, 120),ccc.WindDirection\r\n order by dataTime desc', null, null, null, '奉节扬尘表格数据', 'CHART_FENGJIE_DUST', '2018-02-08 15:29:52', 'wl', 'aa.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n and ((\'${DeviceSN}\' = \'\' and 1=1) or (\'${DeviceSN}\' <> \'\' and cc.DeviceSN =\'${DeviceSN}\'))', null, null, null, 'FEATURE_TYPE.EVENMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('299', 'CHART_TYPE.TABLE', '奉节人员记录详情', '', ' SELECT (@rowNO := @rowNo+1) AS rowno, a.record_time,a.name,a.card_type,a.group_name,a.dict_value, a.sex FROM\r\n(SELECT DATE_FORMAT(b.record_time, \'%Y-%m-%d %h:%i:%s\') AS record_time,c.name ,g.name AS group_name,s.dict_value,\r\nCASE WHEN b.type = \'1\' THEN \'进场\' \r\n         WHEN b.type = \'2\' THEN \'离场\'\r\n          WHEN b.type = \'3\' THEN \'培训\'  \r\n         ELSE \'其他\' END AS card_type,\r\nCASE WHEN c.sex = \'1\' THEN \'男\' \r\n         WHEN c.sex = \'2\' THEN \'女\' \r\n         ELSE \'其他\' END AS sex,\r\n         a.number\r\nFROM f_device a,d_card_record b,f_worker c,f_group g, s_sys_dict s\r\nWHERE a.device_id = b.device_id AND b.worker_id = c.worker_id\r\nAND a.project_id = c.project_id AND c.group_id= g.group_id AND c.profession_id AND s.dict_id =c.profession_id AND s.dict_key=\'worker_type\'\r\nAND DATE_FORMAT(b.record_time,\'%Y%m%d\')=DATE_FORMAT(CURDATE(),\'%Y%m%d\')\r\n${SYNTAX_FILTER_STR} order by name,b.type)a,(SELECT @rowNO:=0) b ORDER BY a.record_time DESC', null, null, null, '奉节人员记录详情', 'CHART_FENGJIE_DOORGUARD', '2018-03-09 09:12:28', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
