INSERT INTO `zhgd_chart` VALUES ('319', 'CHART_TYPE.TABLE', '混凝土标养室今日预警次数', '', 'select distinct * from (select distinct top 100 percent aa.DeviceSN,\r\nCONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\' as dataTime,\r\nround(MAX(ccc.Temperature),2) as Temperature,round(min(ccc.Humidity),2) as Humidity\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_DustDetectionDevice aa left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere \r\nbb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n ${SYNTAX_FILTER_STR} \r\n group by  aa.DeviceSN, CONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\'\r\n order by dataTime) rrr where rrr.Temperature < @[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\r\nor rrr.Temperature > @[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\r\nor rrr.Humidity < @[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN]', null, null, null, '混凝土标养条件实时数据', 'CHART_CONCREATE_TODAY1', '2018-05-22 14:21:15', 'cx', 'aa.DeviceSN=\'${DeviceSN}\' ', null, null, null, 'FEATURE_TYPE.CONCRETE_BY', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '1', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('320', 'CHART_TYPE.TABLE', '混凝土标养室历史预警详情表', '', 'select SUBSTRING(rrr.dataTime,11,6) as dataTime,rrr.temp,rrr.humi,rrr.DeviceSN from\r\n\r\n(select top 100 percent CONVERT(char(14),DeviceTime,120)+\'00\'  as dataTime,max(Temperature) as temp,MIN(Humidity) as humi,DeviceSN as DeviceSN\r\nfrom data_Daily_Concreate where 1=1   ${SYNTAX_FILTER_STR}\r\ngroup by CONVERT(char(14),DeviceTime,120)+\'00\',DeviceSN ) as rrr where rrr.temp < @[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\r\nor rrr.temp >@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\r\nor rrr.humi < @[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN] order by dataTime', null, null, null, '混凝土标养条件月度每日实时数据', 'CHART_CONCREATE_MONTH1', '2018-04-24 13:12:32', 'cx', 'DeviceSN = \'${DeviceSN}\' and SUBSTRING(CONVERT(char(14),DeviceTime,120)+\'00\',0,11) = \'${dataTime}\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('321', 'CHART_TYPE.LINE', '混凝土标养温度监测', '', 'select distinct \'设备号：\'+bb.DeviceNO as DeviceNO,\r\ncase when \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\'=\'\' then \'0\' \r\n else \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\' end as tempMax,\r\n case when \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\'=\'\' then \'0\' \r\n else \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\' end as tempMin,\r\n case when \'@[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN]\'=\'\' then \'0\' \r\n else \'@[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN]\' end as humidityMin,\r\n   aa.DeviceSN,\r\nCONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\' as dataTime,\r\nround(MAX(ccc.Temperature),2) as Temperature,round(MIN(ccc.Humidity),2) as Humidity\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_DustDetectionDevice aa left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere \r\nbb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n${SYNTAX_FILTER_STR}\r\n group by \'设备号：\'+bb.DeviceNO, aa.DeviceSN, CONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\'\r\n order by dataTime', 'dataTime', '', '', '今日混凝土标养温度检测', 'CHART_CONCREATE_TEMP', '2018-04-24 13:06:42', 'cx', 'aa.DeviceSN=\'${DeviceSN}\' and  a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'FEATURE_TYPE.CONCRETE_BY', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('322', 'CHART_TYPE.LINE', '混凝土标养相对湿度监测', '', 'select distinct \'设备号：\'+bb.DeviceNO as DeviceNO,\r\ncase when \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\'=\'\' then \'0\' \r\n else \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\' end as tempMax,\r\n case when \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\'=\'\' then \'0\' \r\n else \'@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\' end as tempMin,\r\n case when \'@[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN]\'=\'\' then \'0\' \r\n else \'@[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN]\' end as humidityMin,\r\n   aa.DeviceSN,\r\nCONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\' as dataTime,\r\nround(MAX(ccc.Temperature),2) as Temperature,round(MIN(ccc.Humidity),2) as Humidity\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_DustDetectionDevice aa left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere \r\nbb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n${SYNTAX_FILTER_STR} \r\n group by \'设备号：\'+bb.DeviceNO, aa.DeviceSN, CONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\'\r\n order by dataTime', 'dataTime', '', '', '今日混凝土标养相对湿度监测', 'CHART_CONCREATE_HUMI', '2018-04-24 13:08:16', 'cx', ' aa.DeviceSN=\'${DeviceSN}\' and a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'FEATURE_TYPE.CONCRETE_BY', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('323', 'CHART_TYPE.SIMPLE_VIEW', '今日提醒次数', '', 'select COUNT(1) as times,a.DeviceSN as DeviceSN from (select distinct top 100 percent \r\n   aa.DeviceSN,\r\nCONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\' as dataTime,\r\nround(MAX(ccc.Temperature),2) as Temperature,round(MIN(ccc.Humidity),2) as Humidity\r\nfrom JC_Project a,\r\nCraneMonitor.Monitor_DustDetectionDevice aa left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] ccc where \r\nbb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n ${SYNTAX_FILTER_STR} \r\n group by  aa.DeviceSN, CONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\'\r\n order by dataTime)  as a where a.Temperature<@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN] or a.Temperature>@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX] or a.Humidity<@[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN] group by DeviceSN', null, null, null, '混凝土标养条件今日提醒次数', 'CHART_CONCREATE_TODAY', '2018-04-26 09:01:20', 'cx', 'aa.DeviceSN=\'${DeviceSN}\' and a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.CONCRETE_BY', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_CONCREATE_TODAY', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('324', 'CHART_TYPE.COLUMN', '历史告警', 'monthTime', 'select \r\nSUBSTRING(convert(char(14),dd.datatime,120),0,8) as monthTime,\r\nSUBSTRING(dd.datatime,0,11) dataTime,\r\nCOUNT(*) as counts,\r\ndd.DeviceSN as DeviceSN\r\n from\r\n(\r\nselect distinct convert(char(14),DeviceTime,120) as datatime, \r\nMAX(Temperature) as Temperature,\r\nMIN(Humidity) as Humidity,\r\nDeviceSN\r\nfrom data_Daily_Concreate \r\nwhere 1 = 1\r\n${SYNTAX_FILTER_STR}\r\ngroup by convert(char(14),DeviceTime,120),DeviceSN\r\n) dd\r\nwhere \r\ndd.Temperature < @[GROUP_PARAMS_CONCRETE_TEMPERATURE_MIN_MIN]\r\nor dd.Temperature >@[GROUP_PARAMS_CONCRETE_TEMPERATURE_MAX_MAX]\r\nor Humidity < @[GROUP_PARAMS_CONCRETE_HUMIDITY_MIN_MIN]\r\ngroup by SUBSTRING(convert(char(14),dd.datatime,120),0,8),SUBSTRING(dd.datatime,0,11),DeviceSN order by dataTime', 'dataTime', '', '', '混凝土标养月度每日提醒次数', 'CHART_CONCREATE_MONTH', '2018-04-24 13:11:33', 'cx', 'DeviceSN=\'${DeviceSN}\' and ((\'${monthTime}\'=\'\' and 1=1) or (\'${monthTime}\'<>\'\' and convert(char(7),DeviceTime,120) = \'${monthTime}\'))', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '1', 'yyyy-MM', 'monthTime', '1', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('325', 'CHART_TYPE.COLUMN', '今日教育总数', '', 'select A.hours as time,A.sum as Asum, isnull(B.sum1,0) as Bsum\r\nfrom\r\n(select count(*) as sum,CONVERT(char(3), t.update_datetime, 24)+\'00\' as hours \r\nfrom zhgd_test_record t,zhgd_smart_wifi w\r\nwhere t.group_code = w.group_code \r\nand t.test_code = w.test_code \r\nand CONVERT(varchar(10),t.update_datetime, 120) = CONVERT(char(10), GETDATE(), 120)\r\n${SYNTAX_FILTER_STR} \r\ngroup by CONVERT(char(3), t.update_datetime, 24)+\'00\') as A LEFT JOIN\r\n(select count(*) as sum1,t.group_code as group_code ,CONVERT(char(3), t.update_datetime, 24)+\'00\' as hours1 \r\nfrom zhgd_test_record t,zhgd_smart_wifi w\r\nwhere t.group_code = w.group_code \r\nand t.test_code = w.test_code \r\nand t.test_result=\'EXAMINATION_YES_RESULT\' \r\n${SYNTAX_FILTER_STR} \r\nand CONVERT(varchar(10),t.update_datetime, 120) = CONVERT(char(10), GETDATE(), 120)\r\ngroup by CONVERT(char(3), t.update_datetime, 24)+\'00\',t.group_code)as B\r\non A.hours = B.hours1', 'time', '', '', 'WIFI今日教育总数', 'CHART_DAY_COUNT', '2018-04-19 15:12:58', 'lz', 't.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, null, '0', '0', '0', '0', 'yyyy-MM-dd HH', '', '0', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('326', 'CHART_TYPE.LINE', 'WIFI今日在线人数', '', 'select\r\nCONVERT(char(3), t.update_datetime, 24)+\'00\' as hours,count(distinct t.tester_phone) as num\r\nfrom \r\nzhgd_test_record as t, zhgd_smart_wifi as w\r\nwhere t.test_code = w.test_code and t.group_code = w.group_code\r\nand t.update_datetime is  not null \r\nand CONVERT(char(10), t.update_datetime, 120) = CONVERT(char(10), GETDATE(), 120)\r\n${SYNTAX_FILTER_STR} \r\ngroup by CONVERT(char(3), t.update_datetime, 24)+\'00\'', 'hours', '', '', 'WIFI今日在线人数', 'CHART_DAY_ONLINE', '2018-04-13 12:12:59', 'lz', 't.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('327', 'CHART_TYPE.LINE', '月度在线人数', '', 'select\r\nCONVERT(varchar(10),t.update_datetime, 120) as day, count(distinct t.tester_phone) as num\r\nfrom \r\nzhgd_test_record as t, zhgd_smart_wifi as w\r\nwhere t.test_code = w.test_code and t.group_code = w.group_code\r\nand t.update_datetime is  not null\r\nand CONVERT(varchar(7),t.update_datetime, 120) = CONVERT(char(7), GETDATE(), 120)\r\n${SYNTAX_FILTER_STR} \r\ngroup by CONVERT(varchar(10),t.update_datetime, 120)', 'day', '', '', 'WIFI月度在线人数', 'CHART_MONTH_ONLINE', '2018-04-13 12:19:57', 'lz', 't.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '%Y-%m-%d', null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('328', 'CHART_TYPE.JSON', '', '', 'select top 1 \r\nround(ccc.PMTen,0) as PMTen,\r\nround(ccc.PMTwoPointFive,0) as PMTwoPointFive,\r\nround(ccc.Noise,0) as Noise \r\nfrom \r\nMonitor_Device aa, Device_Info cc , \r\nMonitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere 1=1 ${SYNTAX_FILTER_STR} and\r\n cc.DeviceSN = aa.DeviceSN and ccc.DeviceID = cc.IID  and ccc.DeviceTime >= DATEADD(hh,-1,GETDATE())\r\n  order by DeviceTime desc', null, null, null, '天气预报扬尘数据', 'CHART_WEATHER_JSON1', '2018-05-11 08:19:51', 'admin', 'aa.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.EVENMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
INSERT INTO `zhgd_chart` VALUES ('329', 'CHART_TYPE.JSON', '', '', 'SELECT CAST(ccc.windspeed AS DECIMAL(18,1)) AS windspeed\nFROM \nf_device_towercrane aa , f_device cc, \nd_project_device bb,\nd_monitor_towercrane_workdata ccc\nWHERE \ncc.device_sn = aa.device_sn\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id AND ccc.device_time > DATE_SUB(NOW(), INTERVAL 1 HOUR) \n ${SYNTAX_FILTER_STR} \n ORDER BY ccc.device_time DESC LIMIT 1', null, null, null, '天气预报塔机风速数据', 'CHART_WEATHER_JSON2', '2018-10-19 13:50:11', 'admin', 'bb.project_id =  \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', '');
INSERT INTO `zhgd_chart` VALUES ('331', 'CHART_TYPE.CHANGE', '今日承载监测', '', 'select CONVERT(CHAR(16), ccc.DeviceTime, 120) as dataTime,CAST(AVG(ccc.LoadValue) as decimal(18,2)) as loadValue,CAST(AVG(ccc.PercentRate) as decimal(18,2)) as percentRate\r\nfrom  CraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN, \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_ElevatorRunData_@[VAR_MONTH_SUFFIX].Monitor_ElevatorRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID and cc.MonitorType = 4 ${SYNTAX_FILTER_STR}\r\ngroup by CONVERT(CHAR(16), ccc.DeviceTime, 120)', null, null, null, '今日承载监测', 'CHART_MATERIEL_DAY', '2018-04-23 11:14:44', 'wl', 'bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\' and  ((\'${DeviceSN}\' = \'\' and 1=1) or (\'${DeviceSN}\' <> \'\' and cc.DeviceSN =\'${DeviceSN}\'))', null, null, null, 'FEATURE_TYPE.MATERIEL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('332', 'CHART_TYPE.CHANGE', '卸料平台页面每日运行详情', '', 'select date_format(a.device_time,\'%Y-%m-%d %H:%i\') as data,cast(avg(a.load_value) as decimal(18,2))as loadValue,cast(avg(a.rated_load) as decimal(18,2))as rateLoad from zhgd.d_materiel_live a,zhgd_dw.d_device_concrete_materiel b where a.device_sn = b.device_sn ${SYNTAX_FILTER_STR} group by date_format(a.device_time,\'%Y-%m-%d %H:%i\')', null, null, null, '卸料平台页面每日运行详情', 'CHART_MATERIEL_EVERYDAY', '2018-04-23 10:26:44', 'wl', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and date_format(a.device_time,\'%Y-%m-%d\') = \'${dataTime}\' and a.device_sn = \'${DeviceSN}\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', '', null);
INSERT INTO `zhgd_chart` VALUES ('333', 'CHART_TYPE.SIMPLE_VIEW', '', '', 'select data_format(device_time,\'%Y-%m-%d\') as dataTime,alarm,device_sn from zhgd.d_materiel_live WHERE 1=1 ${SYNTAX_FILTER_STR} order by dataTime', null, null, null, '卸料平台月度显示', 'CHART_MATERIEL_MONTH', '2018-04-23 10:52:54', 'lz', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and device_sn = \'${DeviceSN}\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_MATERIEL_MONTH', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('334', 'CHART_TYPE.JSON', '监控平台实时数据', '', 'select distinct cc.IID, cc.DeviceSN, aaa.DeviceTime, aaa.Height,\r\ncase when ISNULL(aaa.Alarm, 0)<> 0 then 2\r\n	else 1 end as status\r\nfrom \r\nCraneMonitor.Monitor_ElevatorDevice aa  \r\nleft join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN  \r\nleft join Monitor_ElevatorRunData_@[VAR_MONTH_SUFFIX].Monitor_ElevatorRunData_@[VAR_DAY_SUFFIX] aaa \r\non aaa.DeviceID = cc.IID and aaa.DeviceTime = (select MAX(aaaa.DeviceTime) from Monitor_ElevatorRunData_@[VAR_MONTH_SUFFIX].Monitor_ElevatorRunData_@[VAR_DAY_SUFFIX] aaaa where aaaa.DeviceID = aaa.DeviceID) , \r\nCraneMonitor.Monitor_Device bb\r\nwhere \r\n aa.DID = bb.DID and bb.DeviceState = 0 and bb.isMonitor = 1 \r\n${SYNTAX_FILTER_STR} \r\norder by aaa.DeviceTime desc, cc.DeviceSN', null, null, null, '监控平台实时数据', 'CHART_MATERIEL_SENCE', '2018-05-11 08:18:30', 'admin', 'bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.MATERIEL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
