INSERT INTO `zhgd_chart` VALUES ('351', 'CHART_TYPE.SIMPLE_VIEW', '项目塔机实时数据表', '', 'SELECT DISTINCT DATE_FORMAT(ccc.device_time,\'%H:%i\') AS dtime,\naa.device_sn,\nROUND(ccc.angle,2) AS angle,\nROUND(ccc.radius,2) AS radius,\nROUND(ccc.height,2) AS hight,\nROUND(ccc.load,2) AS load_,\nROUND(ccc.percent,2) AS `[percent]`,\nROUND(ccc.safeload,2) AS safeload,\nROUND(ccc.windspeed,2) AS windspeed,\nROUND(ccc.fall,2) AS fall\nFROM f_project a,\nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb,\nd_towercrane_live ccc\nWHERE \nbb.project_id = a.project_id\nAND aa.source_id = bb.source_id\nAND ccc.source_id = cc.source_id\n${SYNTAX_FILTER_STR}\nGROUP BY DATE_FORMAT(dtime,\'%H:%i\'),aa.device_sn,angle,radius,hight,load_,`[percent]`,safeload,windspeed,fall\nORDER BY dtime DESC LIMIT 30', null, null, null, '塔机项目轮播数据', 'CHART_GOVT_CRONE31', '2018-10-19 13:24:30', 'admin', 'bb.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GOVT_CRONE31', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_2_1_XM', '');
INSERT INTO `zhgd_chart` VALUES ('352', 'CHART_TYPE.SIMPLE_VIEW', '项目升降机实时数据表', '', 'select distinct Top 30\r\n CONVERT(CHAR(16), ccc.DeviceTime, 120) as dtime,\r\naa.DeviceSN as DeviceSN,\r\nLoadValue,\r\nPercentRate,\r\nNumberOfPeople,\r\nWind,\r\nccc.Height as Height,\r\nSpeed,\r\nAngle,\r\nRelFloor,\r\nCallFloor\r\nfrom BasicPlatform.JC_Project a,\r\nCraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_ElevatorRunData_@[VAR_MONTH_SUFFIX].Monitor_ElevatorRunData_@[VAR_DAY_SUFFIX] ccc\r\nwhere  \r\nbb.MonitorType = 1 and bb.ProjectID = a.ProID\r\nand aa.DID = bb.DID\r\nand ccc.DeviceID = cc.IID\r\n${SYNTAX_FILTER_STR}\r\ngroup by CONVERT(CHAR(16), ccc.DeviceTime, 120),aa.DeviceSN,LoadValue,PercentRate,NumberOfPeople,Wind,ccc.Height,Speed,Angle,RelFloor,CallFloor\r\norder by dtime desc', null, null, null, '升降机项目轮播数据', 'CHART_GOVT_ELEVATOR31', '2018-05-29 19:24:08', 'wl', 'a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.ELEVATORMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GOVT_ELEVATOR31', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_1_XM', null);
INSERT INTO `zhgd_chart` VALUES ('353', 'CHART_TYPE.SIMPLE_VIEW', '项目扬尘实时数据表', '', ' select distinct Top 30\r\nCONVERT(CHAR(16), ccc.DeviceTime, 120) as dataTime,\r\naa.DeviceSN as DeviceSN,\r\nPMTen,\r\nPMTwoPointFive,\r\nround(Noise,2) as Noise,\r\nTemperature,\r\ncase when \'@[GROUP_PARAMS.PM2.5_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PM2.5_MAX]\' end as paramPmTwoPointFiveMAX,\r\ncase when \'@[GROUP_PARAMS.PM2.5_MIN]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PM2.5_MIN]\' end as paramPmTwoPointFiveMIN,\r\ncase when \'@[GROUP_PARAMS.PM10_MAX]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PM10_MAX]\' end as paramPmTenMAX,\r\ncase when \'@[GROUP_PARAMS.PM10_MIN]\'=\'\' then \'0\' else \'@[GROUP_PARAMS.PM10_MIN]\' end as paramPmTenMIN\r\nfrom JC_Project a,Monitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc,\r\nMonitor_Device aa left join Device_Info cc on cc.DeviceSN = aa.DeviceSN \r\nwhere \r\nccc.DeviceID = cc.IID and aa.ProID = a.ProID \r\nand ccc.DeviceID = cc.IID\r\n${SYNTAX_FILTER_STR}\r\n group by  CONVERT(CHAR(16), ccc.DeviceTime, 120),aa.DeviceSN,PMTen,PMTwoPointFive,Noise,Temperature\r\n order by dataTime desc\r\n ', null, null, null, '政府项目轮播扬尘数据表', 'CHART_GOVT_EVENMONITOR31', '2018-04-27 15:12:46', 'qjh', 'a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.EVENMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GOVT_EVENMONITOR31', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', '', null);
INSERT INTO `zhgd_chart` VALUES ('354', 'CHART_TYPE.SIMPLE_VIEW', '项目人员实时数据表', '', ' SELECT (@rowNO := @rowNo+1) AS rowno,groupName,personName,sex,entryDate,offDate\r\nFROM\r\n(SELECT \r\ng.name AS groupName,\r\nf.name AS personName,\r\nCASE WHEN f.sex = \'1\' THEN \'男\' \r\nWHEN f.sex = \'2\' THEN \'女\' \r\nELSE \'其他\' END AS sex,\r\nDATE_FORMAT(d.on_time, \'%Y-%m-%d %H:%i:%s\') AS entryDate,\r\nDATE_FORMAT(d.off_time, \'%Y-%m-%d %H:%i:%s\') AS offDate\r\nFROM f_worker f,d_worker_attendance d,f_group g,`f_cooperator` c\r\nWHERE g.group_id=f.group_id AND f.worker_id = d.worker_id AND f.group_id = d.group_id \r\n AND DATE_FORMAT(d.on_time,\'%Y-%m-%d\') = DATE_FORMAT(NOW(), \'%Y-%m-%d\')\r\n AND c.cooperator_id = g.cooperator_id \r\n  AND g.status = 1\r\n${SYNTAX_FILTER_STR}\r\nORDER BY entryDate,offDate )a,\r\n(SELECT @rowNO:=0) b\r\nLIMIT 0,30', null, null, null, '人员实名制项目轮播数据', 'CHART_GOVT_HUMAN31', '2018-04-24 14:53:24', 'qjh', 'd.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GOVT_HUMAN31', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_1_XM', null);
INSERT INTO `zhgd_chart` VALUES ('355', 'CHART_TYPE.SIMPLE_VIEW', '萤石云视频', '', 'select device_type,\r\ndevice_status as deviceStatus,\r\n  device_name,\r\n  device_sn,\r\nfather_device_sn,\r\n  subsystem_id,\r\n  connection_str,\r\ns.service_url as service_url,\r\n  s.subsystem_key as subsystem_key,\r\n  s.subsystem_id as subsystem_id,\r\n  d.id as id\r\n   from zhgd_device d,zhgd_subsystem s where d.system_id = s.id \r\n ${SYNTAX_FILTER_STR}', null, null, null, '项目轮播萤石云视频播放', 'CHART_GOVT_VIDEO', '2018-05-17 10:58:25', 'qjh', 's.type = \'SUBSYSTEM_TYPE.H5_VIDEO\'\r\n  and d.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_GOVT_VIDEO', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_1_XM', null);
INSERT INTO `zhgd_chart` VALUES ('356', 'CHART_TYPE.TABLE', '混凝土标养室设备状态', '', 'select distinct cc.IID, cc.DeviceSN, aaa.DeviceTime, \r\ncase when ISNULL(cc.IsOnline, 0)<> 0 then 1\r\n	else 0 end as status\r\nfrom \r\nCraneMonitor.Monitor_DustDetectionDevice aa  \r\nleft join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN  \r\nleft join Monitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] aaa \r\non aaa.DeviceID = cc.IID \r\nand aaa.DeviceTime = (select MAX(aaaa.DeviceTime) from Monitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] aaaa \r\nwhere aaaa.DeviceID = aaa.DeviceID) , \r\nCraneMonitor.Monitor_Device bb\r\nwhere \r\n aa.DID = bb.DID and bb.DeviceState = 0 and bb.MonitorType = 2 \r\n${SYNTAX_FILTER_STR} \r\norder by aaa.DeviceTime desc, cc.DeviceSN', null, null, null, '混凝土标养室设备状态', 'CHART_CONCREATE_SENCE', '2018-04-24 13:16:27', 'cx', 'bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\' ', null, null, null, 'FEATURE_TYPE.CONCRETE_BY', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, '0', '0', '0', '0', '', '', '0', null, null, null, null, '', '', null);
INSERT INTO `zhgd_chart` VALUES ('357', 'CHART_TYPE.LINE', '沉降监测', '', 'select convert(varchar(10),data_time,120) as data_time,y_value,device_sn\r\nfrom data_MaterialSettlement \r\nwhere 1=1 ${SYNTAX_FILTER_STR}\r\ngroup by y_value,device_sn,convert(varchar(10),data_time,120),id order by CONVERT(varchar(10),data_time,120),id', 'data_time', '(mm)', '(mm)', '沉降监测', 'SETTLEMENT_MONITOR_RAIN', '2018-05-22 15:06:37', 'wl', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and device_sn in (${DeviceSN})', '0', '', null, '', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, null, '0', '0', '0', '0', '', '', '0', '0', '', '%Y-%m-%d', null, '', 'GROUP_VERSION_2_0_XM', null);
