INSERT INTO `zhgd_chart` VALUES ('368', 'CHART_TYPE.SIMPLE_VIEW', '过磅详情', '', 'select date_format(now(),\'%Y-%m\') as monthTime,date_format(now(),\'%Y-%m-%d\') as dayTime,(select count(*) as num from zhgd_dw.f_concrete_weighbridge where data_time > cast(date_format(now(),\'%Y-%m-%d\') as datetime) and data_time < cast(date_add(date_format(now(),\'%Y-%m-%d\'),interval 1 day) as datetime) ${SYNTAX_FILTER_STR}) as dayNum,(select count(*) as num from zhgd_dw.f_concrete_weighbridge where date_format(data_time,\'%Y-%m\') = date_format(now(),\'%Y-%m\') ${SYNTAX_FILTER_STR}) as monthNum,(select count(*) as num from zhgd_dw.f_concrete_weighbridge where 1=1 ${SYNTAX_FILTER_STR}) as sums', null, null, null, '过磅详情', 'CHART_CONCRETEWEIGH01', '2018-05-17 08:52:08', 'wl', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_CONCRETEWEIGH01', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('369', 'CHART_TYPE.SIMPLE_VIEW', '项目总超负差详情', '', 'select count(*) as num,round(sum(ifnull(planned_net_weight - net_weight - cast(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' as decimal(10,2)),0)),2) as sumWeight from zhgd_dw.f_concrete_weighbridge where 1=1 ${SYNTAX_FILTER_STR} and ifnull(planned_net_weight - net_weight,0) > cast(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' as decimal(10,2))', null, null, null, '项目总超负差详情', 'CHART_CONCRETEWEIGH02', '2018-05-23 19:03:50', 'wl', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, 'CHART_CONCRETEWEIGH02', '0', '0', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_2_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('370', 'CHART_TYPE.TABLE', '物料详情表', '', 'select id,date_format(data_time,\'%Y-%m-%d %H:%i:%s\') as DataTime,supplier,car_license,construction_part,concreate_grade,rough_weight,tare_weight,net_weight,planned_net_weight,case when ifnull(planned_net_weight - net_weight,0) > 0 then round(planned_net_weight - net_weight,2) else 0 end as deviation,case when ifnull(planned_net_weight - net_weight - cast(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' as decimal(10,2)),0) > 0 then round(planned_net_weight - net_weight-cast(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' as decimal(10,2)),2) else 0 end as sumWeight,case when image IS null then 0 else 1 end as images,image from zhgd_dw.f_concrete_weighbridge where 1=1 ${SYNTAX_FILTER_STR}', null, null, null, '物料详情表', 'CHART_CONCRETEWEIGH03', '2018-05-23 19:04:18', 'wl', 'group_code = \'@[VAR_SELECTED_PROJECT_CODE]\' and ((\'${dataTime}\' = \') or (\'${dataTime}\' <> \' and (date_format(data_time,\'%Y-%m-%s\') = \'${dataTime}\' or date_format(data_time,\'%Y-%m\') = \'${dataTime}\'))) and ((\'${difference}\' = \') or (\'${difference}\' = \'1\' and ifnull(planned_net_weight - net_weight,0) > cast(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' as decimal(10,2))))', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD_DATA', null, '', '1', '1', '0', '0', '', '', '0', null, null, null, null, '', 'GROUP_VERSION_1_0', null);
