INSERT INTO `zhgd_chart` VALUES ('412', 'CHART_TYPE.JSON', null, null, ' SELECT SUM(abc.CraneCount) AS countCrane,SUM(abc.yCraneCount) AS ycountCrane FROM \n(SELECT cc.device_sn AS DeviceSN,(SELECT COUNT(1) FROM d_towercrane_work_data da1 WHERE \n	da1.driver_id = cc.source_id AND da1.gmt_create >= DATE_FORMAT(NOW(),\'%Y-%m-%d\'))\n	+ (SELECT COUNT(1) FROM d_towercrane_work_data da2 WHERE da2.id_device = cc.source_id \n	AND da2.gmt_create >= DATE_FORMAT(NOW(),\'%Y-%m-%d\')) AS CraneCount\n	,(SELECT COUNT(1) FROM d_towercrane_work_data da1 WHERE da1.id_device = cc.source_id \n	AND da1.gmt_create >= DATE_SUB(CURDATE(),INTERVAL 1 DAY) \n	AND da1.gmt_create <= DATE_FORMAT(NOW(),\'%Y-%m-%d\'))\n	+ (SELECT COUNT(1) FROM d_towercrane_work_data da2 WHERE da2.id_device = cc.source_id \n	AND da2.gmt_create >= DATE_SUB(CURDATE(),INTERVAL 1 DAY) AND da2.gmt_create <= DATE_FORMAT(NOW(),\'%Y-%m-%d\'))) AS yCraneCount\nFROM d_project_device bb\nLEFT JOIN f_device_towercrane aa ON aa.source_id = bb.source_id\nLEFT JOIN f_device cc ON cc.device_sn = aa.device_sn  \n WHERE 1=1 ${SYNTAX_FILTER_STR} ) abc', null, null, null, '机械设备', 'GDDN_JXSB', '2018-10-19 17:05:03', 'admin', 'bb.project_id= \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_CRANE', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本生产管理模块', 'GROUP_VERSION_3_0_XM', 'GDDN_JXSB_EDIT');
