INSERT INTO `zhgd_chart` VALUES ('417', 'CHART_TYPE.SIMPLE_VIEW', '支撑轴力', '', 'SELECT A.location_no,A.device_name,A.diff,DATE_FORMAT(A.dataTime,\'%Y-%m-%d %H:%i\') AS dataTimes FROM\n(SELECT location_no,ROUND(a.diff,2) AS diff,a.device_name,DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i:%s\') AS dataTime,a.device_id,a.project_id\nFROM f_material_structure_axialforce a\nWHERE 1=1 ${SYNTAX_FILTER_STR} AND a.data_time = (SELECT MAX(cc.data_time) FROM f_material_structure_axialforce cc WHERE cc.project_id = a.project_id) ORDER BY a.data_time DESC) A\nWHERE  A.device_id = \n(SELECT dd.device_id FROM f_material_structure_axialforce dd WHERE dd.location_no = A.location_no AND dd.project_id = A.project_id \nAND DATE_FORMAT(dd.data_time,\'%Y-%m-%d %H:%i\') = DATE_FORMAT(A.dataTime,\'%Y-%m-%d %H:%i\') ORDER BY ABS(dd.diff) DESC LIMIT 1)\nGROUP BY location_no ORDER BY location_no ASC ', null, null, null, '支撑轴力', 'CHART_MATERIAL_STRUCTURE_AXIALFORCE1', '2018-06-19 16:08:40', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_JKJC', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_MATERIAL_STRUCTURE_AXIALFORCE1', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本技术管理模块', 'GROUP_VERSION_3_0_XM', null);
