INSERT INTO `zhgd_chart` VALUES ('484', 'CHART_TYPE.SIMPLE_VIEW', '', '', 'SELECT \r\nCASE WHEN \'@[GROUP_PARAMS.ECECAVATOR]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.ECECAVATOR]\' END AS ececavator,\r\nCASE WHEN \'@[GROUP_PARAMS.DRILL]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.DRILL]\' END AS drill,\r\nCASE WHEN \'@[GROUP_PARAMS.STEEL]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.STEEL]\' END AS steel,\r\nCASE WHEN \'@[GROUP_PARAMS.CRANE]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.CRANE]\' END AS crane', null, null, null, '机械设备', 'CHART_SCGL_JXSB', '2018-07-02 08:30:57', 'cx', '', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_SCGL_JXSB', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本生产管理模块', 'GROUP_VERSION_3_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('485', 'CHART_TYPE.MULTI_AXES', '', '', 'SELECT DISTINCT \'设备号：\'+device_sn AS DeviceSN,CASE WHEN \'@[GROUP_PARAMS.WINDSPEED_MAX]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.WINDSPEED_MAX]\' END AS windspeedMAX,\r\nCONCAT(LEFT(DATE_FORMAT(device_time,\'%H:%i\'),4),\'0\') AS dataTime,\r\nROUND(MAX(height),2) AS hight,\r\nROUND(MAX(windspeed),2) AS windspeed,\r\nROUND(MAX(\'load\'),4) AS \'load\'\r\nFROM d_towercrane_live\r\nWHERE device_time > DATE_FORMAT(NOW(),\'%y-%m-%d\') ${SYNTAX_FILTER_STR} \r\nGROUP BY LEFT(DATE_FORMAT(device_time,\'%H-%i\'),4) ORDER BY device_time', 'dataTime', null, null, '今日数据', 'CHART_TIDB_CRONE111', '2018-07-04 09:33:45', 'cx', 'project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND device_sn =\'${DeviceSN}\' ', '0', '', null, 'SUBSYSTEM_TYPE.TIDB_CRANE', null, null, null, null, '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '0', '0', '0', '0', '', '', '0', null, null, null, null, 'V3.0版本塔机监控', 'GROUP_VERSION_3_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('486', 'CHART_TYPE.JSON', null, null, 'SELECT CAST(ccc.tsp AS DECIMAL(18,2)) AS TSP\nFROM `f_project` a,`d_dusty_live` ccc,\n`d_project_device` aa LEFT JOIN `f_device` cc ON cc.device_sn = aa.device_sn \nWHERE \nccc.source_id = cc.source_id AND aa.project_id = a.project_id AND  ccc.device_time > DATE_FORMAT(NOW(), \"%Y-%m-%d\") \n${SYNTAX_FILTER_STR}\nORDER BY DATE_FORMAT(NOW(), \"%Y-%m-%d\")  DESC LIMIT 1', null, null, null, '空气环境', 'CHART_LSSG_KQHJ_TSP', '2018-10-19 13:52:51', 'admin', 'aa.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.EVENMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本绿色施工模块', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('487', 'CHART_TYPE.3D_COLUMN', '塔机今日告警', '', 'SELECT DISTINCT \'设备号：\'+ aa.device_sn AS DiviceSN,\r\n(SELECT CASE WHEN SUM(CASE WHEN ccc.warn_alarm <> 0 THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN ccc.warn_alarm <> 0 THEN 1 ELSE 0 END) END  FROM d_towercrane_alarm ccc WHERE ccc.device_sn = cc.device_sn\r\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%y-%m-%d\')) AS Alarm0,\r\n(SELECT CASE WHEN SUM(CASE WHEN ccc.notice_alarm <> 0 THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN ccc.notice_alarm <> 0 THEN 1 ELSE 0 END) END  FROM d_towercrane_alarm ccc WHERE ccc.device_sn = cc.device_sn\r\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%y-%m-%d\')) AS Alarm1,\r\n(SELECT CASE WHEN SUM(CASE WHEN ccc.normal_alarm <> 0 THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE SUM(CASE WHEN ccc.normal_alarm <> 0 THEN 1 ELSE 0 END) END  FROM d_towercrane_alarm ccc WHERE ccc.device_sn = cc.device_sn\r\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%y-%m-%d\')) AS Alarm2,\r\n(SELECT CASE WHEN COUNT(ccc.unprofessional_alarm) IS NULL THEN 0 ELSE COUNT(ccc.unprofessional_alarm) END FROM d_towercrane_alarm ccc WHERE ccc.device_sn= cc.device_sn\r\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%y-%m-%d\') AND ccc.unprofessional_alarm IN(3,12,48,64)) AS Alarm3\r\nFROM \r\nf_device_towercrane aa  LEFT JOIN d_project_device cc ON cc.device_sn = aa.device_sn\r\nWHERE 1=1 ${SYNTAX_FILTER_STR}', 'DeviceSN', '报警数量', '', '今日告警', 'CHART_TIDB_CRONE121', '2018-07-04 10:01:49', 'cx', 'aa.device_sn = \'${DeviceSN}\' AND cc.project_id=\'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'SUBSYSTEM_TYPE.TIDB_CRANE', '0', '0', null, '', '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '0', '0', '0', '0', '', '', '0', '0', '', '', null, 'V3.0版本塔机监控', 'GROUP_VERSION_3_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('488', 'CHART_TYPE.AREA', '塔机实时吊重', '', 'SELECT DISTINCT \'设备号：\'+ device_sn,\r\nDATE_FORMAT(device_time,\'%H:%i:%s\') AS dataTime,\r\nROUND(height,2) AS hight,\r\nROUND(\'load\',4) AS \'load\',\r\nROUND(angle,4) AS angle\r\nFROM d_towercrane_live\r\nWHERE device_time > DATE_FORMAT(NOW(),\'%y-%m-%d\') \r\n${SYNTAX_FILTER_STR} \r\n ORDER BY device_time\r\n ', 'dataTime', '', '吨', '实时吊重', 'CHART_TIDB_CRONE141', '2018-07-04 10:05:42', 'cx', 'device_sn = \'${DeviceSN}\' AND project_id=\'@[VAR_SELECTED_PROJECT_CODE]\'', '0', '', null, 'FEATURE_TYPE.CRONEMONITOR', '1', '1', '10', 'and  device_time  > \'@[VAR_REALTIME_XVALUE]\'', '', '', null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '0', '0', '0', '0', '', '', '0', '0', '', '', null, 'V3.0版本塔机监控', 'GROUP_VERSION_3_0_XM', null);
INSERT INTO `zhgd_chart` VALUES ('489', 'CHART_TYPE.JSON', null, null, 'SELECT DISTINCT cc.device_sn, aaa.device_time,\nCASE WHEN IFNULL(aaa.warn_alarm, 0)<> 0 THEN 2 \n	 WHEN IFNULL(aaa.notice_alarm, 0)<> 0 THEN 3 \n	 WHEN IFNULL(aaa.normal_alarm, 0)<> 0 THEN 4 \n	 WHEN aaa.unprofessional_alarm IN(3,12,48,64) THEN 5 \n	 WHEN IFNULL(cc.online, 0)<> 0 THEN 1 \n	ELSE 0 END AS \'status\'\n	FROM \nf_device cc LEFT JOIN d_towercrane_live aaa \nON aaa.device_sn = cc.device_sn, \nd_project_device bb\nWHERE \n aaa.project_id = bb.project_id AND bb.device_status = 0 AND bb.monitor_type = 0 AND aaa.device_time = (SELECT MAX(device_time) FROM d_towercrane_live WHERE aaa.device_sn = device_sn AND device_time > SUBDATE(NOW(),INTERVAL 1 HOUR)) \n${SYNTAX_FILTER_STR}\nORDER BY aaa.device_time DESC, cc.device_sn ', null, null, null, '获取数据中心塔机实时状态用于场景图', 'CHART_TIDB_CRANE_SENCE', '2018-10-19 13:51:10', 'admin', 'bb.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本塔机监控', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('490', 'CHART_TYPE.TABLE', '人员门禁考勤信息', '', 'SELECT (@rowNO := @rowNo+1) AS rowno,c.worker_name,c.gender_code,\r\nc.profession_name,c.group_name,CASE WHEN b.off_time IS NULL THEN \'进场\' ELSE \'出场\' END AS card_type,\r\nIFNULL(DATE_FORMAT(b.on_time, \'%Y-%m-%d %H:%i:%s\'),\'-\') AS record_time,\r\nIFNULL(DATE_FORMAT(b.off_time, \'%Y-%m-%d %H:%i:%s\'),\'-\') AS off_time\r\nFROM d_device_entrance_guard a,f_human_worker_attendance b,f_human_worker c,(SELECT @rowNO:=0) d \r\nWHERE b.worker_id = c.worker_id ${SYNTAX_FILTER_STR}  \r\nAND record_date = DATE_FORMAT(NOW(),\'%Y%m%d\') AND (a.device_sn = b.on_time_device_sn OR a.device_sn = b.off_time_device_sn)\r\nORDER BY b.on_time ASC', null, null, null, '人员门禁考勤信息', 'CHART_HUMAN_GUARD', '2018-08-23 17:47:46', 'sbb', 'b.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.device_sn IN (${DeviceSN})', null, null, null, 'SUBSYSTEM_TYPE.TIDB_HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '1', '0', '0', '0', '', '', '0', null, null, null, null, 'V3.0版本', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('491', 'CHART_TYPE.TABLE', '巡更点记录详情', '', 'SELECT DISTINCT a.device_name,b.position,DATE_FORMAT(a.data_time, \'%Y-%m-%d %H:%i:%s\') AS data_time,a.id,\r\na.member_name,a.note,LENGTH(image_url)-LENGTH(REPLACE(image_url,\',\',\'\'))+1 AS image_url\r\nFROM f_patrol_point_note a,d_device_patrol_point b\r\nWHERE a.device_sn = b.device_sn  ${SYNTAX_FILTER_STR} ORDER BY a.data_time DESC LIMIT 100', null, null, null, '巡更点记录详情', 'CHART_PATROL_RECORD', '2018-08-23 17:46:25', 'sbb', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.device_sn = \'${DeviceSN}\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '1', '1', '0', '0', '', '', '0', null, null, null, null, 'V3.0版本', 'GROUP_VERSION_3_0_XM', '');
