INSERT INTO `zhgd_chart` VALUES ('495', 'CHART_TYPE.SIMPLE_VIEW', '月进度计划', '', 'SELECT  a.activity_name AS name,a.id AS id,a.plan_level AS planLevel,a.duration AS duration,a.predecessors AS predecessors,\nDATE_FORMAT(a.plan_start_time,\'%Y-%m-%d\') AS startTime,DATE_FORMAT(a.plan_end_time,\'%Y-%m-%d\') AS endTime \nFROM zhgd_group_plan a \nWHERE a.plan_type=\'2\' AND DATE_FORMAT(NOW(),\'%Y-%m-%d\') > a.plan_start_time AND DATE_FORMAT(NOW(),\'%Y-%m-%d\') < a.plan_end_time AND a.plan_level > 0  ${SYNTAX_FILTER_STR}\n', null, null, null, '月进度计划', 'CHART_PRODUCTION_MONTH_SCHEDULE', '2018-10-24 14:16:40', 'admin', 'a.group_code = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_PRODUCTION_MONTH_SCHEDULE', '0', '0', '0', '0', '', '', '0', null, null, null, '1', 'V3.0版本生产管理模块', 'GROUP_VERSION_3_0_XM', '');
