INSERT INTO `zhgd_chart` VALUES ('499', 'CHART_TYPE.JSON', null, null, 'SELECT data_time,\r\nIFNULL(plan_num_of_patrol_point,0) AS planPoint,\r\nIFNULL(complete_num_of_patrol_point,0) AS completePoint,\r\nIFNULL(abnormal_num_of_patrol_point,0) AS abnormalPoint,\r\nIFNULL(plan_num_of_patrol_route,0) AS planRoute,\r\nIFNULL(complete_num_of_patrol_route,0) AS completeRoute\r\nFROM a_patrol_log WHERE DATE_FORMAT(data_time,\'%y-%m-%d\') = DATE_FORMAT(NOW( ),\'%y-%m-%d\')\r\n ${SYNTAX_FILTER_STR}\r\nORDER BY data_time DESC LIMIT 1', null, null, null, '安全检查', 'CHART_AQGL_AQJC02', '2018-07-16 09:15:53', 'wl', 'project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_PATROL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本安全管理模块', 'GROUP_VERSION_3_0_XM', 'CHART_AQGL_AQJC02_EDIT');
INSERT INTO `zhgd_chart` VALUES ('500', 'CHART_TYPE.JSON', null, null, ' SELECT DISTINCT top 1 \r\nCONVERT(CHAR(16), ccc.DeviceTime, 120) AS dataTime,\r\nCAST(AVG(ccc.PMTen) AS DECIMAL(18,2)) AS PMTen,\r\nCAST(AVG(ccc.PMTwoPointFive) AS DECIMAL(18,2)) AS PMTwoPointFive,\r\n CAST(AVG(ccc.Noise) AS DECIMAL(18,2)) AS Noise,\r\n CAST(AVG(ccc.Temperature) AS DECIMAL(18,2))  AS Temperature,\r\n CAST(AVG(ccc.Humidity) as decimal(18,2)) as Humidity,\r\nCAST(AVG(ccc.WindSpeed) as decimal(18,2)) as WindSpeed,\r\nccc.WindDirection\r\nFROM JC_Project a,Monitor_DustRunData_@[VAR_MONTH_SUFFIX].Monitor_DustRunData_@[VAR_DAY_SUFFIX] ccc,\r\nMonitor_Device aa LEFT JOIN Device_Info cc ON cc.DeviceSN = aa.DeviceSN \r\nWHERE \r\nccc.DeviceID = cc.IID AND aa.ProID = a.ProID AND ccc.DeviceTime > CONVERT(CHAR(10), getdate(), 120)  \r\n  ${SYNTAX_FILTER_STR} \r\n GROUP BY CONVERT(CHAR(16), ccc.DeviceTime, 120),ccc.WindDirection\r\n ORDER BY dataTime desc', null, null, null, '环境监测', 'CHART_DUST_DAY', '2018-07-17 10:57:08', 'wl', ' aa.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.EVENMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本绿色施工模块_定制', 'GROUP_VERSION_3_0_XM', null);
