INSERT INTO `zhgd_chart` VALUES ('504', 'CHART_TYPE.JSON', null, null, 'SELECT a.worker,a.total_num,a.specialWorker,a.femaleWorker,a.oldWorker,a.minorityWorker,a.total_num AS totalWorker\nFROM \n(SELECT SUM(aa.num_of_attendances) AS worker,SUM(aa.total_num) AS total_num,SUM(aa.num_of_attendances_special_group) AS specialWorker,\nSUM(aa.num_of_attendances_female) AS femaleWorker,SUM(aa.num_of_attendances_old) AS oldWorker,SUM(aa.num_of_attendances_minority) AS minorityWorker\nFROM a_human_attendance aa,\n(SELECT MAX(data_time) AS dataTime FROM a_human_attendance WHERE project_id = \'@[VAR_SELECTED_PROJECT_CODE]\') bb\nWHERE aa.data_time > DATE_FORMAT(NOW(),\'%y-%m-%d\') ${SYNTAX_FILTER_STR}  AND aa.data_time = bb.dataTime) a', null, null, null, '劳务工人', 'CHART_AQGL_LWGR_RY', '2018-07-27 16:53:33', 'wl', 'aa.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本安全管理模块-劳务工人面板', 'GROUP_VERSION_3_0_XM', 'CHART_AQGL_LWGR_EDIT');
INSERT INTO `zhgd_chart` VALUES ('505', 'CHART_TYPE.JSON', null, null, ' SELECT ee.DeviceName AS DeviceName,ee.DeviceSN AS DeviceSN,DATE_FORMAT( ee.upTime, \"%H:%i\" ) AS upTime,IID FROM\n(SELECT cc.device_sn AS DeviceSN,cc.source_id AS IID,cc.up_time AS upTime,bb.device_sn AS DeviceName\nFROM d_project_device bb\nLEFT JOIN f_device_towercrane aa ON aa.source_id = bb.source_id\nLEFT JOIN f_device cc ON cc.device_sn = aa.device_sn\n WHERE 1 = 1 AND bb.project_id=\'791E6B46-7FCA-E711-892E-7845C4ECC6E2\' )AS ee\n WHERE ee.upTime >= DATE_FORMAT(NOW(), \"%Y-%m-%d\" ) ORDER BY UpTime LIMIT 1', null, null, null, '塔机开机时间', 'CHART_AQGL_LWGR_TJ', '2018-10-19 13:19:03', 'admin', 'bb.project_id= \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'FEATURE_TYPE.CRONEMONITOR', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, null, null, null, null, 'V3.0版本安全管理模块-劳务工人面板', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('506', 'CHART_TYPE.SIMPLE_VIEW', '', '', 'SELECT dayNum.num AS dayNum,monthNum.num AS monthNum,sums.num AS sums FROM \r\n(SELECT COUNT(*) AS num FROM f_concrete_weighbridge\r\nWHERE DATE_FORMAT(data_time,\'%Y-%m-%d\')=DATE_FORMAT(NOW(),\'%Y-%m-%d\') ${SYNTAX_FILTER_STR}) AS dayNum,\r\n(SELECT COUNT(*) AS num FROM f_concrete_weighbridge \r\nWHERE DATE_FORMAT(data_time,\'%Y-%m\')=DATE_FORMAT(NOW(),\'%Y-%m\') ${SYNTAX_FILTER_STR}) AS monthNum,\r\n(SELECT COUNT(*) AS num FROM f_concrete_weighbridge WHERE 1=1  ${SYNTAX_FILTER_STR}) AS sums', null, null, null, '过磅详情', 'CHART_CONCREATE_WEIGHT01', '2018-08-22 08:35:28', 'wl', 'project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' ', null, null, null, 'SUBSYSTEM_TYPE.TIDB_CONCRETE_MATERIAL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_CONCREATE_WEIGHT01', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本电子地磅', 'GROUP_VERSION_3_0_XM', '');
