INSERT INTO `zhgd_chart` VALUES ('508', 'CHART_TYPE.SIMPLE_VIEW', '', '', 'SELECT DATE_FORMAT(data_time,\'%Y年%m月\') AS data_time,COUNT(*) AS num,ROUND(SUM(IFNULL(planned_net_weight - net_weight - CAST(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' AS DECIMAL(10,2)),0)),2) AS sumWeight,\r\nDATE_FORMAT(data_time,\'%Y年\') AS years\r\nFROM f_concrete_weighbridge WHERE IFNULL(CONVERT(planned_net_weight - net_weight,DECIMAL(10,2)),0) > CAST(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' AS DECIMAL(10,2)) ${SYNTAX_FILTER_STR} GROUP BY DATE_FORMAT(data_time,\'%Y年%m月\') ORDER BY years DESC,data_time\r\n', null, null, null, '年度超负差', 'CHART_CONCREATE_WEIGHT03', '2018-08-22 08:35:46', 'wl', 'project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_CONCRETE_MATERIAL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_CONCREATE_WEIGHT03', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本电子地磅', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('509', 'CHART_TYPE.SIMPLE_VIEW', '监测概况', '', 'select\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.1]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.1]\' END AS GZM1,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.2]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.2]\' END AS GZM2,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.3]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.3]\' END AS GZM3,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.4]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.4]\' END AS GZM4,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.5]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.5]\' END AS GZM5,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.6]\'=\'\' THEN \'-\' ELSE \'@[GROUP_PARAMS.GZM.6]\' END AS GZM6,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.7]\'=\'\' THEN \'-\' ELSE \'@[GROUP_PARAMS.GZM.7]\' END AS GZM7,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.8]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.8]\' END AS GZM8,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.9]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.9]\' END AS GZM9,\r\nCASE WHEN \'@[GROUP_PARAMS.GZM.10]\'=\'\' THEN \'0\' ELSE \'@[GROUP_PARAMS.GZM.10]\' END AS GZM10', null, null, null, '监测概况', 'CHART_GZM_JCGK', '2018-08-29 10:47:30', 'wl', '', null, null, null, '', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JNDI.ZHGD', null, 'CHART_GZM_JCGK', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '高支模页面', 'GROUP_VERSION_3_1_XM,GROUP_VERSION_3_0_XM', 'CHART_GZM_JCGK_EDIT');
INSERT INTO `zhgd_chart` VALUES ('510', 'CHART_TYPE.SIMPLE_VIEW', '报警信息', '', 'SELECT COUNT(*) AS alertNum1 FROM f_gzm_gjzl a \r\nWHERE  a.value > 0 AND a.value > a.alert_value AND a.value < a.alarm_value\r\n${SYNTAX_FILTER_STR} GROUP BY DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')', null, null, null, '报警信息', 'CHART_GZM_BJXX', '2018-09-10 10:02:17', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_GZM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GZM_BJXX', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '高支模页面', 'GROUP_VERSION_3_1_XM,GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('511', 'CHART_TYPE.SIMPLE_VIEW', '报警详情', '', 'SELECT * FROM\n(SELECT a.device_name,ROUND(a.value,1) AS VALUE,ROUND(a.alert_value,1) AS alert_value,ROUND(a.alarm_value,1) AS alarm_value,DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')AS data_time,3 AS rownum FROM f_gzm_gjzl a \nWHERE a.value > 0 AND a.value > a.alarm_value AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' \nUNION ALL\nSELECT a.device_name,ROUND(a.value,1) AS VALUE,ROUND(a.alert_value,1) AS alert_value,ROUND(a.alarm_value,1) AS alarm_value,DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')AS data_time,4 AS rownum FROM f_gzm_hgqj a \nWHERE a.value > 0 AND a.value > a.alarm_value AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\nUNION ALL\nSELECT a.device_name,ROUND(a.value,1) AS VALUE,ROUND(a.alert_value,1) AS alert_value,ROUND(a.alarm_value,1) AS alarm_value,DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')AS data_time,2 AS rownum FROM f_gzm_mbcj a \nWHERE a.value > 0 AND a.value > a.alarm_value AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\nUNION ALL\nSELECT a.device_name,ROUND(a.value,1) AS VALUE,ROUND(a.alert_value,1) AS alert_value,ROUND(a.alarm_value,1) AS alarm_value,DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')AS data_time,1 AS rownum FROM f_gzm_zjspwy a \nWHERE a.value > 0 AND a.value > a.alarm_value AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\n) A WHERE 1=1 ${SYNTAX_FILTER_STR} ORDER BY A.data_time DESC', null, null, null, '报警详情', 'CHART_GZM_BJXQ', '2018-10-16 18:12:02', 'admin', 'A.rownum = ${rownum} ', null, null, null, 'SUBSYSTEM_TYPE.TIDB_GZM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GZM_BJXQ', '1', '0', '0', '0', '', '', '0', null, null, null, '0', '高支模页面', 'GROUP_VERSION_3_0_XM,GROUP_VERSION_3_1_XM', '');
INSERT INTO `zhgd_chart` VALUES ('512', 'CHART_TYPE.SIMPLE_VIEW', '沉降监测详情', '', 'SELECT DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')AS data_time,ROUND(a.value,1) AS value,ROUND(a.alert_value,1) AS alert_value,ROUND(a.alarm_value,1) AS alarm_value,\r\nCASE WHEN a.value > a.alert_value AND a.value < a.alarm_value THEN \'预警\' ELSE CASE WHEN a.value > a.alarm_value THEN \'报警\' ELSE \'正常\' END END AS status,\r\na.device_id \r\nFROM f_gzm_mbcj a\r\nWHERE 1=1 ${SYNTAX_FILTER_STR} GROUP BY DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\') ORDER BY a.data_time DESC LIMIT 15', null, null, null, '沉降监测详情', 'CHART_GZM_CJJC', '2018-09-10 13:53:59', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.device_id = \'${deviceSN}\'\r\nAND ((\'${isfilter}\' <> \'\' AND a.data_time > (SELECT MAX(DATE_FORMAT(data_time,\"%Y-%m-%d\")) FROM f_gzm_mbcj WHERE project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND device_id = \'${deviceSN}\')) OR (\'${isfilter}\' = \'\' AND 1=1))\r\nAND ((\'${dataTime}\' <> \'\' AND DATE_FORMAT(a.data_time,\"%Y-%m-%d\") = \'${dataTime}\') OR (\'${dataTime}\' = \'\' AND 1=1))\r\nAND ((\'${status}\' <> \'\' AND a.status = \'${status}\') OR (\'${status}\' = \'\' AND 1=1))', null, null, null, 'SUBSYSTEM_TYPE.TIDB_GZM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GZM_CJJC', '0', '0', '0', '0', '', '', '0', null, null, null, '0', '高支模页面', 'GROUP_VERSION_3_1_XM,GROUP_VERSION_3_0_XM', '');
