INSERT INTO `zhgd_chart` VALUES ('526', 'CHART_TYPE.SIMPLE_VIEW', '沉降监测详情列表', '', 'SELECT DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\')AS data_time,ROUND(a.value,1) AS value,ROUND(a.alert_value,1) AS alert_value,ROUND(a.alarm_value,1) AS alarm_value,CASE WHEN a.value > a.alert_value AND a.value < a.alarm_value THEN \'预警\' ELSE CASE WHEN a.value > a.alarm_value THEN \'报警\' ELSE \'正常\' END END AS status,a.device_id,a.device_name  \r\nFROM f_gzm_mbcj a\r\nWHERE 1=1 ${SYNTAX_FILTER_STR} GROUP BY DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i\') ORDER BY a.data_time DESC', null, null, null, '沉降监测详情列表', 'CHART_GZM_CJJC03', '2018-09-10 13:52:25', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.device_id = \'${deviceSN}\'\r\nAND ((\'${isfilter}\' <> \'\' AND a.data_time > (SELECT MAX(DATE_FORMAT(data_time,\"%Y-%m-%d\")) FROM f_gzm_mbcj WHERE project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND device_id = \'${deviceSN}\')) OR (\'${isfilter}\' = \'\' AND 1=1))\r\nAND ((\'${dataTime}\' <> \'\' AND DATE_FORMAT(a.data_time,\"%Y-%m-%d\") = \'${dataTime}\') OR (\'${dataTime}\' = \'\' AND 1=1))\r\nAND ((\'${status}\' <> \'\' AND a.status = \'${status}\') OR (\'${status}\' = \'\' AND 1=1))', null, null, null, 'SUBSYSTEM_TYPE.TIDB_GZM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GZM_CJJC03', '1', '0', '0', '0', '', '', '0', null, null, null, '0', '高支模页面', 'GROUP_VERSION_3_1_XM,GROUP_VERSION_3_0_XM', '');
