INSERT INTO `zhgd_chart` VALUES ('532', 'CHART_TYPE.TABLE', '物料详情表', '', 'SELECT DATE_FORMAT(a.data_time,\'%Y-%m-%d %H:%i:%s\') AS dataTime,a.supplier,a.car_license,a.construction_part,a.concreate_grade,a.rough_weight,a.tare_weight,\r\na.net_weight,a.planned_net_weight,\r\nROUND(IFNULL(a.planned_net_weight - a.net_weight,0),2) AS offset_value,\r\nROUND(IFNULL(a.planned_net_weight - a.net_weight - CAST(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' AS DECIMAL(10,2)),0),2) AS sumWeight,\r\nCASE WHEN a.image IS NULL THEN 0 ELSE 1 END AS image\r\nFROM f_concrete_weighbridge a \r\nWHERE 1=1 ${SYNTAX_FILTER_STR} order by a.data_time desc ', null, null, null, '物料详情表', 'CHART_CONCREATE_WEIGHT04', '2018-08-23 11:05:25', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND\r\n( \r\n(${source} = 1 AND ( (${typea} = 1 AND DATE_FORMAT(a.data_time,\'%Y-%m-%d\') = DATE_FORMAT(NOW(),\'%Y-%m-%d\')) OR (${typea} = 2 AND DATE_FORMAT(a.data_time,\'%Y-%m\') = DATE_FORMAT(NOW(),\'%Y-%m\')) OR (${typea} = 3 AND 1=1) ))\r\nOR\r\n(${source} = 2 AND IFNULL(CONVERT(planned_net_weight - net_weight,DECIMAL(10,2)),0) > CAST(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' AS DECIMAL(10,2)))\r\nOR\r\n(${source} = 3 AND DATE_FORMAT(data_time,\'%Y年%m月\')=\'${dataTime}\' AND IFNULL(CONVERT(planned_net_weight - net_weight,DECIMAL(10,2)),0) > CAST(\'@[GROUP_PARAMS.CONCRETEWEIGH_MAX]\' AS DECIMAL(10,2)))\r\nOR\r\n(${source} = 4 AND DATE_FORMAT(data_time,\'%Y-%m-%d\')=\'${times}\')\r\nOR\r\n(${source} = 5 AND ( (\'${is_offset}\' = \'1\' AND planned_net_weight <> net_weight) OR (\'${is_offset}\' = \'0\' AND 1=1) ))\r\n)', null, null, null, 'SUBSYSTEM_TYPE.TIDB_CONCRETE_MATERIAL', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '1', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('533', 'CHART_TYPE.TABLE', '人员考勤表', '', 'SELECT f.worker_name,f.gender_code,f.group_name,DATE_FORMAT(a.off_time,\'%Y-%m-%d %H:%i:%s\') AS offTime,DATE_FORMAT(a.on_time,\'%Y-%m-%d %H:%i:%s\') AS onTime \nFROM  f_human_worker f,f_human_worker_attendance a\nWHERE f.project_id = a.project_id AND f.worker_id = a.worker_id ${SYNTAX_FILTER_STR}\n order by  a.on_time desc', null, null, null, '人员考勤表', 'CHART_RESOURCESINTO_06', '2018-08-23 11:20:25', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND DATE_FORMAT(a.on_time,\'%Y-%m-%d\') = \'${dataTime}\' \nAND\n( \n(${isNight} = 1 AND (DATE_FORMAT(a.on_time,\'%k\') >= 18 OR DATE_FORMAT(a.on_time,\'%k\') < 6))\nOR\n(${isNight} = 0 AND DATE_FORMAT(a.on_time,\'%k\') < 18 AND DATE_FORMAT(a.on_time,\'%k\') >= 6)\n)', null, null, null, 'SUBSYSTEM_TYPE.TIDB_HUMAN', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_TEMPLATE_3.0', '1', '0', '0', '0', '', '', '0', null, null, null, null, 'V3.0版本', 'GROUP_VERSION_3_0_XM', '');
