INSERT INTO `zhgd_chart` VALUES ('539', 'CHART_TYPE.SIMPLE_VIEW', '监测设备', '', 'SELECT A.num,A.device_sn FROM \r\n(\r\nSELECT COUNT(device_sn) AS num,device_sn,IFNULL(device_type,1201) AS device_type FROM d_device_high_formwork WHERE device_type = 1201 ${SYNTAX_FILTER_STR}\r\nUNION ALL\r\nSELECT COUNT(device_sn) AS num,device_sn,IFNULL(device_type,1202) AS device_type FROM d_device_high_formwork WHERE device_type = 1202 ${SYNTAX_FILTER_STR}\r\nUNION ALL\r\nSELECT COUNT(device_sn) AS num,device_sn,IFNULL(device_type,1203) AS device_type FROM d_device_high_formwork WHERE device_type = 1203 ${SYNTAX_FILTER_STR}\r\nUNION ALL\r\nSELECT COUNT(device_sn) AS num,device_sn,IFNULL(device_type,1204) AS device_type FROM d_device_high_formwork WHERE device_type = 1204 ${SYNTAX_FILTER_STR}\r\n) A ORDER BY device_type ASC ', null, null, null, '监测设备', 'CHART_GZM_JCSB', '2018-09-11 14:25:29', 'wl', 'project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_GZM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GZM_JCSB', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本技术管理模块', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('540', 'CHART_TYPE.SIMPLE_VIEW', '历史报警', '', 'SELECT A.alarmNum,A.typea FROM \r\n(SELECT COUNT(*) AS alarmNum,1 AS typea FROM f_gzm_gjzl a WHERE a.value > 0 AND a.value > a.alarm_value ${SYNTAX_FILTER_STR} \r\nUNION ALL\r\nSELECT COUNT(*) AS alarmNum,2 AS typea FROM f_gzm_hgqj b WHERE b.value > 0 AND b.value > b.alarm_value AND b.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' \r\nUNION ALL\r\nSELECT COUNT(*) AS alarmNum,3 AS typea FROM f_gzm_mbcj c WHERE c.value > 0 AND c.value > c.alarm_value AND c.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' \r\nUNION ALL\r\nSELECT COUNT(*) AS alarmNum,4 AS typea FROM f_gzm_zjspwy d WHERE d.value > 0 AND d.value > d.alarm_value AND d.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' \r\n) A ORDER BY A.alarmNum DESC ', null, null, null, '历史报警', 'CHART_GZM_LSBJ02', '2018-09-10 17:13:47', 'wl', 'a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'', null, null, null, 'SUBSYSTEM_TYPE.TIDB_GZM', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_GZM_LSBJ02', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本技术管理模块', 'GROUP_VERSION_3_0_XM', 'CHART_GZM_LSBJ02_EDIT');
