INSERT INTO `zhgd_chart` VALUES ('579', 'CHART_TYPE.SIMPLE_VIEW', '', '', 'SELECT\n	metervalue,\n	ymetervalue\nFROM\n	(\n		SELECT\n			COUNT(meter_daily_diff_value) AS tsum,\n			IFNULL(meter_daily_diff_value,0) AS metervalue\n		FROM\n			f_electricity\n		WHERE\n			project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\n		AND is_master_meter = \'1\'\n		AND DATE_FORMAT(meter_datetime, \'%y-%m-%d\') = DATE_FORMAT(NOW(), \'%y-%m-%d\')\n		ORDER BY\n			meter_datetime DESC\n		LIMIT 1\n	) AS a,\n	(\n		SELECT\n			COUNT(meter_daily_diff_value) AS ynum,\n			IFNULL(meter_daily_diff_value,0) AS ymetervalue\n		FROM\n			f_electricity\n		WHERE\n			project_id = \'@[VAR_SELECTED_PROJECT_CODE]\'\n		AND is_master_meter = \'1\'\n		AND DATE_FORMAT(meter_datetime, \'%y-%m-%d\') = DATE_SUB(CURDATE(), INTERVAL 1 DAY)\n		ORDER BY\n			meter_datetime DESC\n		LIMIT 1\n	) AS b;', null, null, null, '机械设备', 'GDDN_JXSB_02', '2018-10-25 21:36:33', 'admin', '', null, null, null, 'SUBSYSTEM_TYPE.TIDB_METER', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'GDDN_JXSB_02', '0', '0', '0', '0', '', '', '0', null, null, null, '0', 'V3.0版本生产管理模块', 'GROUP_VERSION_3_0_XM', '');
INSERT INTO `zhgd_chart` VALUES ('580', 'CHART_TYPE.SIMPLE_VIEW', '车辆识别', '', 'select  car_number, image_url,\nDATE_FORMAT(grab_time,\"%Y-%m-%d %H:%i\") as grab_time,\ncase io_type\nwhen \"In\" then \"进场\"\nwhen \"Out\" then \"出场\"\nELSE \"\" END as io_type\nfrom f_vehicle_traffic where 1=1 ${SYNTAX_FILTER_STR} ORDER BY grab_time desc', null, null, null, '车辆识别列表', 'CHART_CLSB_CLSB', '2018-10-25 11:35:59', 'admin', '((\'${endTime}\' <> \'\' AND DATE_FORMAT(\'${endTime}\',\"%Y-%m-%d %H:%i\") >= DATE_FORMAT(grab_time,\"%Y-%m-%d %H:%i\")) OR (\'${endTime}\' = \'\' AND 1=1))\nAND  ((\'${startTime}\' <> \'\' AND DATE_FORMAT(grab_time,\"%Y-%m-%d %H:%i\")>= DATE_FORMAT(\'${startTime}\',\"%Y-%m-%d %H:%i\")) OR (\'${startTime}\' = \'\' AND 1=1))\n', null, null, null, 'SUBSYSTEM_TYPE.TIDB_CLSB', null, null, null, null, null, null, null, null, 'DATASOURCE_TYPE.JDBC', null, 'CHART_CLSB_CLSB', '1', '0', '0', '0', '', '', '0', null, null, null, '0', '', 'GROUP_VERSION_3_0_XM', '');
