INSERT INTO `zhgd_chart_addon` VALUES ('16', '184', '人员进场', '2017-10-11 14:06:28', 'lsw', '', 'FEATURE_TYPE.HUMAN', 'SELECT a.workernum,b.enterworkernum FROM\r\n(SELECT COUNT(DISTINCT w.group_id) AS cooperatornum,COUNT(*) AS workernum\r\nFROM `d_worker_attendance` w\r\nWHERE  1=1\r\nAND w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nAND DATE_FORMAT(w.record_date,\'%Y-%m-%d\') =DATE_FORMAT(CURDATE(),\'%Y-%m-%d\')\r\n) a,\r\n\r\n(SELECT COUNT(DISTINCT w.cooperator_id) AS cooperatornum,COUNT(*) AS enterworkernum\r\nFROM `d_worker_attendance` w\r\nWHERE  1=1\r\nAND w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nAND DATE_FORMAT(w.record_date,\'%Y-%m-%d\') =DATE_FORMAT(CURDATE(),\'%Y-%m-%d\')\r\nAND DATE_FORMAT(w.off_time,\'%Y-%m-%d\') IS NULL) b', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('20', '135', 'board01', '2017-06-13 09:32:19', 'dxf', '', 'FEATURE_TYPE.PROJECT_PATROL', 'select count(distinct a.point_id) as exception from d_patrol_point a where a.status=\'2\'  \r\nand a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('29', '125', 'PatrolPoint', '2017-10-19 13:30:38', 'wl', '', 'FEATURE_TYPE.PROJECT_PATROL', 'SELECT a.achievePoint,b.totalPatrolPoint,\r\nCASE   WHEN  b.totalPatrolPoint=0 THEN \'0\' ELSE CONCAT(ROUND((a.achievePoint*1.0 /b.totalPatrolPoint) *100,1),\'%\') END AS PatrolPoint \r\nFROM\r\n(SELECT COUNT(*) AS totalPatrolPoint\r\n FROM `d_patrol_point` a WHERE 1=1  AND a.status = 1 \r\n AND a.`project_id` =  \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n) b,\r\n( SELECT COUNT(*) AS achievePoint\r\n FROM d_patrol_point_log b WHERE (b.times<=b.real_times OR (b.real_times>0 AND b.times IS NULL)) AND \r\nDATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURDATE(),\'%Y%m%d\')   AND \r\nb.`project_id` =\'@[VAR_SELECTED_PROJECT_CODE]\')  a', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('32', '125', 'wayPoint', '2017-06-23 14:44:43', 'lsw', '', 'FEATURE_TYPE.PROJECT_PATROL', 'SELECT A.wayachieve,B.totalway,\r\nCASE  WHEN B.totalway=0 THEN \'未知\' ELSE CONCAT(round((A.wayachieve*1.0 /B.totalway) *100,1),\'%\') END AS \'wayPoint\'\r\nFROM\r\n(select count(distinct a.`route_id`) as totalway\r\n from \r\n`d_patrol_route` a where 1=1 \r\nand a.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\') B,\r\n(SELECT COUNT(*) AS wayachieve\r\n FROM \r\n`d_patrol_route_log` b \r\nWHERE 1=1 AND b.success=1\r\nAND DATE_FORMAT(b.commit_time,\'%Y%m%d\') = DATE_FORMAT(CURDATE(),\'%Y%m%d\') \r\nAND b.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\') A', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('36', '135', 'board02', '2017-06-16 16:53:02', 'dxf', '', 'FEATURE_TYPE.CRONEMONITOR', 'select SUM(a.Alarm2) as num1 from\r\n(select distinct bb.ProjectID,\r\n(select sum(case when ccc.Alarm2 <> 0 then 1 else 0 end)  from DataCenter.Device_TowerCraneAlarm ccc where ccc.IID = cc.IID\r\nand ccc.startTime  > convert(varchar(10),getdate(),120)) as Alarm2\r\n\r\nfrom \r\n\r\nCraneMonitor.Monitor_TowerCraneDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb\r\nwhere\r\naa.DID = bb.DID\r\nand bb.ProjectID= \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n) a', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('37', '135', 'board03', '2017-06-23 09:04:05', 'lsw', '', 'FEATURE_TYPE.HUMAN', 'SELECT a.human,b.personnel FROM\r\n(SELECT COUNT(DISTINCT w.group_id) AS cooperatornum,COUNT(*) AS human\r\nFROM `d_worker_attendance` w\r\nWHERE  1=1\r\nAND w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nAND DATE_FORMAT(w.record_date,\'%Y-%m-%d\') =DATE_FORMAT(CURDATE(),\'%Y-%m-%d\')\r\n) a,\r\n\r\n(SELECT COUNT(DISTINCT w.cooperator_id) AS cooperatornum,COUNT(*) AS personnel\r\nFROM `d_worker_attendance` w\r\nWHERE  1=1\r\nAND w.`project_id` = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\nAND DATE_FORMAT(w.record_date,\'%Y-%m-%d\') =DATE_FORMAT(CURDATE(),\'%Y-%m-%d\')\r\nAND DATE_FORMAT(w.off_time,\'%Y-%m-%d\') IS NULL) b', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('38', '135', 'board04', '2017-06-16 16:36:11', 'dxf', '', 'FEATURE_TYPE.HUMAN', 'select count(*) as noeducation from f_worker w  \r\nwhere w.train_count=\'0\'\r\nand w.status=\'1\'\r\nand w.`project_id` = \r\n\'@[VAR_SELECTED_PROJECT_CODE]\'', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('39', '135', 'board05', '2017-06-16 16:38:40', 'dxf', '', 'FEATURE_TYPE.ELEVATORMONITOR', 'select SUM(a.Alarm0) as breaknum from\r\n (select distinct bb.ProjectID,\r\n(select sum(case when ccc.EarlyAlarm <> 0 then 1 else 0 end) from DataCenter.Device_ElevatorAlarm ccc where  ccc.IID = cc.IID\r\nand ccc.startTime  >=  convert(varchar(10),getdate(),120) ) as Alarm0\r\n\r\nfrom\r\n \r\n CraneMonitor.Monitor_ElevatorDevice aa  left join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb\r\nwhere aa.DID = bb.DID\r\n\r\nand bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n) a\r\n\r\n', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('40', '137', 'num1', '2018-10-19 14:13:07', 'admin', '', 'FEATURE_TYPE.CRONEMONITOR', ' SELECT  SUM(a.alarm1) AS remind,SUM(a.alarm2) AS callp,SUM(a.alarm3) AS Brek FROM \n(SELECT DISTINCT \'设备号：\'+bb.device_no AS DeviceNO, aa.device_sn AS DeviceSN,\n(SELECT SUM(CASE WHEN ccc.alarm0 <> 0 THEN 1 ELSE 0 END)  FROM d_towercrane_alarm ccc WHERE ccc.source_id = cc.source_id\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%Y-%m-%d\')) AS Alarm0,\n(SELECT SUM(CASE WHEN ccc.alarm1 <> 0 THEN 1 ELSE 0 END)  FROM d_towercrane_alarm ccc WHERE ccc.source_id = cc.source_id\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%Y-%m-%d\')) AS Alarm1,\n(SELECT SUM(CASE WHEN ccc.alarm2 <> 0 THEN 1 ELSE 0 END)  FROM d_towercrane_alarm ccc WHERE ccc.source_id = cc.source_id\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%Y-%m-%d\')) AS Alarm2,\n(SELECT COUNT(ccc.alarm3)  FROM d_towercrane_alarm ccc WHERE ccc.source_id = cc.source_id\nAND ccc.start_time  > DATE_FORMAT(NOW(),\'%Y-%m-%d\') AND ccc.alarm3 IN(3,12,48,64)) AS Alarm3\nFROM \nf_device_towercrane aa  LEFT JOIN f_device cc ON cc.device_sn = aa.device_sn , \nd_project_device bb\nWHERE\naa.source_id = bb.source_id\n AND bb.project_id= \'@[VAR_SELECTED_PROJECT_CODE]\') a', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('41', '146', 'num12', '2018-05-29 19:13:00', 'wl', '', 'FEATURE_TYPE.ELEVATORMONITOR', 'select D.num1,D.num2,A.total,B.num5,C.num4 from\r\n(select COUNT(*) as total from CraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN  LEFT JOIN  DataCenter.Device_ElevatorLiveData aaa ON aaa.IID = cc.IID AND CONVERT(VARCHAR(10),aaa.DeviceTime,120) = CONVERT(VARCHAR(10),GETDATE(),120), \r\nCraneMonitor.Monitor_Device bb\r\nwhere bb.MonitorType = 1 and bb.DID = aa.DID and  bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\' AND bb.DeviceState = 0 AND bb.isMonitor = 1) as A,\r\n\r\n(select COUNT(*) as num5  from CraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN  LEFT JOIN  DataCenter.Device_ElevatorLiveData aaa ON aaa.IID = cc.IID AND CONVERT(VARCHAR(10),aaa.DeviceTime,120) = CONVERT(VARCHAR(10),GETDATE(),120), \r\nCraneMonitor.Monitor_Device bb\r\nwhere bb.MonitorType = 1 and bb.DID = aa.DID and  bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\' AND bb.DeviceState = 0 AND bb.isMonitor = 1 and cc.IsOnline = 1) as B,\r\n\r\n(select COUNT(*) as num4  from CraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN  LEFT JOIN  DataCenter.Device_ElevatorLiveData aaa ON aaa.IID = cc.IID AND CONVERT(VARCHAR(10),aaa.DeviceTime,120) = CONVERT(VARCHAR(10),GETDATE(),120), \r\nCraneMonitor.Monitor_Device bb\r\nwhere bb.MonitorType = 1 and bb.DID = aa.DID and  bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\' AND bb.DeviceState = 0 AND bb.isMonitor = 1 and cc.IsOnline = 0) as C,\r\n\r\n(SELECT SUM(a.Alarm0) AS num1,SUM(a.Alarm1) AS num2 FROM(SELECT DISTINCT bb.ProjectID,\r\n(SELECT SUM(CASE WHEN ccc.EarlyAlarm <> 0 THEN 1 ELSE 0 END) FROM DataCenter.Device_ElevatorAlarm ccc WHERE  ccc.IID = cc.IID AND ccc.startTime  >=  CONVERT(VARCHAR(10),getdate(),120) ) AS Alarm0,\r\n(SELECT SUM(CASE WHEN ccc.Alarm <> 0 THEN 1 ELSE 0 END) FROM DataCenter.Device_ElevatorAlarm ccc WHERE  ccc.IID = cc.IID AND ccc.startTime  >=  CONVERT(VARCHAR(10),getdate(),120) ) AS Alarm1\r\nFROM CraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN ,CraneMonitor.Monitor_Device bb\r\nWHERE bb.MonitorType = 1 and aa.DID = bb.DID and bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\') a) D', '', 'DATASOURCE_TYPE.JDBC');
