INSERT INTO `zhgd_chart_addon` VALUES ('79', '143', 'num1', '2017-09-12 14:37:53', 'wl', '', 'FEATURE_TYPE.HUMAN', 'SELECT\r\n(SELECT COUNT(a.attendance_id) FROM d_worker_attendance a WHERE TIMESTAMPDIFF(DAY,a.record_date,CURRENT_DATE()) = 0  AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.off_time IS NULL)  AS nowPersonnel,\r\n\r\n(SELECT COUNT(a.attendance_id) FROM d_worker_attendance a WHERE TIMESTAMPDIFF(DAY,a.record_date,CURRENT_DATE()) = 0  AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\')  AS todayPersonnel,\r\n\r\n(SELECT COUNT(a.attendance_id) FROM d_worker_attendance a WHERE TIMESTAMPDIFF(DAY,a.record_date,CURRENT_DATE()) = 1  AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\')  AS yesterdayPersonnel,\r\n\r\n(SELECT COUNT(a.attendance_id) FROM d_worker_attendance a WHERE TIMESTAMPDIFF(DAY,a.record_date,CURRENT_DATE()) = 2  AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\')  AS towdayPersonnel,\r\n\r\n(SELECT COUNT(DISTINCT a.group_id) FROM d_worker_attendance a WHERE TIMESTAMPDIFF(DAY,a.record_date,CURRENT_DATE()) = 0 AND a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.off_time IS NULL) AS team', '', 'DATASOURCE_TYPE.JDBC');
