INSERT INTO `zhgd_chart_addon` VALUES ('166', '412', 'sumload', '2018-06-07 09:41:30', 'qjh', '', 'FEATURE_TYPE.CRONEMONITOR', ' select cast(round(SUM(ll.suLoad),2) as numeric(20,2)) as sumload,cast(round(SUM(ll.ySumLoad),2) as numeric(20,2)) as ysumload  from \r\n(select (select isnull(sum(load_),0) from Monitor_TowerCraneWorkData.Monitor_TowerCraneWorkData_@[VAR_MONTH_SUFFIX] da1 where da1.id_device = cc.IID and da1.insertTime >= convert(varchar, getdate(), 101))\r\n	+ (select isnull(sum(maxMomentLoad),0) from Monitor_TowerCraneWorkDataEx.Monitor_TowerCraneWorkDataEx_@[VAR_MONTH_SUFFIX] da2 where da2.id_device = cc.IID and da2.insertTime >= convert(varchar, getdate(), 101)) as suLoad,\r\n	(select isnull(sum(load_),0) from Monitor_TowerCraneWorkData.Monitor_TowerCraneWorkData_@[VAR_MONTH_SUFFIX] da1 where da1.id_device = cc.IID and da1.insertTime >= convert(varchar, dateadd(day, -1, getdate()), 101) and da1.insertTime <= convert(varchar, getdate(), 101))\r\n	+ (select isnull(sum(maxMomentLoad),0) from Monitor_TowerCraneWorkDataEx.Monitor_TowerCraneWorkDataEx_@[VAR_MONTH_SUFFIX] da2 where da2.id_device = cc.IID and da2.insertTime >= convert(varchar, dateadd(day, -1, getdate()), 101) and da2.insertTime <= convert(varchar, getdate(), 101)) as ySumLoad\r\nfrom CraneMonitor.Monitor_Device bb\r\nleft join CraneMonitor.Monitor_TowerCraneDevice aa on aa.DID = bb.DID\r\nleft join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN  \r\n where bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\') ll', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('167', '412', 'sumtime', '2018-06-07 09:41:56', 'qjh', '', 'FEATURE_TYPE.CRONEMONITOR', ' select SUM(tt.sumTime) as sumtime,SUM(tt.ySumTime) as ysumtime from\r\n (select(select sum(isnull(DATEDIFF(S,startRecordTime,endRecordTime),0)) from Monitor_TowerCraneWorkDataEx.Monitor_TowerCraneWorkDataEx_@[VAR_MONTH_SUFFIX] da2 where da2.id_device = cc.IID and da2.insertTime >= convert(varchar, getdate(), 101)) as sumTime,\r\n (select sum(isnull(DATEDIFF(S,startRecordTime,endRecordTime),0)) from Monitor_TowerCraneWorkDataEx.Monitor_TowerCraneWorkDataEx_@[VAR_MONTH_SUFFIX] da2 where da2.id_device = cc.IID and da2.insertTime >= convert(varchar, dateadd(day, -1, getdate()), 101) and da2.insertTime <= convert(varchar, getdate(), 101)) as ySumTime\r\nfrom CraneMonitor.Monitor_Device bb\r\nleft join CraneMonitor.Monitor_TowerCraneDevice aa on aa.DID = bb.DID\r\nleft join DataCenter.Device_Info cc on cc.DeviceSN = aa.DeviceSN  \r\n where bb.ProjectID = \'@[VAR_SELECTED_PROJECT_CODE]\') tt', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('170', '415', 'mostP', '2018-06-27 14:04:25', 'wl', '', 'SUBSYSTEM_TYPE.TIDB_HUMAN', 'SELECT a.group_name AS ggname,a.num_of_attendances AS mycount \r\nFROM a_human_attendance a,\r\n(SELECT MAX(data_time) AS dataTime FROM a_human_attendance WHERE project_id = \'@[VAR_SELECTED_PROJECT_CODE]\') b  \r\nWHERE a.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND a.data_time > DATE_FORMAT(NOW(),\'%Y-%m-%d\') AND a.data_time = b.dataTime  AND a.group_id IS NOT NULL\r\nGROUP BY a.group_id ORDER BY  mycount DESC  LIMIT 1', '', 'DATASOURCE_TYPE.JDBC');
