INSERT INTO `zhgd_chart_addon` VALUES ('195', '448', 'eisonline', '2018-06-19 14:53:14', 'cx', '', 'FEATURE_TYPE.ELEVATORMONITOR', ' SELECT isnull(A.IsOnline,0) as eisonline,isnull(B.IsntOnline,0) as eisntonlinene from\r\n(SELECT COUNT(*)AS IsOnline\r\nFROM \r\nCraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN  LEFT JOIN  DataCenter.Device_ElevatorLiveData aaa ON aaa.IID = cc.IID AND CONVERT(VARCHAR(10),aaa.DeviceTime,120) = CONVERT(VARCHAR(10),GETDATE(),120) , \r\nCraneMonitor.Monitor_Device bb\r\nWHERE \r\naa.DID = bb.DID AND bb.DeviceState = 0 AND bb.isMonitor = 1 \r\n and bb.ProjectID= \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n AND cc.IsOnline=1) A,\r\n\r\n (SELECT COUNT(*) AS IsntOnline\r\nFROM \r\nCraneMonitor.Monitor_ElevatorDevice aa  LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN  LEFT JOIN  DataCenter.Device_ElevatorLiveData aaa ON aaa.IID = cc.IID AND CONVERT(VARCHAR(10),aaa.DeviceTime,120) = CONVERT(VARCHAR(10),GETDATE(),120) , \r\nCraneMonitor.Monitor_Device bb\r\nWHERE \r\naa.DID = bb.DID AND bb.DeviceState = 0 AND bb.isMonitor = 1 \r\n and bb.ProjectID= \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n\r\n AND cc.IsOnline=0) B', '', 'DATASOURCE_TYPE.JDBC');
