INSERT INTO `zhgd_chart_addon` VALUES ('208', '453', 'times', '2018-06-27 16:53:51', 'cx', '', 'FEATURE_TYPE.CONCRETE_BY', 'SELECT COUNT(1) AS times FROM (SELECT DISTINCT top 100 percent \r\n   aa.DeviceSN,\r\nCONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\' AS dataTime,\r\nROUND(MAX(ccc.Temperature),2) AS Temperature,ROUND(MIN(ccc.Humidity),2) AS Humidity\r\nFROM JC_Project a,\r\nCraneMonitor.Monitor_DustDetectionDevice aa LEFT JOIN DataCenter.Device_Info cc ON cc.DeviceSN = aa.DeviceSN , \r\nCraneMonitor.Monitor_Device bb,\r\nMonitor_DustDetectionRunData_@[VAR_MONTH_SUFFIX].Monitor_DustDetectionRunData_@[VAR_DAY_SUFFIX] ccc WHERE \r\nbb.ProjectID = a.ProID\r\nAND aa.DID = bb.DID\r\nAND ccc.DeviceID = cc.IID\r\nAND a.ProID = \'@[VAR_SELECTED_PROJECT_CODE]\'\r\n GROUP BY  aa.DeviceSN, CONVERT(CHAR(3), ccc.DeviceTime, 24)+\'00\'\r\n ORDER BY dataTime)  AS a WHERE a.Temperature<18.0 OR a.Temperature>22.0 OR a.Humidity<95.0 GROUP BY DeviceSN', '', 'DATASOURCE_TYPE.JDBC');
INSERT INTO `zhgd_chart_addon` VALUES ('209', '441', 'num', '2018-06-27 18:01:08', 'wl', '', 'SUBSYSTEM_TYPE.TIDB_HUMAN', 'SELECT a.worker,a.total_num,a.specialWorker,a.femaleWorker,a.oldWorker,a.minorityWorker,a.total_num AS totalWorker\r\nFROM \r\n(SELECT SUM(aa.num_of_attendances) AS worker,SUM(aa.total_num) AS total_num,SUM(aa.num_of_attendances_special_group) AS specialWorker,\r\nSUM(aa.num_of_attendances_female) AS femaleWorker,SUM(aa.num_of_attendances_old) AS oldWorker,SUM(aa.num_of_attendances_minority) AS minorityWorker\r\nFROM a_human_attendance aa,\r\n(SELECT MAX(data_time) AS dataTime FROM a_human_attendance WHERE project_id = \'@[VAR_SELECTED_PROJECT_CODE]\') bb\r\nWHERE aa.data_time > DATE_FORMAT(NOW(),\'%y-%m-%d\') AND aa.project_id = \'@[VAR_SELECTED_PROJECT_CODE]\' AND aa.data_time = bb.dataTime) a', '', 'DATASOURCE_TYPE.JDBC');
