INSERT INTO `zhgd_chart_addon` VALUES ('240', '51', 'alarm', '2018-10-19 14:04:59', 'admin', '', 'FEATURE_TYPE.CRONEMONITOR', 'SELECT \n(SELECT CASE WHEN SUM(CASE WHEN ccc.alarm0 <> 0 THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE \nSUM(CASE WHEN ccc.alarm0 <> 0 THEN 1 ELSE 0 END) END  FROM `d_towercrane_alarm` ccc WHERE ccc.source_id = @[IID]\nAND DATEDIFF(DAY,ccc.startTime,GETDATE()) < 15 AND ((\'${dataTime}\' = \'\' AND ccc.startTime > \nDATE_FORMAT(NOW(),\'%Y-%m-%d\')) OR (\'${dataTime}\' <> \'\' AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d\') = \'${dataTime}\'))) AS Alarm0,\n(SELECT CASE WHEN SUM(CASE WHEN ccc.alarm1 <> 0 THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE \nSUM(CASE WHEN ccc.alarm1 <> 0 THEN 1 ELSE 0 END) END  FROM `d_towercrane_alarm` ccc WHERE ccc.source_id = @[IID]\nAND DATEDIFF(DAY,ccc.startTime,GETDATE()) < 15 AND ((\'${dataTime}\' = \'\' AND ccc.startTime > \nDATE_FORMAT(NOW(),\'%Y-%m-%d\')) OR (\'${dataTime}\' <> \'\' AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d\') = \'${dataTime}\'))) AS Alarm1,\n(SELECT CASE WHEN SUM(CASE WHEN ccc.alarm2 <> 0 THEN 1 ELSE 0 END) IS NULL THEN 0 ELSE \nSUM(CASE WHEN ccc.alarm2 <> 0 THEN 1 ELSE 0 END) END  FROM `d_towercrane_alarm` ccc WHERE ccc.source_id = @[IID]\nAND DATEDIFF(DAY,ccc.startTime,GETDATE()) < 15 AND ((\'${dataTime}\' = \'\' AND ccc.startTime > \nDATE_FORMAT(NOW(),\'%Y-%m-%d\')) OR (\'${dataTime}\' <> \'\' AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d\') = \'${dataTime}\'))) AS Alarm2,\n(SELECT CASE WHEN COUNT(ccc.alarm3) IS NULL THEN 0 ELSE COUNT(ccc.alarm3) END   FROM \nDataCenter.Device_TowerCraneAlarm ccc WHERE ccc.IID = @[IID] AND ccc.Alarm3 IN(3,12,48,64)\nAND DATEDIFF(DAY,ccc.startTime,GETDATE()) < 15 AND ((\'${dataTime}\' = \'\' AND ccc.startTime > \nDATE_FORMAT(NOW(),\'%Y-%m-%d\')) OR (\'${dataTime}\' <> \'\' AND DATE_FORMAT(ccc.start_time,\'%Y-%m-%d\') = \'${dataTime}\'))) AS Alarm3,\nCASE WHEN \'${dataTime}\' = \'\' THEN DATE_FORMAT(NOW(),\'%Y-%m-%d\') ELSE \'${dataTime}\' END AS dataTime', '', 'DATASOURCE_TYP