INSERT INTO `zhgd_interface` VALUES ('48', '品茗数据中心_安监_项目考勤', 'aj_f_worker_attendence', '2018-08-14', 'cx', null, null, 'SUBSYSTEM_TYPE.TIDB_GOV_HUMAN', 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, '1', 'SELECT \r\nc.`record_date`,\r\nCOUNT(DISTINCT b.worker_id) AS total_worker_num,\r\nCOUNT(DISTINCT c.worker_id) AS attendance_num,\r\na.`project_num`\r\nFROM `dzb_human_project_num` a, \r\n`f_human_worker` b, \r\n`f_human_worker_attendance` c\r\nWHERE a.`project_num` = b.`project_num`  \r\nAND a.`project_num` = c.`project_num` \r\nAND a.`area_code` = \'${area_code}\'\r\nAND (\'${project_num}\' = \'\' OR \'${project_num}\' = a.`project_num`)\r\nAND c.`record_date` >= DATE_SUB(CURDATE(),INTERVAL +15 DAY)\r\nGROUP BY a.`project_num`, c.`record_date` ORDER BY c.`record_date`', null);
