INSERT INTO `zhgd_interface` VALUES ('59', '品茗数据中心_安监_项目薪资发放', 'aj_a_project_salary', '2018-08-15', 'cx', null, null, 'SUBSYSTEM_TYPE.TIDB_GOV_HUMAN', 'DATASOURCE_TYPE.JDBC', null, null, null, null, null, null, null, null, null, '1', 'SELECT \r\nb.`project_salary_id`,\r\nb.`data_time`,\r\na.`project_id`,\r\na.`project_name`,\r\na.`project_num`,\r\nb.`project_worker_total_num`,\r\nCONCAT(ROUND(b.paid_rate*100),\'%\') AS paid_rate\r\nFROM `dzb_human_project_num` a,`a_project_salary` b\r\nWHERE a.`project_num` = b.`project_num`\r\nAND a.`area_code` = \'${area_code}\'\r\nAND (\'${project_num}\' = \'\' OR \'${project_num}\' = a.`project_num`)\r\nAND (\'${month_time}\' = \'\' OR \'${month_time}\' = DATE_FORMAT(data_time,\'%Y-%m\'))\r\nAND (\'${years}\' = \'\' OR \'${years}\'=DATE_FORMAT(data_time,\'%Y\'))\r\nAND (\'${project_name}\' = \'\' OR  a.`project_name` LIKE \'%${project_name}%\')\r\nAND (CASE \r\nWHEN \'${total1}\' = \'\' AND \'${total2}\' != \'\' THEN b.`project_worker_total_num` <= \'${total2}\'\r\nWHEN \'${total1}\' != \'\' AND \'${total2}\' = \'\' THEN b.`project_worker_total_num` >= \'${total1}\'\r\nWHEN \'${total1}\' != \'\' AND \'${total2}\' != \'\'THEN b.`project_worker_total_num`  BETWEEN \'${total1}\' AND \'${total2}\'\r\nELSE 1=1 END)\r\nAND (CASE \r\nWHEN \'${release1}\' = \'\' AND \'${release2}\' != \'\' THEN b.`paid_rate` <= \'${release2}\'\r\nWHEN \'${release1}\' != \'\' AND \'${release2}\' = \'\' THEN b.`paid_rate` >= \'${release1}\'\r\nWHEN \'${release1}\' != \'\' AND \'${release2}\' != \'\'THEN b.`paid_rate`  BETWEEN \'${release1}\' AND \'${release2}\'\r\nELSE 1=1 END)', null);
